   * @param {Object} options - 缓存选项
   * @param {number} options.ttl - 缓存过期时间（秒）
   * @param {Function} [options.keyGenerator] - 自定义缓存键生成函数
   * @param {Function} [options.shouldCache] - 判断是否缓存响应的函数
   * @returns {Function} - Express中间件
   */
  static createCacheMiddleware(options = {}) {
    const { ttl = 300, keyGenerator, shouldCache } = options;

    return (req, res, next) => {
      // 只缓存GET请求
      if (req.method !== 'GET') {
        return next();
      }

      // 生成缓存键
      const cacheKey = keyGenerator ? keyGenerator(req) : CacheMiddleware.defaultKeyGenerator(req);

      try {
        // 尝试从缓存获取数据
        const cachedData = cacheService.get(cacheKey);

        if (cachedData) {
          logger.debug(`缓存命中: ${cacheKey}`);
          return res.json(cachedData);
        }

        // 重写res.json方法以缓存响应
        const originalJson = res.json;
        res.json = function (body) {
          // 判断是否应该缓存
          if (!shouldCache || shouldCache(req, res, body)) {
            cacheService.set(cacheKey, body, ttl);
            logger.debug(`缓存设置成功: ${cacheKey}, TTL: ${ttl}秒`);
          }
          return originalJson.call(this, body);
        };

        next();
      } catch (error) {
        logger.error(`缓存中间件错误: ${cacheKey}`, error);
        next(); // 缓存出错时继续请求处理，不影响正常业务
      }
    };
  }

  /**
   * 默认缓存键生成器
   * @param {Object} req - Express请求对象
   * @returns {string} - 缓存键
   */
  static defaultKeyGenerator(req) {
    // 包含查询参数的完整URL作为缓存键
    const queryString = new URLSearchParams(req.query).toString();
    return `${req.originalUrl}${queryString ? `?${queryString}` : ''}`;
  }

  /**
   * 创建缓存清除中间件
   * 用于在数据更新时清除相关缓存
   * @param {Function} keyPatternGenerator - 生成缓存键模式的函数
   * @returns {Function} - Express中间件
   */
  static createCacheInvalidator(keyPatternGenerator) {
    return async (req, res, next) => {
      try {
        // 先执行后续中间件
        await new Promise((resolve, reject) => {
          res.on('finish', resolve);
          res.on('error', reject);
          next();
        });

        // 生成缓存键模式并清除匹配的缓存
        const keyPattern = keyPatternGenerator(req, res);
        if (keyPattern) {
          const stats = cacheService.getStats();
          const keys = stats && stats.keys ? stats.keys : [];
          const matchedKeys = keys.filter((key) => key.includes(keyPattern));

          if (matchedKeys.length > 0) {
            const count = cacheService.delMulti(matchedKeys);
            logger.debug(`缓存清除成功: 模式=${keyPattern}, 数量=${count}`);
          }
        }
      } catch (error) {
        logger.error('缓存清除中间件错误', error);
        // 缓存清除失败不影响主流程
      }
    };
  }

  /**
   * 常用缓存场景的快捷方法
   */

  /**
   * 设备数据缓存中间件
   * @param {number} [ttl=60] - 缓存时间（秒）
   * @returns {Function} - Express中间件
   */
  static deviceDataCache(ttl = 60) {
    return CacheMiddleware.createCacheMiddleware({
      ttl,
      shouldCache: (req, _res, body) =>
        // 只缓存成功响应
        _res.statusCode === 200 && body && body.success !== false
    });
  }

  /**
   * 能源统计数据缓存中间件
   * @param {number} [ttl=300] - 缓存时间（秒）
   * @returns {Function} - Express中间件
   */
  static energyStatsCache(ttl = 300) {
    return CacheMiddleware.createCacheMiddleware({
      ttl,
      shouldCache: (req, _res, body) => _res.statusCode === 200 && body && body.success !== false
    });
  }

  /**
   * 清除设备数据缓存的中间件
   * @returns {Function} - Express中间件
   */
  static invalidateDeviceCache() {
    return CacheMiddleware.createCacheInvalidator((req, _) => {
      // 从请求参数中获取设备ID
      const deviceId = req.params.id || req.body.deviceId;
      return deviceId ? `device/${deviceId}` : null;
    });
  }

  /**
   * 清除能源统计缓存的中间件
   * @returns {Function} - Express中间件
   */
  static invalidateEnergyStatsCache() {
    return CacheMiddleware.createCacheInvalidator(() => 'energy/stats');
  }
}

export default CacheMiddleware;

/**
 * 统一错误处理模块
 * 提供标准化的错误类和错误处理中间件
 */

import { logger } from './logger.js';

/**
 * 应用程序错误类
 * 继承自Error，添加状态码和错误代码
 */
export class AppError extends Error {
  constructor(message, statusCode, errorCode, details = {}) {
    super(message);
    this.statusCode = statusCode;
    this.errorCode = errorCode;
    this.details = details;
    this.isOperational = true;
    this.timestamp = new Date().toISOString();
    Error.captureStackTrace(this, this.constructor);
  }
}

/**
 * 验证错误类
 */
export class ValidationError extends AppError {
  constructor(message, details) {
    super(message, 400, 'VALIDATION_ERROR', details);
  }
}

/**
 * 认证错误类
 */
export class AuthenticationError extends AppError {
  constructor(message = '认证失败') {
    super(message, 401, 'AUTHENTICATION_ERROR');
  }
}

/**
 * 授权错误类
 */
export class AuthorizationError extends AppError {
  constructor(message = '权限不足') {
    super(message, 403, 'AUTHORIZATION_ERROR');
  }
}

/**
 * 资源未找到错误类
 */
export class NotFoundError extends AppError {
  constructor(resource = '资源') {
    super(`${resource}未找到`, 404, 'NOT_FOUND');
  }
}

/**
 * 数据库错误类
 */
export class DatabaseError extends AppError {
  constructor(message, details) {
    super(message, 500, 'DATABASE_ERROR', details);
  }
}

/**
 * 外部服务错误类
 */
export class ExternalServiceError extends AppError {
  constructor(service, message = '外部服务调用失败') {
    super(`${service}: ${message}`, 502, 'EXTERNAL_SERVICE_ERROR');
    this.service = service;
  }
}

/**
 * 错误处理中间件
 * 统一处理应用程序中的错误
 */
export const errorHandler = (err, req, res, _next) => {
  let error = { ...err };
  error.message = err.message;

  // 记录错误日志
  // 结构化错误日志
  logger.error('API Error', {
    errorCode: error.errorCode || 'UNKNOWN_ERROR',
    message: error.message,
    details: error.details,
    url: req.url,
    method: req.method,
    ip: req.ip,
    userAgent: req.get('User-Agent'),
    stack: error.stack
  });

  // 处理特定类型的错误
  if (err.name === 'ValidationError') {
    error = new ValidationError(err.message);
  } else if (err.name === 'JsonWebTokenError') {
    error = new AuthenticationError('无效的访问令牌');
  } else if (err.name === 'TokenExpiredError') {
    error = new AuthenticationError('访问令牌已过期');
  } else if (err.code === 'SQLITE_CONSTRAINT') {
    error = new ValidationError('数据约束违反');
  } else if (err.code === 'ENOTFOUND') {
    error = new ExternalServiceError('DNS', '域名解析失败');
  } else if (err.code === 'ECONNREFUSED') {
    error = new ExternalServiceError('Network', '连接被拒绝');
  }

  // 设置默认错误信息
  if (!error.statusCode) {
    error.statusCode = 500;
  }
  if (!error.errorCode) {
    error.errorCode = 'INTERNAL_SERVER_ERROR';
  }

  // 构建响应
  const response = {
    success: false,
    error: {
      code: error.errorCode,
      message:
        process.env.NODE_ENV === 'production'
          ? getProductionMessage(error.statusCode)
          : error.message,
      timestamp: new Date().toISOString(),
      ...(process.env.NODE_ENV !== 'production' && { details: error.details })
    }
  };

  // 在开发环境中包含堆栈跟踪
  if (process.env.NODE_ENV === 'development') {
    response.error.stack = error.stack;
  }

  // 如果是验证错误，包含字段信息
  if (error instanceof ValidationError && error.field) {
    response.error.field = error.field;
  }

  res.status(error.statusCode).json(response);
};

/**
 * 根据状态码获取生产环境下的错误消息
 * @param {number} statusCode HTTP状态码
 * @returns {string} 错误消息
 */
const getProductionMessage = (statusCode) => {
  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  switch (statusCode) {
    case 400:
      return '请求参数无效或格式不正确。';
    case 401:
      return '认证失败，请检查您的凭据。';
    case 403:
      return '您没有执行此操作的权限。';
    case 404:
      return '请求的资源不存在。';
    case 429:
      return '请求过于频繁，请稍后再试。';
    case 500:
