  get: (key) => redisClient.get(key),
  set: (key, value) => redisClient.set(key, value),
  setAsync: (key, value) => redisClient.set(key, value),
  del: (key) => redisClient.del(key),
  expire: (key, ttl) => redisClient.expire(key, ttl),
