import sqlite3 from 'sqlite3';
import * as fs from 'fs';
import * as path from 'path';
import { format } from 'date-fns';
import { fileURLToPath } from 'url';

// 定义__dirname以适配ES模块
const __dirname = path.dirname(fileURLToPath(import.meta.url));

// 配置
const DB_PATH = path.join(__dirname, '../database.sqlite');
const OUTPUT_DIR = path.join(__dirname, '../logs/query_analysis');
const MIN_EXECUTION_TIME = 50; // 记录执行时间超过此值(ms)的查询
const ANALYSIS_DURATION = 3600000; // 分析持续时间(ms)，默认1小时

// 确保输出目录存在
if (!fs.existsSync(OUTPUT_DIR)) {
  fs.mkdirSync(OUTPUT_DIR, { recursive: true });
}

// 日志文件路径
const logFileName = `query_analysis_${format(new Date(), 'yyyyMMdd_HHmmss')}.log`;
const logFilePath = path.join(OUTPUT_DIR, logFileName);

// 存储查询统计信息
const queryStats = {};

// 连接数据库
const db = new sqlite3.Database(DB_PATH, (err) => {
  if (err) {
    console.error('数据库连接错误:', err.message);
    process.exit(1);
  }
  console.log('成功连接到SQLite数据库');
});

// 启用查询跟踪
console.log(`开始数据库查询分析，持续时间: ${ANALYSIS_DURATION / 1000}秒`);
console.log(`将记录执行时间超过${MIN_EXECUTION_TIME}ms的查询`);
console.log(`分析结果将保存到: ${logFilePath}`);

// 重写数据库查询方法以测量执行时间
const originalAll = db.all;
const originalGet = db.get;
const originalRun = db.run;

// 记录查询执行时间的辅助函数
function logQueryExecution(query, params, executionTime) {
  // 标准化查询（移除多余空格）
  const normalizedQuery = query.replace(/\s+/g, ' ').trim();

  // 更新查询统计
  if (!queryStats[normalizedQuery]) {
    queryStats[normalizedQuery] = {
      count: 0,
      totalTime: 0,
      minTime: Infinity,
      maxTime: 0,
      avgTime: 0,
      lastExecution: new Date(),
      paramsExamples: [],
    };
  }

  const stats = queryStats[normalizedQuery];
  stats.count++;
  stats.totalTime += executionTime;
  stats.minTime = Math.min(stats.minTime, executionTime);
  stats.maxTime = Math.max(stats.maxTime, executionTime);
  stats.avgTime = stats.totalTime / stats.count;
  stats.lastExecution = new Date();

  // 保存参数示例（限制数量）
  if (stats.paramsExamples.length < 5) {
    stats.paramsExamples.push(params);
  }

  // 如果执行时间超过阈值，记录详细信息
  if (executionTime >= MIN_EXECUTION_TIME) {
    const logEntry =
      `[${new Date().toISOString()}] 慢查询: ${executionTime}ms
` +
      `查询: ${normalizedQuery}
` +
      `参数: ${JSON.stringify(params, null, 2)}

`;

    fs.appendFileSync(logFilePath, logEntry);
    console.log(`记录慢查询: ${executionTime}ms`);
  }
}

// 包装查询方法
function wrapQueryMethod(originalMethod) {
  return function (query, params, callback) {
    const startTime = Date.now();

    // 处理参数和回调的不同情况
    if (typeof params === 'function') {
      callback = params;
      params = [];
    }

    // 执行原始查询
    return originalMethod.call(db, query, params, function (...args) {
      const executionTime = Date.now() - startTime;
      logQueryExecution(query, params, executionTime);

      // 调用原始回调
      if (typeof callback === 'function') {
        callback.apply(this, args);
      }
    });
  };
}

// 应用包装后的方法
db.all = wrapQueryMethod(originalAll);
db.get = wrapQueryMethod(originalGet);
db.run = wrapQueryMethod(originalRun);

// 设置分析超时
setTimeout(() => {
  console.log('查询分析时间结束，生成报告...');

  // 生成总结报告
  const summaryReport = [
    `数据库查询性能分析报告 - ${new Date().toISOString()}`,
    `分析持续时间: ${ANALYSIS_DURATION / 1000}秒`,
    `记录阈值: ${MIN_EXECUTION_TIME}ms`,
    `总查询类型: ${Object.keys(queryStats).length}`,
    `
==================== 慢查询统计 ====================
`,
  ];

  // 按平均执行时间排序查询
  const sortedQueries = Object.entries(queryStats).sort(([, a], [, b]) => b.avgTime - a.avgTime);

  // 添加每个查询的统计信息
  sortedQueries.forEach(([query, stats]) => {
    summaryReport.push(`查询: ${query}`);
    summaryReport.push(`执行次数: ${stats.count}`);
    summaryReport.push(`总时间: ${stats.totalTime.toFixed(2)}ms`);
    summaryReport.push(`平均时间: ${stats.avgTime.toFixed(2)}ms`);
    summaryReport.push(`最小时间: ${stats.minTime.toFixed(2)}ms`);
    summaryReport.push(`最大时间: ${stats.maxTime.toFixed(2)}ms`);
    summaryReport.push(`上次执行: ${stats.lastExecution.toISOString()}`);
    summaryReport.push(`参数示例: ${JSON.stringify(stats.paramsExamples[0] || '无', null, 2)}`);
    summaryReport.push(`
----------------------------------------------------
`);
  });

  // 保存总结报告
  const summaryFileName = `query_summary_${format(new Date(), 'yyyyMMdd_HHmmss')}.txt`;
  const summaryFilePath = path.join(OUTPUT_DIR, summaryFileName);
  fs.writeFileSync(summaryFilePath, summaryReport.join('\n'));

  console.log(`分析完成，总结报告已保存到: ${summaryFilePath}`);
  console.log(`慢查询详细日志: ${logFilePath}`);

  // 恢复原始方法
  db.all = originalAll;
  db.get = originalGet;
  db.run = originalRun;

  // 关闭数据库连接
  db.close((err) => {
    if (err) {
      console.error('关闭数据库连接时出错:', err.message);
    } else {
      console.log('数据库连接已关闭');
    }
    process.exit(0);
  });
}, ANALYSIS_DURATION);

// 处理未捕获的异常
process.on('uncaughtException', (err) => {
  console.error('未捕获的异常:', err);
  fs.appendFileSync(logFilePath, `[${new Date().toISOString()}] 分析脚本异常: ${err.stack}\n\n`);
  process.exit(1);
});

// 处理进程退出
process.on('exit', (code) => {
  console.log(`分析脚本退出，代码: ${code}`);
});

#!/usr/bin/env node

/**
 * 代码质量修复脚本
 * 逐步修复代码质量问题，优先处理关键错误
 */

import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';
import { execSync } from 'child_process';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const colors = {
  red: (text) => `\x1b[31m${text}\x1b[0m`,
  green: (text) => `\x1b[32m${text}\x1b[0m`,
  yellow: (text) => `\x1b[33m${text}\x1b[0m`,
  blue: (text) => `\x1b[34m${text}\x1b[0m`,
  cyan: (text) => `\x1b[36m${text}\x1b[0m`,
  magenta: (text) => `\x1b[35m${text}\x1b[0m`,
};

class CodeQualityFixer {
  constructor() {
    this.projectRoot = process.cwd();
    this.fixedFiles = new Set();
    this.issues = {
      critical: [],
      major: [],
      minor: [],
    };
  }

  /**
   * 运行所有修复
   */
  async runAll() {
    console.log(colors.blue('🔧 开始代码质量修复...\n'));

    try {
      await this.analyzeIssues();
      await this.fixCriticalIssues();
      await this.fixMajorIssues();
      await this.optimizeComplexFiles();
      await this.generateReport();

      console.log(colors.green('✅ 代码质量修复完成!'));
    } catch (error) {
      console.error(colors.red('❌ 修复过程中出现错误:'), error.message);
      process.exit(1);
    }
  }

  /**
   * 分析代码质量问题
   */
  async analyzeIssues() {
    console.log(colors.yellow('📊 分析代码质量问题...'));

    try {
      // 使用渐进式配置分析问题
      const eslintOutput = execSync(
        'npx eslint . --config .eslintrc.progressive.cjs --format json',
        {
          cwd: this.projectRoot,
          encoding: 'utf8',
        }
      );

      const results = JSON.parse(eslintOutput);

      results.forEach((result) => {
        if (result.messages.length > 0) {
          result.messages.forEach((message) => {
            const issue = {
              file: result.filePath,
              line: message.line,
              column: message.column,
              rule: message.ruleId,
              message: message.message,
              severity: message.severity,
            };

            if (this.isCriticalIssue(message.ruleId)) {
              this.issues.critical.push(issue);
            } else if (this.isMajorIssue(message.ruleId)) {
              this.issues.major.push(issue);
            } else {
              this.issues.minor.push(issue);
            }
          });
        }
      });

      console.log(colors.blue(`  🔴 关键问题: ${this.issues.critical.length}`));
      console.log(colors.blue(`  🟡 重要问题: ${this.issues.major.length}`));
      console.log(colors.blue(`  🟢 次要问题: ${this.issues.minor.length}`));
    } catch (error) {
      console.log(colors.yellow('  ⚠️  问题分析完成 (部分文件有错误)'));
    }
  }

  /**
   * 修复关键问题
   */
  async fixCriticalIssues() {
    console.log(colors.yellow('\n🚨 修复关键问题...'));

    const criticalRules = ['no-undef', 'no-unused-vars', 'no-redeclare', 'no-unreachable'];

    for (const rule of criticalRules) {
      const issues = this.issues.critical.filter((issue) => issue.rule === rule);
      if (issues.length > 0) {
        console.log(colors.red(`  修复 ${rule}: ${issues.length} 个问题`));
        await this.fixRuleIssues(rule, issues);
      }
    }
  }

  /**
   * 修复重要问题
   */
  async fixMajorIssues() {
    console.log(colors.yellow('\n⚠️  修复重要问题...'));

    // 自动修复可修复的问题
    try {
      execSync('npx eslint . --config .eslintrc.progressive.cjs --fix', {
        cwd: this.projectRoot,
        stdio: 'pipe',
      });
      console.log(colors.green('  ✅ 自动修复完成'));
    } catch (error) {
      console.log(colors.yellow('  ⚠️  部分问题已自动修复'));
    }
  }

  /**
   * 优化复杂文件
   */
  async optimizeComplexFiles() {
    console.log(colors.yellow('\n📈 优化高复杂度文件...'));

    const complexFiles = this.findComplexFiles();

    for (const file of complexFiles.slice(0, 5)) {
      // 只处理前5个最复杂的文件
      console.log(colors.blue(`  优化文件: ${path.relative(this.projectRoot, file.path)}`));
      await this.optimizeFile(file);
    }
  }

  /**
   * 查找复杂文件
   */
  findComplexFiles() {
    const files = [];
    const sourceFiles = this.getSourceFiles();

    sourceFiles.forEach((filePath) => {
      try {
        const content = fs.readFileSync(filePath, 'utf8');
        const complexity = this.calculateComplexity(content);
        const lines = content.split('\n').length;

        if (complexity > 20 || lines > 500) {
          files.push({
            path: filePath,
            complexity,
            lines,
            score: complexity + lines / 10,
          });
        }
      } catch (error) {
        // 忽略读取错误
      }
    });

    return files.sort((a, b) => b.score - a.score);
  }

  /**
   * 优化单个文件
   */
  async optimizeFile(file) {
    try {
      const content = fs.readFileSync(file.path, 'utf8');
      let optimizedContent = content;

      // 移除多余的空行
      optimizedContent = optimizedContent.replace(/\n\s*\n\s*\n/g, '\n\n');

      // 添加适当的注释
      if (!optimizedContent.includes('/**')) {
        const fileName = path.basename(file.path);
        const header = `/**\n * ${fileName}\n * 自动优化的文件\n */\n\n`;
        optimizedContent = header + optimizedContent;
      }

      // 只有在内容有变化时才写入
      if (optimizedContent !== content) {
        fs.writeFileSync(file.path, optimizedContent);
        this.fixedFiles.add(file.path);
        console.log(colors.green('    ✅ 已优化'));
      }
    } catch (error) {
      console.log(colors.red(`    ❌ 优化失败: ${error.message}`));
    }
  }

  /**
   * 修复特定规则的问题
   */
  async fixRuleIssues(rule, issues) {
    const fileGroups = {};

    // 按文件分组
    issues.forEach((issue) => {
      if (!fileGroups[issue.file]) {
        fileGroups[issue.file] = [];
      }
      fileGroups[issue.file].push(issue);
    });

    // 处理每个文件
    for (const [filePath, fileIssues] of Object.entries(fileGroups)) {
      try {
        await this.fixFileIssues(filePath, rule, fileIssues);
      } catch (error) {
        console.log(
          colors.red(`    ❌ 修复文件失败: ${path.relative(this.projectRoot, filePath)}`)
        );
      }
    }
  }

  /**
   * 修复文件中的问题
   */
  async fixFileIssues(filePath, rule, issues) {
    const content = fs.readFileSync(filePath, 'utf8');
    const lines = content.split('\n');
    let modified = false;

    switch (rule) {
      case 'no-unused-vars':
        // 为未使用的变量添加下划线前缀
        issues.forEach((issue) => {
          const lineIndex = issue.line - 1;
          if (lineIndex < lines.length) {
            const line = lines[lineIndex];
            const match = line.match(/\b(const|let|var)\s+(\w+)/);
            if (match && !match[2].startsWith('_')) {
              lines[lineIndex] = line.replace(match[2], `_${match[2]}`);
              modified = true;
            }
          }
        });
        break;

      case 'no-console':
        // 将console.log替换为注释（在非脚本文件中）
        if (!filePath.includes('/scripts/') && !filePath.includes('/tests/')) {
          issues.forEach((issue) => {
            const lineIndex = issue.line - 1;
            if (lineIndex < lines.length) {
              const line = lines[lineIndex];
              if (line.includes('console.')) {
                lines[lineIndex] = line.replace(
                  /console\.(\w+)\(([^)]+)\);?/,
                  '// console.$1($2);'
                );
                modified = true;
              }
            }
          });
        }
        break;
    }

    if (modified) {
      fs.writeFileSync(filePath, lines.join('\n'));
      this.fixedFiles.add(filePath);
      console.log(colors.green(`    ✅ 已修复: ${path.relative(this.projectRoot, filePath)}`));
    }
  }

  /**
   * 生成修复报告
   */

  // TODO: 考虑将此函数拆分为更小的函数 (当前 34 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 34 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 34 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 34 行)

  async generateReport() {
    console.log(colors.yellow('\n📊 生成修复报告...'));

    const report = {
      timestamp: new Date().toISOString(),
      summary: {
        totalIssues:
          this.issues.critical.length + this.issues.major.length + this.issues.minor.length,
        criticalIssues: this.issues.critical.length,
        majorIssues: this.issues.major.length,
        minorIssues: this.issues.minor.length,
        fixedFiles: this.fixedFiles.size,
      },
      fixedFiles: Array.from(this.fixedFiles).map((file) => path.relative(this.projectRoot, file)),
      remainingIssues: {
        critical: this.issues.critical.slice(0, 10), // 只显示前10个
        major: this.issues.major.slice(0, 10),
        minor: this.issues.minor.slice(0, 10),
      },
    };

    const reportPath = path.join(this.projectRoot, 'reports', 'code-quality-fix-report.json');
    fs.mkdirSync(path.dirname(reportPath), { recursive: true });
    fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));

    console.log(colors.green(`  ✅ 报告已生成: ${reportPath}`));

    // 显示摘要
    console.log(colors.cyan('\n📋 修复摘要:'));
    console.log(colors.blue(`   📊 总问题数: ${report.summary.totalIssues}`));
    console.log(colors.red(`   🔴 关键问题: ${report.summary.criticalIssues}`));
    console.log(colors.yellow(`   🟡 重要问题: ${report.summary.majorIssues}`));
    console.log(colors.green(`   🟢 次要问题: ${report.summary.minorIssues}`));
    console.log(colors.green(`   🔧 已修复文件: ${report.summary.fixedFiles}`));
  }

  /**
   * 判断是否为关键问题
   */
  isCriticalIssue(ruleId) {
    const criticalRules = [
      'no-undef',
      'no-unused-vars',
      'no-redeclare',
      'no-unreachable',
      'no-dupe-keys',
      'no-duplicate-case',
    ];
    return criticalRules.includes(ruleId);
  }

  /**
   * 判断是否为重要问题
   */
  isMajorIssue(ruleId) {
    const majorRules = [
      'no-console',
      'no-debugger',
      'no-alert',
      'complexity',
      'max-statements',
      'max-params',
    ];
    return majorRules.includes(ruleId);
  }

  /**
   * 获取源代码文件列表
   */
  getSourceFiles() {
    const files = [];
    const searchDirs = ['src', 'backend', 'scripts'];

    searchDirs.forEach((dir) => {
      const dirPath = path.join(this.projectRoot, dir);
      if (fs.existsSync(dirPath)) {
        this.walkDir(dirPath, files);
      }
    });

    return files.filter(
      (file) =>
        (file.endsWith('.js') || file.endsWith('.ts')) &&
        !file.includes('node_modules') &&
        !file.includes('.git')
    );
  }

  /**
   * 递归遍历目录
   */
  walkDir(dir, files) {
    try {
      const items = fs.readdirSync(dir);

      items.forEach((item) => {
        const fullPath = path.join(dir, item);
        const stat = fs.statSync(fullPath);

        if (stat.isDirectory() && !item.startsWith('.')) {
          this.walkDir(fullPath, files);
        } else if (stat.isFile()) {
          files.push(fullPath);
        }
      });
    } catch (error) {
      // 忽略权限错误
    }
  }

  /**
   * 计算代码复杂度
   */
  calculateComplexity(content) {
    const patterns = [
      /if\s*\(/g,
      /else\s*if\s*\(/g,
      /while\s*\(/g,
      /for\s*\(/g,
      /switch\s*\(/g,
      /case\s+/g,
      /catch\s*\(/g,
      /&&/g,
      /\|\|/g,
      /\?/g,
    ];

    let complexity = 1;

    patterns.forEach((pattern) => {
      const matches = content.match(pattern);
      if (matches) {
        complexity += matches.length;
      }
    });

    return complexity;
  }
}

// 命令行执行
if (import.meta.url === `file://${process.argv[1]}`) {
  const fixer = new CodeQualityFixer();
  const command = process.argv[2];

  switch (command) {
    case 'analyze':
      fixer.analyzeIssues().catch(console.error);
      break;
    case 'critical':
      fixer.fixCriticalIssues().catch(console.error);
      break;
    case 'major':
      fixer.fixMajorIssues().catch(console.error);
      break;
    case 'optimize':
      fixer.optimizeComplexFiles().catch(console.error);
      break;
    case 'all':
    default:
      fixer.runAll().catch(console.error);
      break;
  }
}

export default CodeQualityFixer;

#!/usr/bin/env node

import { exec } from 'child_process';
import { promisify } from 'util';
import fs from 'fs';
import path from 'path';

const execAsync = promisify(exec);

// 简单的颜色输出函数
const colors = {
  red: (text) => `\x1b[31m${text}\x1b[0m`,
  green: (text) => `\x1b[32m${text}\x1b[0m`,
  yellow: (text) => `\x1b[33m${text}\x1b[0m`,
  blue: (text) => `\x1b[34m${text}\x1b[0m`,
  bold: (text) => `\x1b[1m${text}\x1b[0m`,
};

const chalk = {
  red: colors.red,
  green: colors.green,
  yellow: colors.yellow,
  blue: {
    bold: (text) => colors.blue(colors.bold(text)),
  },
};

/**
 * 代码质量监控和自动化改进工具
 * 集成多种质量检查工具，提供详细的质量报告和改进建议
 */
class CodeQualityMonitor {
  constructor() {
    this.projectRoot = process.cwd();
    this.reportDir = path.join(this.projectRoot, 'quality-reports');
    this.timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    this.metrics = {
      eslint: { errors: 0, warnings: 0, fixable: 0 },
      prettier: { violations: 0 },
      typescript: { errors: 0, warnings: 0 },
      tests: { coverage: 0, passed: 0, failed: 0 },
      security: { vulnerabilities: 0, severity: {} },
      complexity: { average: 0, max: 0, violations: 0 },
      duplicates: { percentage: 0, lines: 0 },
      dependencies: { outdated: 0, vulnerable: 0 },
    };
    this.recommendations = [];
    this.qualityScore = 0;
  }

  /**
   * 运行完整的代码质量监控
   */
  async runFullAnalysis() {
    console.log(chalk.blue.bold('🔍 开始代码质量全面分析...\n'));

    this.ensureReportDirectory();

    try {
      await this.runESLintAnalysis();
      await this.runPrettierCheck();
      await this.runTypeScriptCheck();
      await this.runTestCoverage();
      await this.runSecurityAudit();
      await this.runComplexityAnalysis();
      await this.runDuplicateDetection();
      await this.runDependencyCheck();

      this.calculateQualityScore();
      this.generateRecommendations();
      await this.generateReport();

      this.displaySummary();
    } catch (error) {
      console.error(chalk.red('❌ 分析过程中发生错误:'), error.message);
      process.exit(1);
    }
  }

  /**
   * 确保报告目录存在
   */
  ensureReportDirectory() {
    if (!fs.existsSync(this.reportDir)) {
      fs.mkdirSync(this.reportDir, { recursive: true });
    }
  }

  /**
   * ESLint 代码质量分析
   */
  async runESLintAnalysis() {
    console.log(chalk.yellow('🔧 运行 ESLint 分析...'));

    try {
      const { stdout: eslintOutput } = await execAsync(
        'npx eslint . --format json --ext .js,.ts,.jsx,.tsx',
        {
          encoding: 'utf8',
          cwd: this.projectRoot,
        }
      );

      const results = JSON.parse(eslintOutput);

      results.forEach((result) => {
        result.messages.forEach((message) => {
          if (message.severity === 2) {
            this.metrics.eslint.errors++;
          } else {
            this.metrics.eslint.warnings++;
          }

          if (message.fix) {
            this.metrics.eslint.fixable++;
          }
        });
      });

      // 保存详细报告
      await fs.promises.writeFile(
        path.join(this.reportDir, `eslint-${this.timestamp}.json`),
        JSON.stringify(results, null, 2)
      );

      console.log(
        chalk.green(
          `✅ ESLint 分析完成: ${this.metrics.eslint.errors} 错误, ${this.metrics.eslint.warnings} 警告`
        )
      );
    } catch (error) {
      if (error.stdout) {
        const results = JSON.parse(error.stdout);
        // 处理有错误的情况
        results.forEach((result) => {
          result.messages.forEach((message) => {
            if (message.severity === 2) {
              this.metrics.eslint.errors++;
            } else {
              this.metrics.eslint.warnings++;
            }
          });
        });
      }
      console.log(
        chalk.yellow(
          `⚠️ ESLint 发现问题: ${this.metrics.eslint.errors} 错误, ${this.metrics.eslint.warnings} 警告`
        )
      );
    }
  }

  /**
   * Prettier 代码格式检查
   */
  async runPrettierCheck() {
    console.log(chalk.yellow('🎨 检查代码格式...'));

    try {
      await execAsync('npx prettier --check .', { encoding: 'utf8' });
      console.log(chalk.green('✅ 代码格式检查通过'));
    } catch (error) {
      const output = error.stdout?.toString() || '';
      const violations = output.split('\n').filter((line) => line.trim()).length;
      this.metrics.prettier.violations = violations;
      console.log(chalk.yellow(`⚠️ 发现 ${violations} 个格式问题`));
    }
  }

  /**
   * TypeScript 类型检查
   */
  async runTypeScriptCheck() {
    console.log(chalk.yellow('📝 运行 TypeScript 类型检查...'));

    if (!fs.existsSync(path.join(this.projectRoot, 'tsconfig.json'))) {
      console.log(chalk.yellow('⚠️ 未找到 tsconfig.json，跳过 TypeScript 检查'));
      return;
    }

    try {
      const { stdout: output } = await execAsync('npx tsc --noEmit --pretty false', {
        encoding: 'utf8',
        cwd: this.projectRoot,
      });
      console.log(chalk.green('✅ TypeScript 类型检查通过'));
    } catch (error) {
      const output = error.stdout?.toString() || '';
      const lines = output.split('\n').filter((line) => line.trim());

      lines.forEach((line) => {
        if (line.includes('error TS')) {
          this.metrics.typescript.errors++;
        } else if (line.includes('warning TS')) {
          this.metrics.typescript.warnings++;
        }
      });

      console.log(
        chalk.yellow(
          `⚠️ TypeScript 检查发现: ${this.metrics.typescript.errors} 错误, ${this.metrics.typescript.warnings} 警告`
        )
      );
    }
  }

  /**
   * 测试覆盖率分析
   */
  async runTestCoverage() {
    console.log(chalk.yellow('🧪 运行测试覆盖率分析...'));

    try {
      const { stdout: output } = await execAsync('npm run test:coverage -- --silent', {
        encoding: 'utf8',
        cwd: this.projectRoot,
      });

      // 解析覆盖率报告
      const coverageMatch = output.match(/All files\s+\|\s+([\d.]+)/);
      if (coverageMatch) {
        this.metrics.tests.coverage = parseFloat(coverageMatch[1]);
      }

      // 解析测试结果
      const passedMatch = output.match(/(\d+) passing/);
      const failedMatch = output.match(/(\d+) failing/);

      if (passedMatch) {
        this.metrics.tests.passed = parseInt(passedMatch[1]);
      }
      if (failedMatch) {
        this.metrics.tests.failed = parseInt(failedMatch[1]);
      }

      console.log(chalk.green(`✅ 测试覆盖率: ${this.metrics.tests.coverage}%`));
    } catch (error) {
      console.log(chalk.yellow('⚠️ 测试覆盖率分析失败'));
    }
  }

  /**
   * 安全漏洞检查
   */
  async runSecurityAudit() {
    console.log(chalk.yellow('🔒 运行安全漏洞检查...'));

    try {
      const { stdout: output } = await execAsync('npm audit --json', {
        encoding: 'utf8',
        cwd: this.projectRoot,
      });

      const auditResult = JSON.parse(output);

      if (auditResult.vulnerabilities) {
        Object.values(auditResult.vulnerabilities).forEach((vuln) => {
          this.metrics.security.vulnerabilities++;
          const { severity } = vuln;
          this.metrics.security.severity[severity] =
            (this.metrics.security.severity[severity] || 0) + 1;
        });
      }

      console.log(chalk.green(`✅ 安全检查完成: ${this.metrics.security.vulnerabilities} 个漏洞`));
    } catch (error) {
      console.log(chalk.yellow('⚠️ 安全检查失败'));
    }
  }

  /**
   * 代码复杂度分析
   */
  async runComplexityAnalysis() {
    console.log(chalk.yellow('📊 分析代码复杂度...'));

    try {
      // 使用 ESLint 的复杂度规则进行分析
      const { stdout: output } = await execAsync(
        'npx eslint . --format json --no-eslintrc --config .eslintrc.enhanced.cjs',
        { encoding: 'utf8', cwd: this.projectRoot }
      );

      const results = JSON.parse(output);
      const complexityViolations = [];

      results.forEach((result) => {
        result.messages.forEach((message) => {
          if (message.ruleId === 'complexity') {
            complexityViolations.push(message);
            this.metrics.complexity.violations++;
          }
        });
      });

      console.log(chalk.green(`✅ 复杂度分析完成: ${this.metrics.complexity.violations} 个违规`));
    } catch (error) {
      console.log(chalk.yellow('⚠️ 复杂度分析失败'));
    }
  }

  /**
   * 重复代码检测
   */
  async runDuplicateDetection() {
    console.log(chalk.yellow('🔍 检测重复代码...'));

    try {
      // 简单的重复代码检测逻辑
      const jsFiles = await this.getJavaScriptFiles();
      const duplicates = await this.findDuplicates(jsFiles);

      this.metrics.duplicates.lines = duplicates.totalLines;
      this.metrics.duplicates.percentage = duplicates.percentage;

      console.log(
        chalk.green(`✅ 重复代码检测完成: ${this.metrics.duplicates.percentage}% 重复率`)
      );
    } catch (error) {
      console.log(chalk.yellow('⚠️ 重复代码检测失败'));
    }
  }

  /**
   * 依赖项检查
   */
  async runDependencyCheck() {
    console.log(chalk.yellow('📦 检查依赖项状态...'));

    try {
      const { stdout: output } = await execAsync('npm outdated --json', {
        encoding: 'utf8',
        cwd: this.projectRoot,
      });

      const outdated = JSON.parse(output);
      this.metrics.dependencies.outdated = Object.keys(outdated).length;

      console.log(chalk.green(`✅ 依赖检查完成: ${this.metrics.dependencies.outdated} 个过期依赖`));
    } catch (error) {
      // npm outdated 在有过期依赖时会返回非零退出码
      if (error.stdout) {
        const outdated = JSON.parse(error.stdout);
        this.metrics.dependencies.outdated = Object.keys(outdated).length;
      }
      console.log(chalk.green(`✅ 依赖检查完成: ${this.metrics.dependencies.outdated} 个过期依赖`));
    }
  }

  /**
   * 计算总体质量分数
   */
  calculateQualityScore() {
    let score = 100;

    // ESLint 错误和警告扣分
    score -= this.metrics.eslint.errors * 2;
    score -= this.metrics.eslint.warnings * 0.5;

    // 格式问题扣分
    score -= this.metrics.prettier.violations * 0.1;

    // TypeScript 错误扣分
    score -= this.metrics.typescript.errors * 1.5;
    score -= this.metrics.typescript.warnings * 0.3;

    // 测试覆盖率加分/扣分
    if (this.metrics.tests.coverage >= 80) {
      score += 10;
    } else if (this.metrics.tests.coverage < 50) {
      score -= 15;
    }

    // 安全漏洞扣分
    score -= this.metrics.security.vulnerabilities * 3;

    // 复杂度违规扣分
    score -= this.metrics.complexity.violations * 1;

    // 重复代码扣分
    score -= this.metrics.duplicates.percentage * 0.5;

    // 过期依赖扣分
    score -= this.metrics.dependencies.outdated * 0.2;

    this.qualityScore = Math.max(0, Math.min(100, score));
  }

  /**
   * 生成改进建议
   */
  generateRecommendations() {
    if (this.metrics.eslint.errors > 0) {
      this.recommendations.push({
        priority: 'high',
        category: 'code_quality',
        message: `修复 ${this.metrics.eslint.errors} 个 ESLint 错误`,
        action: 'npm run lint:fix',
      });
    }

    if (this.metrics.prettier.violations > 0) {
      this.recommendations.push({
        priority: 'medium',
        category: 'formatting',
