    console.log(`   高复杂度文件: ${summary.highComplexityFiles.length}`);
    console.log(`   重复代码块: ${summary.duplicateCode.length}`);

    if (recommendations.length > 0) {
      console.log('\n💡 改进建议:');
      recommendations.forEach((rec, index) => {
