    }

    // 重复代码扣分
    if (summary.duplicateCode.length > 10) {
      score -= 20;
    } else if (summary.duplicateCode.length > 5) {
      score -= 10;
    } else if (summary.duplicateCode.length > 0) {
      score -= 5;
    }

    return Math.max(0, score);
  }

  // 获取质量等级
  getQualityGrade(score) {
