    let selectedBackup;

    if (backupId) {
      selectedBackup = backups.find((b) => b.id === backupId);
      if (!selectedBackup) {
        throw new Error(`备份不存在: ${backupId}`);
      }
    } else {
      // 使用最新的备份
      selectedBackup = backups[0];
    }

    console.log(`📦 回滚到备份: ${selectedBackup.id}`);
    console.log(`📅 备份时间: ${selectedBackup.timestamp}`);

