  /**
   * 创建迁移表
   */
  async createMigrationTables() {
    console.log('📋 创建迁移管理表...');

    // 创建迁移记录表
    await this.db.createTable(this.options.tableName, {
      id: 'INTEGER PRIMARY KEY AUTOINCREMENT',
      name: 'VARCHAR(255) NOT NULL',
      batch: 'INTEGER NOT NULL',
      migration_time: 'DATETIME DEFAULT CURRENT_TIMESTAMP',
    });

    // 创建迁移锁表
    await this.db.createTable(this.options.lockTable, {
      index: 'INTEGER PRIMARY KEY',
      is_locked: 'INTEGER DEFAULT 0',
    });

    // 初始化锁记录
    await this.db.insertOrIgnore(this.options.lockTable, {
      index: 1,
      is_locked: 0,
    });

    console.log('✅ 迁移管理表创建完成');
  }

  /**
   * 创建新迁移
   */
  async createMigration(name, options = {}) {
    if (!name) {
      throw new Error('迁移名称不能为空');
    }

    const timestamp = new Date()
      .toISOString()
      .replace(/[:.T-]/g, '')
      .slice(0, 14);
    const filename = `${timestamp}_${name.toLowerCase().replace(/\s+/g, '_')}.js`;
    const filepath = path.join(this.options.projectRoot, this.options.migrationsDir, filename);

    const template = this.generateMigrationTemplate(name, options);

    await fs.promises.writeFile(filepath, template);

    console.log(`✅ 迁移文件已创建: ${filename}`);
    console.log(`📁 路径: ${filepath}`);

    return filepath;
  }

  /**
   * 生成迁移模板
   */
  generateMigrationTemplate(name, options = {}) {
    const { table, action = 'create' } = options;

    let template = `/**
 * 迁移: ${name}
 * 创建时间: ${new Date().toISOString()}
 */

`;

    if (action === 'create' && table) {
      template += `export async function up(db) {
  // 创建表: ${table}
  await db.createTable('${table}', {
    id: 'INTEGER PRIMARY KEY AUTOINCREMENT',
    created_at: 'DATETIME DEFAULT CURRENT_TIMESTAMP',
    updated_at: 'DATETIME DEFAULT CURRENT_TIMESTAMP'
    // 添加其他字段...
  });

  console.log('✅ 表 ${table} 创建成功');
}

export async function down(db) {
  // 删除表: ${table}
  await db.dropTable('${table}');

  console.log('✅ 表 ${table} 删除成功');
}
`;
    } else if (action === 'alter' && table) {
      template += `export async function up(db) {
  // 修改表: ${table}
  await db.alterTable('${table}', {
    // 添加列
    // new_column: 'VARCHAR(255)'

    // 修改列
    // existing_column: 'TEXT'
  });

  console.log('✅ 表 ${table} 修改成功');
}

export async function down(db) {
  // 回滚表修改: ${table}
  await db.alterTable('${table}', {
    // 回滚操作...
  });

  console.log('✅ 表 ${table} 回滚成功');
}
`;
    } else {
      template += `export async function up(db) {
  // 执行迁移操作
  console.log('执行迁移: ${name}');

  // 示例：创建表
  // await db.createTable('example_table', {
  //   id: 'INTEGER PRIMARY KEY AUTOINCREMENT',
  //   name: 'VARCHAR(255) NOT NULL',
  //   created_at: 'DATETIME DEFAULT CURRENT_TIMESTAMP'
  // });

  // 示例：插入数据
  // await db.insert('example_table', {
  //   name: 'Example'
  // });

  console.log('✅ 迁移 ${name} 执行成功');
}

export async function down(db) {
  // 回滚迁移操作
  console.log('回滚迁移: ${name}');

  // 示例：删除表
  // await db.dropTable('example_table');

  console.log('✅ 迁移 ${name} 回滚成功');
}
`;
    }

    return template;
  }

  /**
   * 运行迁移
   */
  async migrate(options = {}) {
    const { target, step } = options;

    console.log('🚀 开始执行数据库迁移...');

    try {
      // 获取迁移锁
      await this.acquireLock();

      // 获取待执行的迁移
      const pendingMigrations = await this.getPendingMigrations();

      if (pendingMigrations.length === 0) {
        console.log('✅ 没有待执行的迁移');
        return;
      }

      console.log(`📋 发现 ${pendingMigrations.length} 个待执行的迁移`);

      // 确定要执行的迁移
      let migrationsToRun = pendingMigrations;

      if (target) {
        const targetIndex = pendingMigrations.findIndex((m) => m.name.includes(target));
        if (targetIndex === -1) {
          throw new Error(`未找到目标迁移: ${target}`);
        }
        migrationsToRun = pendingMigrations.slice(0, targetIndex + 1);
      } else if (step) {
        migrationsToRun = pendingMigrations.slice(0, step);
      }

      // 获取下一个批次号
      const nextBatch = await this.getNextBatchNumber();

      // 执行迁移
      for (const migration of migrationsToRun) {
        await this.runMigration(migration, nextBatch);
      }

      console.log(`✅ 成功执行 ${migrationsToRun.length} 个迁移`);
    } finally {
      // 释放迁移锁
      await this.releaseLock();
    }
  }

  /**
   * 回滚迁移
   */
  async rollback(options = {}) {
    const { target, step = 1 } = options;

    console.log('🔄 开始回滚数据库迁移...');

    try {
      // 获取迁移锁
      await this.acquireLock();

      // 获取已执行的迁移
      const executedMigrations = await this.getExecutedMigrations();

      if (executedMigrations.length === 0) {
        console.log('✅ 没有可回滚的迁移');
        return;
      }

      // 确定要回滚的迁移
      let migrationsToRollback;

      if (target) {
        const targetIndex = executedMigrations.findIndex((m) => m.name.includes(target));
        if (targetIndex === -1) {
          throw new Error(`未找到目标迁移: ${target}`);
        }
        migrationsToRollback = executedMigrations.slice(0, targetIndex + 1);
      } else {
        // 获取最后一个批次的迁移
        const lastBatch = Math.max(...executedMigrations.map((m) => m.batch));
        migrationsToRollback = executedMigrations.filter((m) => m.batch === lastBatch);

        if (step > 1) {
          // 如果指定了步数，获取更多批次
          const batches = [...new Set(executedMigrations.map((m) => m.batch))].sort(
            (a, b) => b - a
          );
          const targetBatches = batches.slice(0, step);
          migrationsToRollback = executedMigrations.filter((m) => targetBatches.includes(m.batch));
        }
      }

      console.log(`📋 将回滚 ${migrationsToRollback.length} 个迁移`);

      // 按执行顺序的逆序回滚
      migrationsToRollback.reverse();

      // 执行回滚
      for (const migration of migrationsToRollback) {
        await this.rollbackMigration(migration);
      }

      console.log(`✅ 成功回滚 ${migrationsToRollback.length} 个迁移`);
    } finally {
      // 释放迁移锁
      await this.releaseLock();
    }
  }

  /**
   * 获取迁移状态
   */
  async status() {
    console.log('📊 检查迁移状态...');

    const allMigrations = await this.getAllMigrationFiles();
    const executedMigrations = await this.getExecutedMigrations();

    console.log('\n📋 迁移状态报告:');
    console.log('='.repeat(60));

    const executedNames = new Set(executedMigrations.map((m) => m.name));

    for (const migration of allMigrations) {
      const status = executedNames.has(migration.name) ? '✅ 已执行' : '⏳ 待执行';
      const executed = executedMigrations.find((m) => m.name === migration.name);
      const batchInfo = executed ? ` (批次: ${executed.batch})` : '';

      console.log(`${status} ${migration.name}${batchInfo}`);
    }

    console.log('='.repeat(60));
    console.log(`总计: ${allMigrations.length} 个迁移`);
    console.log(`已执行: ${executedMigrations.length} 个`);
    console.log(`待执行: ${allMigrations.length - executedMigrations.length} 个`);
  }

  /**
   * 运行种子数据
   */
  async seed(options = {}) {
    const { specific } = options;

    console.log('🌱 开始运行种子数据...');

    const seedFiles = await this.getSeedFiles(specific);

    if (seedFiles.length === 0) {
      console.log('✅ 没有找到种子文件');
      return;
    }

    console.log(`📋 发现 ${seedFiles.length} 个种子文件`);

    for (const seedFile of seedFiles) {
      await this.runSeed(seedFile);
    }

    console.log(`✅ 成功运行 ${seedFiles.length} 个种子文件`);
  }

  /**
   * 重置数据库
   */
  async reset() {
    console.log('🔄 重置数据库...');

    try {
      // 获取迁移锁
      await this.acquireLock();

      // 回滚所有迁移
      const executedMigrations = await this.getExecutedMigrations();

      if (executedMigrations.length > 0) {
        console.log(`📋 回滚 ${executedMigrations.length} 个迁移`);

        // 按执行顺序的逆序回滚
        executedMigrations.reverse();

        for (const migration of executedMigrations) {
          await this.rollbackMigration(migration);
        }
      }

      // 重新运行所有迁移
      await this.migrate();

      console.log('✅ 数据库重置完成');
    } finally {
      // 释放迁移锁
      await this.releaseLock();
    }
  }

  /**
   * 获取待执行的迁移
   */
  async getPendingMigrations() {
    const allMigrations = await this.getAllMigrationFiles();
    const executedMigrations = await this.getExecutedMigrations();

    const executedNames = new Set(executedMigrations.map((m) => m.name));

    return allMigrations.filter((migration) => !executedNames.has(migration.name));
  }

  /**
   * 获取已执行的迁移
   */
  async getExecutedMigrations() {
    return await this.db.select(this.options.tableName, {
      orderBy: 'migration_time DESC',
    });
  }

  /**
   * 获取所有迁移文件
   */
  async getAllMigrationFiles() {
    const migrationsDir = path.join(this.options.projectRoot, this.options.migrationsDir);

    try {
      await fs.promises.access(migrationsDir);
    } catch {
      return [];
    }

    const files = (await fs.promises.readdir(migrationsDir))
      .filter((file) => file.endsWith('.js'))
      .sort();

    return files.map((file) => ({
      name: file,
      path: path.join(migrationsDir, file),
    }));
  }

  /**
   * 运行单个迁移
   */
  async runMigration(migration, batch) {
    console.log(`   执行迁移: ${migration.name}`);

    try {
      // 动态导入迁移文件
      const migrationModule = await import(`file://${migration.path}`);

      if (typeof migrationModule.up !== 'function') {
        throw new Error(`迁移文件 ${migration.name} 缺少 up 函数`);
      }

      // 执行迁移
      await migrationModule.up(this.db);

      // 记录迁移
      await this.db.insert(this.options.tableName, {
        name: migration.name,
        batch,
      });

      console.log(`   ✅ ${migration.name} 执行成功`);
    } catch (error) {
      console.error(`   ❌ ${migration.name} 执行失败: ${error.message}`);
      throw error;
    }
  }

  /**
   * 回滚单个迁移
   */
  async rollbackMigration(migration) {
    console.log(`   回滚迁移: ${migration.name}`);

    try {
      // 构建迁移文件路径
      const migrationPath = path.join(
        this.options.projectRoot,
        this.options.migrationsDir,
        migration.name
      );

      // 动态导入迁移文件
      const migrationModule = await import(`file://${migrationPath}`);

      if (typeof migrationModule.down !== 'function') {
        throw new Error(`迁移文件 ${migration.name} 缺少 down 函数`);
      }

      // 执行回滚
      await migrationModule.down(this.db);

      // 删除迁移记录
      await this.db.delete(this.options.tableName, {
        name: migration.name,
      });

      console.log(`   ✅ ${migration.name} 回滚成功`);
    } catch (error) {
      console.error(`   ❌ ${migration.name} 回滚失败: ${error.message}`);
      throw error;
    }
  }

  /**
   * 获取种子文件
   */
  async getSeedFiles(specific = null) {
    const seedsDir = path.join(this.options.projectRoot, this.options.seedsDir);

    try {
      await fs.promises.access(seedsDir);
    } catch {
      return [];
    }

    let files = (await fs.promises.readdir(seedsDir)).filter((file) => file.endsWith('.js')).sort();

    if (specific) {
      files = files.filter((file) => file.includes(specific));
    }

    return files.map((file) => ({
      name: file,
      path: path.join(seedsDir, file),
    }));
  }

  /**
   * 运行种子文件
   */
  async runSeed(seedFile) {
    console.log(`   运行种子: ${seedFile.name}`);

    try {
      // 动态导入种子文件
      const seedModule = await import(`file://${seedFile.path}`);

      if (typeof seedModule.seed !== 'function') {
        throw new Error(`种子文件 ${seedFile.name} 缺少 seed 函数`);
      }

      // 执行种子
      await seedModule.seed(this.db);

      console.log(`   ✅ ${seedFile.name} 运行成功`);
    } catch (error) {
      console.error(`   ❌ ${seedFile.name} 运行失败: ${error.message}`);
      throw error;
    }
  }

  /**
   * 获取下一个批次号
   */
  async getNextBatchNumber() {
    const result = await this.db.select(this.options.tableName, {
      select: 'MAX(batch) as max_batch',
    });

    const maxBatch = result[0]?.max_batch || 0;
    return maxBatch + 1;
  }

  /**
   * 获取迁移锁
   */
  async acquireLock() {
    const lockResult = await this.db.select(this.options.lockTable, {
      where: { index: 1 },
    });

    if (lockResult[0]?.is_locked) {
      throw new Error('迁移正在进行中，请稍后再试');
    }

    await this.db.update(this.options.lockTable, { is_locked: 1 }, { index: 1 });
  }

  /**
   * 释放迁移锁
   */
  async releaseLock() {
    await this.db.update(this.options.lockTable, { is_locked: 0 }, { index: 1 });
  }

  /**
   * 清理资源
   */
  async cleanup() {
    if (this.db) {
      await this.db.disconnect();
    }
  }
}

/**
 * 模拟数据库类（实际使用时应该替换为真实的数据库连接）
 */
class MockDatabase {
  constructor(config) {
    this.config = config;
    this.tables = new Map();
  }

  async connect() {
    console.log('   模拟数据库连接成功');
  }

  async disconnect() {
    console.log('   模拟数据库连接关闭');
  }

  async createTable(tableName, schema) {
    this.tables.set(tableName, {
      schema,
      data: [],
    });
    console.log(`   创建表: ${tableName}`);
  }

  async dropTable(tableName) {
    this.tables.delete(tableName);
    console.log(`   删除表: ${tableName}`);
  }

  async alterTable(tableName, changes) {
    console.log(`   修改表: ${tableName}`);
  }

  async insert(tableName, data) {
    if (!this.tables.has(tableName)) {
      throw new Error(`表不存在: ${tableName}`);
    }

    const table = this.tables.get(tableName);
    const record = {
      id: table.data.length + 1,
      ...data,
      migration_time: new Date().toISOString(),
    };

    table.data.push(record);
    return record;
  }

  async insertOrIgnore(tableName, data) {
    try {
      return await this.insert(tableName, data);
    } catch (error) {
      // 忽略错误
    }
  }

  async select(tableName, options = {}) {
    if (!this.tables.has(tableName)) {
      return [];
    }

    const table = this.tables.get(tableName);
    let result = [...table.data];

    // 简单的查询实现
    if (options.where) {
      result = result.filter((row) =>
        Object.entries(options.where).every(([key, value]) => row[key] === value)
      );
    }

    if (options.orderBy) {
      const [field, direction = 'ASC'] = options.orderBy.split(' ');
      result.sort((a, b) => {
        if (direction.toUpperCase() === 'DESC') {
          return b[field] > a[field] ? 1 : -1;
        }
        return a[field] > b[field] ? 1 : -1;
      });
    }

    return result;
  }

  async update(tableName, data, where) {
    if (!this.tables.has(tableName)) {
      throw new Error(`表不存在: ${tableName}`);
    }

    const table = this.tables.get(tableName);

    table.data.forEach((row) => {
      const matches = Object.entries(where).every(([key, value]) => row[key] === value);
      if (matches) {
        Object.assign(row, data);
      }
    });
  }

  async delete(tableName, where) {
    if (!this.tables.has(tableName)) {
      throw new Error(`表不存在: ${tableName}`);
    }

    const table = this.tables.get(tableName);

    table.data = table.data.filter(
      (row) => !Object.entries(where).every(([key, value]) => row[key] === value)
    );
  }
}

// 命令行接口
if (import.meta.url === `file://${process.argv[1]}`) {
  const args = process.argv.slice(2);
  const command = args[0];

  const manager = new MigrationManager();

  async function main() {
    try {
      await manager.initialize();

      switch (command) {
        case 'create':
          const name = args[1];
          if (!name) {
            console.error(
              '请指定迁移名称: create <name> [--table=table_name] [--action=create|alter]'
            );
            process.exit(1);
          }

          const options = {};
          args.slice(2).forEach((arg) => {
            if (arg.startsWith('--table=')) {
              options.table = arg.split('=')[1];
            }
            if (arg.startsWith('--action=')) {
              options.action = arg.split('=')[1];
            }
          });

          await manager.createMigration(name, options);
          break;

        case 'migrate':
        case 'up':
          const migrateOptions = {};
          if (args.includes('--step')) {
            const stepIndex = args.indexOf('--step');
            migrateOptions.step = parseInt(args[stepIndex + 1]) || 1;
          }
          await manager.migrate(migrateOptions);
          break;

        case 'rollback':
        case 'down':
          const rollbackOptions = {};
          if (args.includes('--step')) {
            const stepIndex = args.indexOf('--step');
            rollbackOptions.step = parseInt(args[stepIndex + 1]) || 1;
          }
          await manager.rollback(rollbackOptions);
          break;

        case 'status':
          await manager.status();
          break;

        case 'seed':
          const seedOptions = {};
          if (args[1]) {
            seedOptions.specific = args[1];
          }
          await manager.seed(seedOptions);
          break;

        case 'reset':
          await manager.reset();
          break;

        default:
          console.log(`
使用方法:
  node migration-manager.js create <name> [--table=table_name] [--action=create|alter]
  node migration-manager.js migrate [--step=n]
  node migration-manager.js rollback [--step=n]
  node migration-manager.js status
  node migration-manager.js seed [seed_name]
  node migration-manager.js reset
`);
      }
    } catch (error) {
      console.error(`\n❌ 操作失败: ${error.message}`);
      process.exit(1);
    } finally {
      await manager.cleanup();
    }
  }

  main();
}

export default MigrationManager;

#!/usr/bin/env node

import { execSync } from 'child_process';
import fs from 'fs';
import path from 'path';
import chalk from 'chalk';

/**
 * 代码质量检查脚本
 * 集成ESLint、Prettier、测试和安全检查
 */
class QualityChecker {
  constructor() {
    this.errors = [];
    this.warnings = [];
    this.projectRoot = process.cwd();
    this.startTime = Date.now();
  }

  /**
   * 运行所有质量检查
   */
  async runAll() {
    console.log(chalk.blue.bold('🔍 开始代码质量检查...\n'));

    try {
      await this.checkDependencies();
      await this.runPrettierCheck();
      await this.runESLintCheck();
      await this.runTests();
      await this.runSecurityCheck();
      await this.checkFileStructure();
      await this.generateReport();
    } catch (error) {
      this.errors.push(`质量检查过程中发生错误: ${error.message}`);
    }

    this.printSummary();
    process.exit(this.errors.length > 0 ? 1 : 0);
  }

  /**
   * 检查依赖项
   */
  async checkDependencies() {
    console.log(chalk.yellow('📦 检查项目依赖...'));

    try {
      // 检查package.json是否存在
      const packageJsonPath = path.join(this.projectRoot, 'package.json');
      if (!fs.existsSync(packageJsonPath)) {
        this.errors.push('package.json 文件不存在');
        return;
      }

      // 检查node_modules是否存在
      const nodeModulesPath = path.join(this.projectRoot, 'node_modules');
      if (!fs.existsSync(nodeModulesPath)) {
        console.log(chalk.yellow('  正在安装依赖...'));
        execSync('npm install', { stdio: 'inherit' });
      }

      // 检查关键依赖
      const packageJson = JSON.parse(fs.readFileSync(packageJsonPath, 'utf8'));
      const requiredDeps = ['express', 'sqlite3', 'jest'];
      const missingDeps = requiredDeps.filter(
        (dep) => !packageJson.dependencies?.[dep] && !packageJson.devDependencies?.[dep]
      );

      if (missingDeps.length > 0) {
        this.warnings.push(`缺少推荐依赖: ${missingDeps.join(', ')}`);
      }

      console.log(chalk.green('  ✅ 依赖检查完成'));
    } catch (error) {
      this.errors.push(`依赖检查失败: ${error.message}`);
    }
  }

  /**
   * 运行Prettier格式检查
   */
  async runPrettierCheck() {
    console.log(chalk.yellow('🎨 检查代码格式...'));

    try {
      // 检查是否有Prettier配置
      const prettierConfigExists = [
        '.prettierrc',
        '.prettierrc.json',
        '.prettierrc.js',
        'prettier.config.js',
      ].some((config) => fs.existsSync(path.join(this.projectRoot, config)));

      if (!prettierConfigExists) {
        this.warnings.push('未找到Prettier配置文件');
        return;
      }

      // 运行Prettier检查
      const result = execSync('npx prettier --check "src/**/*.js" "tests/**/*.js" "*.js"', {
        encoding: 'utf8',
        stdio: 'pipe',
      });

      console.log(chalk.green('  ✅ 代码格式检查通过'));
    } catch (error) {
      if (error.status === 1) {
        this.warnings.push('代码格式不符合Prettier规范');
        console.log(chalk.yellow('  ⚠️  代码格式需要修复'));
        console.log(chalk.gray('  运行 "npm run format" 自动修复格式问题'));
      } else {
        this.errors.push(`Prettier检查失败: ${error.message}`);
      }
    }
  }

  /**
   * 运行ESLint检查
   */
  async runESLintCheck() {
    console.log(chalk.yellow('🔧 运行ESLint检查...'));

    try {
      // 检查ESLint配置
      const eslintConfigExists = [
        '.eslintrc',
        '.eslintrc.json',
        '.eslintrc.js',
        '.eslintrc.yml',
        '.eslintrc.yaml',
      ].some((config) => fs.existsSync(path.join(this.projectRoot, config)));

      if (!eslintConfigExists) {
        this.warnings.push('未找到ESLint配置文件');
        return;
      }

      // 运行ESLint
      const result = execSync('npx eslint "src/**/*.js" "tests/**/*.js" "*.js" --format=json', {
        encoding: 'utf8',
        stdio: 'pipe',
      });

      const eslintResults = JSON.parse(result);
      let totalErrors = 0;
      let totalWarnings = 0;

      eslintResults.forEach((file) => {
        totalErrors += file.errorCount;
        totalWarnings += file.warningCount;

        if (file.errorCount > 0 || file.warningCount > 0) {
          console.log(chalk.gray(`  ${file.filePath}:`));
          file.messages.forEach((message) => {
            const level = message.severity === 2 ? 'error' : 'warning';
            const color = message.severity === 2 ? chalk.red : chalk.yellow;
            console.log(color(`    ${level}: ${message.message} (${message.ruleId})`));
          });
        }
      });

      if (totalErrors > 0) {
        this.errors.push(`ESLint发现 ${totalErrors} 个错误`);
      }
      if (totalWarnings > 0) {
        this.warnings.push(`ESLint发现 ${totalWarnings} 个警告`);
      }

      if (totalErrors === 0 && totalWarnings === 0) {
        console.log(chalk.green('  ✅ ESLint检查通过'));
      }
    } catch (error) {
      if (error.status === 1) {
        // ESLint找到了问题，但这已经在上面处理了
      } else {
        this.errors.push(`ESLint检查失败: ${error.message}`);
      }
    }
  }

  /**
   * 运行测试
   */
  async runTests() {
    console.log(chalk.yellow('🧪 运行测试套件...'));

    try {
      const result = execSync('npm test', {
        encoding: 'utf8',
        stdio: 'pipe',
        env: { ...process.env, CI: 'true' },
      });

      console.log(chalk.green('  ✅ 所有测试通过'));
    } catch (error) {
      this.errors.push('测试失败');
      console.log(chalk.red('  ❌ 测试失败'));
      console.log(chalk.gray(error.stdout));
    }
  }

  /**
   * 运行安全检查
   */
  async runSecurityCheck() {
    console.log(chalk.yellow('🔒 运行安全检查...'));

    try {
      // 检查npm audit
      const auditResult = execSync('npm audit --audit-level=moderate --json', {
        encoding: 'utf8',
        stdio: 'pipe',
      });

      const audit = JSON.parse(auditResult);
      if (audit.metadata.vulnerabilities.total > 0) {
        const { high, critical } = audit.metadata.vulnerabilities;
        if (high > 0 || critical > 0) {
          this.errors.push(`发现 ${high} 个高危和 ${critical} 个严重安全漏洞`);
        } else {
          this.warnings.push(`发现 ${audit.metadata.vulnerabilities.total} 个安全漏洞`);
        }
      } else {
        console.log(chalk.green('  ✅ 未发现安全漏洞'));
      }
    } catch (error) {
      if (error.status === 1) {
        // npm audit 发现了漏洞，但这已经在上面处理了
      } else {
        this.warnings.push('安全检查失败，请手动运行 npm audit');
      }
    }

    // 检查敏感文件
    this.checkSensitiveFiles();
  }

  /**
   * 检查敏感文件
   */
  checkSensitiveFiles() {
    const sensitivePatterns = [
      /\.env$/,
      /\.env\./,
      /private.*key/i,
      /.*\.pem$/,
      /.*\.p12$/,
      /.*\.pfx$/,
      /config.*secret/i,
    ];

    const checkDirectory = (dir) => {
      const files = fs.readdirSync(dir);
      files.forEach((file) => {
        const filePath = path.join(dir, file);
        const stat = fs.statSync(filePath);

        if (stat.isDirectory() && !file.startsWith('.') && file !== 'node_modules') {
          checkDirectory(filePath);
        } else if (stat.isFile()) {
          sensitivePatterns.forEach((pattern) => {
            if (pattern.test(file)) {
              this.warnings.push(`发现敏感文件: ${filePath}`);
            }
          });
        }
      });
    };

    try {
      checkDirectory(this.projectRoot);
    } catch (error) {
      this.warnings.push(`敏感文件检查失败: ${error.message}`);
    }
  }

  /**
   * 检查文件结构
   */
  async checkFileStructure() {
    console.log(chalk.yellow('📁 检查项目结构...'));

    const requiredFiles = ['package.json', 'README.md', '.gitignore'];

    const recommendedDirs = ['src', 'tests'];

    requiredFiles.forEach((file) => {
      if (!fs.existsSync(path.join(this.projectRoot, file))) {
        this.warnings.push(`缺少必要文件: ${file}`);
      }
    });

    recommendedDirs.forEach((dir) => {
      if (!fs.existsSync(path.join(this.projectRoot, dir))) {
        this.warnings.push(`缺少推荐目录: ${dir}`);
      }
    });

    console.log(chalk.green('  ✅ 项目结构检查完成'));
  }

  /**
   * 生成质量报告
   */

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  async generateReport() {
    console.log(chalk.yellow('📊 生成质量报告...'));

    const report = {
      timestamp: new Date().toISOString(),
      duration: Date.now() - this.startTime,
      summary: {
        errors: this.errors.length,
        warnings: this.warnings.length,
        status: this.errors.length === 0 ? 'PASS' : 'FAIL',
      },
      details: {
        errors: this.errors,
        warnings: this.warnings,
      },
    };

    const reportPath = path.join(this.projectRoot, 'quality-report.json');
    fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));

    console.log(chalk.green(`  ✅ 质量报告已生成: ${reportPath}`));
  }

  /**
   * 打印总结
   */
  printSummary() {
    const duration = ((Date.now() - this.startTime) / 1000).toFixed(2);

    console.log(`\n${chalk.blue.bold('📋 质量检查总结')}`);
    console.log(chalk.gray(`执行时间: ${duration}秒`));

    if (this.errors.length === 0 && this.warnings.length === 0) {
      console.log(chalk.green.bold('🎉 所有检查通过！代码质量良好。'));
    } else {
      if (this.errors.length > 0) {
        console.log(chalk.red.bold(`❌ 发现 ${this.errors.length} 个错误:`));
        this.errors.forEach((error) => {
          console.log(chalk.red(`  • ${error}`));
        });
      }

      if (this.warnings.length > 0) {
        console.log(chalk.yellow.bold(`⚠️  发现 ${this.warnings.length} 个警告:`));
        this.warnings.forEach((warning) => {
          console.log(chalk.yellow(`  • ${warning}`));
        });
      }
    }

    console.log(`\n${chalk.blue('建议的修复命令:')}`);
    console.log(chalk.gray('  npm run format    # 自动修复格式问题'));
    console.log(chalk.gray('  npm run lint:fix  # 自动修复ESLint问题'));
    console.log(chalk.gray('  npm audit fix     # 修复安全漏洞'));
  }
}

// ES模块中直接执行主函数
const checker = new QualityChecker();
checker.runAll().catch((error) => {
  console.error(chalk.red('质量检查失败:'), error);
  process.exit(1);
});

export default QualityChecker;

#!/usr/bin/env node

/**
 * 代码质量提升自动化脚本
 * 执行各种代码质量检查和改进任务
 */

import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';
import { execSync } from 'child_process';

// 导入新的分析工具
import ComplexityAnalyzer from './complexity-analyzer.js';
import SecurityAuditor from './security-auditor.js';
import PerformanceMonitor from './performance-monitor.js';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// 简单的颜色输出函数
const colors = {
  red: (text) => `\x1b[31m${text}\x1b[0m`,
  green: (text) => `\x1b[32m${text}\x1b[0m`,
  yellow: (text) => `\x1b[33m${text}\x1b[0m`,
  blue: (text) => `\x1b[34m${text}\x1b[0m`,
  magenta: (text) => `\x1b[35m${text}\x1b[0m`,
  cyan: (text) => `\x1b[36m${text}\x1b[0m`,
};

class QualityEnhancer {
  constructor() {
    this.projectRoot = path.resolve(__dirname, '..');
    this.results = {
      tests: { passed: 0, failed: 0, coverage: 0 },
      lint: { errors: 0, warnings: 0 },
      security: { vulnerabilities: 0 },
      performance: { score: 0 },
      complexity: { average: 0, max: 0 },
    };
  }

  /**
   * 执行所有质量检查
   */
  async runAll() {
    console.log(colors.blue('🚀 开始代码质量提升检查...\n'));

    try {
      await this.runTests();
      await this.runLinting();
      await this.runSecurityAudit();
      await this.analyzeComplexity();
      await this.checkDependencies();
      await this.runPerformanceMonitoring();
      await this.generateReport();

      console.log(colors.green('✅ 所有质量检查完成!'));
    } catch (error) {
      console.error(colors.red('❌ 质量检查过程中出现错误:'), error.message);
      process.exit(1);
    }
  }

  /**
   * 运行测试并收集覆盖率
   */
  async runTests() {
    console.log(colors.yellow('📋 运行测试套件...'));

    try {
      // 运行单元测试
      const testOutput = execSync(
        'npx jest --config jest.simple.config.js tests/unit/ --coverage --json',
        {
          cwd: this.projectRoot,
          encoding: 'utf8',
        }
      );

      const testResults = JSON.parse(testOutput);
      this.results.tests.passed = testResults.numPassedTests;
      this.results.tests.failed = testResults.numFailedTests;

      if (testResults.coverageMap) {
        // 计算平均覆盖率
        const coverage =
          (Object.values(testResults.coverageMap).reduce(
            (acc, file) =>
              acc +
              (file.s
                ? Object.values(file.s).filter(Boolean).length / Object.keys(file.s).length
                : 0),
            0
          ) /
            Object.keys(testResults.coverageMap).length) *
          100;

        this.results.tests.coverage = Math.round(coverage);
      }

      console.log(colors.green(`  ✓ 测试通过: ${this.results.tests.passed}`));
      console.log(colors.red(`  ✗ 测试失败: ${this.results.tests.failed}`));
      console.log(colors.blue(`  📊 代码覆盖率: ${this.results.tests.coverage}%`));
    } catch (error) {
      console.log(colors.red('  ❌ 测试执行失败'));
      this.results.tests.failed = 1;
    }
  }

  /**
   * 运行代码检查
   */
  async runLinting() {
    console.log(colors.yellow('\n🔍 运行代码检查...'));

    try {
      // 使用质量配置进行检查
      const lintOutput = execSync('npx eslint . --config .eslintrc.quality.cjs --format json', {
        cwd: this.projectRoot,
        encoding: 'utf8',
      });

      const lintResults = JSON.parse(lintOutput);
      this.results.lint.errors = lintResults.reduce((acc, file) => acc + file.errorCount, 0);
      this.results.lint.warnings = lintResults.reduce((acc, file) => acc + file.warningCount, 0);

      console.log(colors.red(`  ❌ 错误: ${this.results.lint.errors}`));
      console.log(colors.yellow(`  ⚠️  警告: ${this.results.lint.warnings}`));

      // 如果有错误，尝试自动修复
      if (this.results.lint.errors > 0 || this.results.lint.warnings > 0) {
        console.log(colors.yellow('  🔧 尝试自动修复...'));
        try {
          execSync('npx eslint . --config .eslintrc.quality.cjs --fix', {
            cwd: this.projectRoot,
            encoding: 'utf8',
          });

          // 运行Prettier格式化
          execSync('npx prettier --write . --config .prettierrc.quality.json', {
            cwd: this.projectRoot,
            encoding: 'utf8',
          });

          console.log(colors.green('  ✅ 代码问题已自动修复'));
        } catch (fixError) {
          console.log(colors.yellow('  ⚠️  部分问题无法自动修复'));
        }
      }
    } catch (error) {
      console.log(colors.red('  ❌ 代码检查失败'));
      this.results.lint.errors = 1;
    }
  }

  /**
   * 运行安全审计
   */
  async runSecurityAudit() {
    console.log(colors.yellow('\n🔒 运行安全审计...'));

    try {
      // 运行npm audit
      const auditOutput = execSync('npm audit --json', {
        cwd: this.projectRoot,
        encoding: 'utf8',
      });

      const auditResults = JSON.parse(auditOutput);
      this.results.security.vulnerabilities = auditResults.metadata?.vulnerabilities?.total || 0;

      console.log(colors.red(`  🚨 npm安全漏洞: ${this.results.security.vulnerabilities}`));

      // 运行自定义安全审计
      try {
        const securityAuditor = new SecurityAuditor();
        const securityReport = await securityAuditor.run(this.projectRoot);
        this.results.security.customAudit = securityReport;

        console.log(colors.blue('  🔍 自定义安全检查完成'));
        console.log(colors.blue(`  📊 安全评分: ${securityReport.summary.securityScore}/100`));
      } catch (customError) {
        console.log(colors.yellow('  ⚠️  自定义安全审计失败'));
      }
    } catch (error) {
      console.log(colors.yellow('  ⚠️  安全审计完成 (可能存在漏洞)'));
    }
  }

  /**
   * 分析代码复杂度
   */
  async analyzeComplexity() {
    console.log(colors.yellow('\n📈 分析代码复杂度...'));

    try {
      // 使用新的复杂度分析器
      const complexityAnalyzer = new ComplexityAnalyzer();
      const complexityReport = await complexityAnalyzer.run(this.projectRoot);

      this.results.complexity.average = complexityReport.summary.averageComplexity;
      this.results.complexity.max = complexityReport.summary.maxComplexity;
      this.results.complexity.report = complexityReport;

      console.log(colors.blue(`  📊 平均复杂度: ${this.results.complexity.average}`));
      console.log(colors.blue(`  📊 最大复杂度: ${this.results.complexity.max}`));
      console.log(
        colors.blue(`  🔍 高复杂度文件: ${complexityReport.summary.highComplexityFiles.length}`)
      );
    } catch (error) {
      console.log(colors.red('  ❌ 复杂度分析失败'));
      // 回退到简化的复杂度分析
      try {
        const sourceFiles = this.getSourceFiles();
        let totalComplexity = 0;
        let maxComplexity = 0;
        let fileCount = 0;

        sourceFiles.forEach((file) => {
          const content = fs.readFileSync(file, 'utf8');
          const complexity = this.calculateComplexity(content);
          totalComplexity += complexity;
          maxComplexity = Math.max(maxComplexity, complexity);
          fileCount++;
        });

        this.results.complexity.average = Math.round(totalComplexity / fileCount);
        this.results.complexity.max = maxComplexity;

        console.log(colors.blue(`  📊 平均复杂度: ${this.results.complexity.average}`));
        console.log(colors.blue(`  📊 最大复杂度: ${this.results.complexity.max}`));
      } catch (fallbackError) {
        console.log(colors.red('  ❌ 复杂度分析完全失败'));
      }
    }
  }

  /**
   * 检查依赖项
   */
  async checkDependencies() {
    console.log(colors.yellow('\n📦 检查依赖项...'));

    try {
