  /**
   * 检查是否为合法的导入语句
   */
  isLegitimateImport(line) {
    const trimmed = line.trim();
    return (
      trimmed.startsWith('import ') ||
      trimmed.startsWith('const ') ||
      trimmed.startsWith('require(') ||
      trimmed.includes('from ') ||
      trimmed.includes('= require(')
    );
  }

  /**
   * 检查是否包含硬编码密钥
   */
  containsHardcodedSecret(line) {
    const secretPatterns = [
      /password\s*[=:]\s*['"][^'"]{8,}['"]/i,
      /secret\s*[=:]\s*['"][^'"]{16,}['"]/i,
      /key\s*[=:]\s*['"][^'"]{16,}['"]/i,
      /token\s*[=:]\s*['"][^'"]{20,}['"]/i,
      /api[_-]?key\s*[=:]\s*['"][^'"]{10,}['"]/i,
    ];

    return (
      secretPatterns.some((pattern) => pattern.test(line)) &&
      !line.includes('process.env') &&
      !line.includes('config.') &&
      !line.includes('default') &&
      !line.includes('example')
    );
  }

  /**
   * 检查是否包含SQL注入风险
   */
  containsSqlInjectionRisk(line) {
    const sqlPatterns = [
      /query\s*\(\s*['"][^'"]*\+/i,
      /execute\s*\(\s*['"][^'"]*\+/i,
      /SELECT\s+.*\+/i,
      /INSERT\s+.*\+/i,
      /UPDATE\s+.*\+/i,
      /DELETE\s+.*\+/i,
    ];

    return sqlPatterns.some((pattern) => pattern.test(line));
  }

  /**
   * 检查是否包含XSS风险
   */
  containsXssRisk(line) {
    const xssPatterns = [
      /innerHTML\s*=\s*[^'"]*\+/i,
      /outerHTML\s*=\s*[^'"]*\+/i,
      /document\.write\s*\(/i,
      /eval\s*\(/i,
      /setTimeout\s*\(\s*['"][^'"]*\+/i,
      /setInterval\s*\(\s*['"][^'"]*\+/i,
    ];

    return xssPatterns.some((pattern) => pattern.test(line));
  }

  /**
   * 检查是否包含弱加密算法
   */
  containsWeakCrypto(line) {
    const weakCryptoPatterns = [
      /['"\`]md5['"\`]/i,
      /['"\`]sha1['"\`]/i,
      /['"\`]des['"\`]/i,
      /['"\`]rc4['"\`]/i,
      /createHash\s*\(\s*['"\`]md5['"\`]/i,
      /createHash\s*\(\s*['"\`]sha1['"\`]/i,
    ];

    return weakCryptoPatterns.some((pattern) => pattern.test(line));
  }

  /**
   * 按文件分组问题
   */
  groupIssuesByFile(issues) {
    const groups = {};
    issues.forEach((issue) => {
      if (!groups[issue.file]) {
        groups[issue.file] = [];
      }
      groups[issue.file].push(issue);
    });
    return groups;
  }

  /**
   * 获取源代码文件列表
   */
  getSourceFiles() {
    const files = [];
    const searchDirs = ['src', 'backend', 'scripts'];

    searchDirs.forEach((dir) => {
      const dirPath = path.join(this.projectRoot, dir);
      if (fs.existsSync(dirPath)) {
        this.walkDir(dirPath, files);
      }
    });

    return files.filter(
      (file) =>
        (file.endsWith('.js') || file.endsWith('.ts')) &&
        !file.includes('node_modules') &&
        !file.includes('.git')
    );
  }

  /**
   * 递归遍历目录
   */
  walkDir(dir, files) {
    try {
      const items = fs.readdirSync(dir);

      items.forEach((item) => {
        const fullPath = path.join(dir, item);
        const stat = fs.statSync(fullPath);

        if (stat.isDirectory() && !item.startsWith('.')) {
          this.walkDir(fullPath, files);
        } else if (stat.isFile()) {
          files.push(fullPath);
        }

        // TODO: 考虑将此函数拆分为更小的函数 (当前 21 行)
