source:
/**
 * 国家核心指标计算引擎
 * 实现《通知》要求的核心指标计算：
 * 1. 单位能耗碳排放 (tCO2/万元)
 * 2. 清洁能源消费占比 (%)
 * 3. 园区总碳排放 (tCO2)
 */

import { EventEmitter } from 'events';
import logger from '../../shared/utils/logger.js';
import { ENERGY_CONSTANTS, MATH_CONSTANTS } from '../../shared/constants/MathConstants.js';

class NationalIndicatorEngine extends EventEmitter {
  constructor() {
    super();
    this.isInitialized = false;
    this.indicatorCache = new Map();
    this.calculationHistory = new Map();
    this.benchmarkData = new Map();

    // 国家标准排放因子 (根据《通知》要求)
    this.emissionFactors = {
      // 电力排放因子 (tCO2/MWh)
      electricity: {
        grid: 0.5703, // 全国电网平均排放因子
        regional: {
          north: 0.8843,   // 华北电网
          northeast: 0.8825, // 东北电网
          east: 0.7035,    // 华东电网
          central: 0.8367,  // 华中电网
          northwest: 0.8922, // 西北电网
          south: 0.8042    // 南方电网
        }
      },
      // 化石燃料排放因子 (tCO2/t或tCO2/万m³)
      fossil_fuels: {
        coal: 1.9003,        // 原煤 tCO2/t
        coke: 2.8604,        // 焦炭 tCO2/t
        crude_oil: 3.0202,   // 原油 tCO2/t
        gasoline: 2.9251,    // 汽油 tCO2/t
        diesel: 3.0959,      // 柴油 tCO2/t
        fuel_oil: 3.1705,    // 燃料油 tCO2/t
        natural_gas: 2.1622, // 天然气 tCO2/万m³
        lpg: 3.0112         // 液化石油气 tCO2/t
      },
      // 工业过程排放因子
      industrial_process: {
        cement: 0.5383,      // 水泥生产 tCO2/t
        steel: 2.07,         // 钢铁生产 tCO2/t
        aluminum: 1.69,      // 电解铝 tCO2/t
        glass: 0.1968,       // 平板玻璃 tCO2/重量箱
        ammonia: 1.485,      // 合成氨 tCO2/t
        nitric_acid: 0.3    // 硝酸 tCO2/t
      }
    };

    // 清洁能源类型定义
    this.cleanEnergyTypes = {
      renewable: ['solar', 'wind', 'hydro', 'biomass', 'geothermal'],
      nuclear: ['nuclear'],
      other_clean: ['hydrogen', 'clean_coal'] // 其他清洁能源
    };

    // 国家目标基准值
    this.nationalTargets = {
      carbon_intensity_reduction: 18, // 单位GDP碳强度下降18%
      clean_energy_ratio: 25,         // 清洁能源消费占比25%
      renewable_ratio: 20,            // 可再生能源消费占比20%
      energy_intensity_reduction: 13.5 // 单位GDP能耗下降13.5%
    };

    this.init();
  }

  async init() {
    try {
      await this.loadBenchmarkData();
      await this.setupCalculationScheduler();
      this.isInitialized = true;
      logger.info('国家核心指标计算引擎初始化完成');
      this.emit('initialized');
    } catch (error) {
      logger.error('国家指标计算引擎初始化失败:', error);
      throw error;
    }
  }

  /**
   * 加载基准数据
   */
  async loadBenchmarkData() {
    try {
      // 加载行业基准数据
      const industryBenchmarks = await this.getIndustryBenchmarks();
      industryBenchmarks.forEach(benchmark => {
        this.benchmarkData.set(benchmark.industry_code, benchmark);
      });

      logger.info(`已加载行业基准数据 ${this.benchmarkData.size} 个`);
    } catch (error) {
      logger.error('加载基准数据失败:', error);
      throw error;
    }
  }

  /**
   * 设置计算调度器
   */
  async setupCalculationScheduler() {
    // 每小时计算一次核心指标
    setInterval(async () => {
      try {
        await this.calculateAllIndicators();
        this.emit('indicators_updated', {
          timestamp: new Date().toISOString(),
          indicators: Array.from(this.indicatorCache.values())
        });
      } catch (error) {
        logger.error('定时计算指标失败:', error);
      }
    }, MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.MILLISECONDS_PER_SECOND);
  }

  /**
   * 计算单位能耗碳排放 (tCO2/万元)
   * 公式: E单位能耗 = (E能源活动 + E工业过程) / GDP
   * @param {string} parkId - 园区ID
   * @param {string} timeRange - 时间范围
   * @returns {Object} 单位能耗碳排放数据
   */
  async calculateUnitEnergyCarbon(parkId, timeRange = '1M') {
    try {
      const startTime = this.getStartTime(timeRange);
      const endTime = new Date();

      // 获取能源活动碳排放
      const energyEmissions = await this.calculateEnergyActivityEmissions(parkId, startTime, endTime);

      // 获取工业过程碳排放
      const processEmissions = await this.calculateIndustrialProcessEmissions(parkId, startTime, endTime);

      // 获取GDP数据
      const gdpData = await this.getGDPData(parkId, startTime, endTime);

      // 计算总碳排放
      const totalEmissions = energyEmissions.total + processEmissions.total;

      // 计算单位能耗碳排放 (tCO2/万元)
      const unitEnergyCarbon = gdpData.total > 0 ? 
        (totalEmissions / (gdpData.total / MATH_CONSTANTS.TEN_THOUSAND)).toFixed(MATH_CONSTANTS.DECIMAL_PLACES) : 0;

      const result = {
        park_id: parkId,
        time_range: timeRange,
        start_time: startTime.toISOString(),
        end_time: endTime.toISOString(),
        calculation_time: new Date().toISOString(),
        unit_energy_carbon: parseFloat(unitEnergyCarbon),
        unit: 'tCO2/万元',
        components: {
          energy_activity_emissions: energyEmissions,
          industrial_process_emissions: processEmissions,
          total_emissions: totalEmissions,
          gdp_data: gdpData
        },
        breakdown: {
          by_energy_type: energyEmissions.by_type,
          by_process_type: processEmissions.by_type,
          by_sector: await this.getEmissionsBySector(parkId, startTime, endTime)
        },
        benchmark_comparison: {
          industry_average: await this.getIndustryAverage(parkId, 'unit_energy_carbon'),
          national_target: this.nationalTargets.carbon_intensity_reduction,
          performance_rating: this.calculatePerformanceRating(unitEnergyCarbon, 'unit_energy_carbon')
        },
        trend_analysis: await this.calculateTrendAnalysis(parkId, 'unit_energy_carbon', timeRange),
        compliance_status: this.assessComplianceStatus(unitEnergyCarbon, 'unit_energy_carbon')
      };

      // 缓存结果
      this.indicatorCache.set(`unit_energy_carbon_${parkId}_${timeRange}`, result);

      logger.info(`计算单位能耗碳排放完成: ${parkId}, 值: ${unitEnergyCarbon} tCO2/万元`);
      return result;
    } catch (error) {
      logger.error('计算单位能耗碳排放失败:', error);
      throw error;
    }
  }

  /**
   * 计算清洁能源消费占比 (%)
   * 公式: R清洁能源 = E清洁能源 / E总能源消费 × 100%
   * @param {string} parkId - 园区ID
   * @param {string} timeRange - 时间范围
   * @returns {Object} 清洁能源消费占比数据
   */
  async calculateCleanEnergyRatio(parkId, timeRange = '1M') {
    try {
      const startTime = this.getStartTime(timeRange);
      const endTime = new Date();

      // 获取总能源消费数据
      const totalEnergyConsumption = await this.getTotalEnergyConsumption(parkId, startTime, endTime);

      // 获取清洁能源消费数据
      const cleanEnergyConsumption = await this.getCleanEnergyConsumption(parkId, startTime, endTime);

      // 计算清洁能源消费占比
      const cleanEnergyRatio = totalEnergyConsumption.total > 0 ? 
        (cleanEnergyConsumption.total / totalEnergyConsumption.total * MATH_CONSTANTS.ONE_HUNDRED).toFixed(MATH_CONSTANTS.DECIMAL_PLACES) : 0;

      // 分类统计
      const renewableRatio = totalEnergyConsumption.total > 0 ? 
        (cleanEnergyConsumption.renewable / totalEnergyConsumption.total * MATH_CONSTANTS.ONE_HUNDRED).toFixed(MATH_CONSTANTS.DECIMAL_PLACES) : 0;

      const nuclearRatio = totalEnergyConsumption.total > 0 ? 
        (cleanEnergyConsumption.nuclear / totalEnergyConsumption.total * MATH_CONSTANTS.ONE_HUNDRED).toFixed(MATH_CONSTANTS.DECIMAL_PLACES) : 0;

      const result = {
        park_id: parkId,
        time_range: timeRange,
        start_time: startTime.toISOString(),
        end_time: endTime.toISOString(),
        calculation_time: new Date().toISOString(),
        clean_energy_ratio: parseFloat(cleanEnergyRatio),
        renewable_ratio: parseFloat(renewableRatio),
        nuclear_ratio: parseFloat(nuclearRatio),
        unit: '%',
        energy_consumption: {
          total: totalEnergyConsumption,
          clean: cleanEnergyConsumption,
          fossil: {
            total: totalEnergyConsumption.total - cleanEnergyConsumption.total,
            by_type: totalEnergyConsumption.by_type.fossil || {}
          }
        },
        breakdown: {
          by_clean_type: {
            solar: this.calculateTypeRatio(cleanEnergyConsumption.by_type.solar, totalEnergyConsumption.total),
            wind: this.calculateTypeRatio(cleanEnergyConsumption.by_type.wind, totalEnergyConsumption.total),
            hydro: this.calculateTypeRatio(cleanEnergyConsumption.by_type.hydro, totalEnergyConsumption.total),
            biomass: this.calculateTypeRatio(cleanEnergyConsumption.by_type.biomass, totalEnergyConsumption.total),
            nuclear: this.calculateTypeRatio(cleanEnergyConsumption.by_type.nuclear, totalEnergyConsumption.total),
            other: this.calculateTypeRatio(cleanEnergyConsumption.by_type.other, totalEnergyConsumption.total)
          },
          by_sector: await this.getCleanEnergyBySector(parkId, startTime, endTime),
          by_time: await this.getCleanEnergyByTime(parkId, startTime, endTime, timeRange)
        },
        benchmark_comparison: {
          national_target: this.nationalTargets.clean_energy_ratio,
          renewable_target: this.nationalTargets.renewable_ratio,
          industry_average: await this.getIndustryAverage(parkId, 'clean_energy_ratio'),
          performance_rating: this.calculatePerformanceRating(cleanEnergyRatio, 'clean_energy_ratio')
        },
        trend_analysis: await this.calculateTrendAnalysis(parkId, 'clean_energy_ratio', timeRange),
        compliance_status: this.assessComplianceStatus(cleanEnergyRatio, 'clean_energy_ratio'),
        carbon_reduction_impact: {
          avoided_emissions: this.calculateAvoidedEmissions(cleanEnergyConsumption.total),
          equivalent_trees: Math.floor(this.calculateAvoidedEmissions(cleanEnergyConsumption.total) / ENERGY_CONSTANTS.TREE_CARBON_ABSORPTION),
          economic_value: this.calculateCarbonEconomicValue(this.calculateAvoidedEmissions(cleanEnergyConsumption.total))
        }
      };

      // 缓存结果
      this.indicatorCache.set(`clean_energy_ratio_${parkId}_${timeRange}`, result);

      logger.info(`计算清洁能源消费占比完成: ${parkId}, 值: ${cleanEnergyRatio}%`);
      return result;
    } catch (error) {
      logger.error('计算清洁能源消费占比失败:', error);
      throw error;
    }
  }

  /**
   * 计算园区总碳排放 (tCO2)
   * 公式: E园区 = E能源活动 + E工业过程 + E其他
   * @param {string} parkId - 园区ID
   * @param {string} timeRange - 时间范围
   * @returns {Object} 园区总碳排放数据
   */
  async calculateTotalParkEmissions(parkId, timeRange = '1M') {
    try {
      const startTime = this.getStartTime(timeRange);
      const endTime = new Date();

      // 获取各类碳排放
      const energyEmissions = await this.calculateEnergyActivityEmissions(parkId, startTime, endTime);
      const processEmissions = await this.calculateIndustrialProcessEmissions(parkId, startTime, endTime);
      const otherEmissions = await this.calculateOtherEmissions(parkId, startTime, endTime);

      // 计算总排放
      const totalEmissions = energyEmissions.total + processEmissions.total + otherEmissions.total;

      const result = {
        park_id: parkId,
        time_range: timeRange,
        start_time: startTime.toISOString(),
        end_time: endTime.toISOString(),
        calculation_time: new Date().toISOString(),
        total_emissions: totalEmissions,
        unit: 'tCO2',
        components: {
          energy_activity: energyEmissions,
          industrial_process: processEmissions,
          other_sources: otherEmissions
        },
        breakdown: {
          by_scope: {
            scope1: energyEmissions.scope1 + processEmissions.total + otherEmissions.total, // 直接排放
            scope2: energyEmissions.scope2, // 间接排放(电力)
            scope3: 0 // 其他间接排放(暂不计算)
          },
          by_source: {
            electricity: energyEmissions.by_type.electricity || 0,
            fossil_fuels: energyEmissions.by_type.fossil_fuels || 0,
            industrial_process: processEmissions.total,
            transportation: otherEmissions.by_type.transportation || 0,
            waste: otherEmissions.by_type.waste || 0,
            other: otherEmissions.by_type.other || 0
          },
          by_sector: await this.getEmissionsBySector(parkId, startTime, endTime),
          by_time: await this.getEmissionsByTime(parkId, startTime, endTime, timeRange)
        },
        intensity_indicators: {
          per_gdp: await this.calculateEmissionIntensity(totalEmissions, 'gdp', parkId, startTime, endTime),
          per_area: await this.calculateEmissionIntensity(totalEmissions, 'area', parkId, startTime, endTime),
          per_employee: await this.calculateEmissionIntensity(totalEmissions, 'employee', parkId, startTime, endTime),
          per_output: await this.calculateEmissionIntensity(totalEmissions, 'output', parkId, startTime, endTime)
        },
        benchmark_comparison: {
          industry_average: await this.getIndustryAverage(parkId, 'total_emissions'),
          similar_parks: await this.getSimilarParksComparison(parkId, totalEmissions),
          performance_rating: this.calculatePerformanceRating(totalEmissions, 'total_emissions')
        },
        trend_analysis: await this.calculateTrendAnalysis(parkId, 'total_emissions', timeRange),
        reduction_potential: await this.calculateReductionPotential(parkId, totalEmissions),
        compliance_status: this.assessComplianceStatus(totalEmissions, 'total_emissions')
      };

      // 缓存结果
      this.indicatorCache.set(`total_emissions_${parkId}_${timeRange}`, result);

      logger.info(`计算园区总碳排放完成: ${parkId}, 值: ${totalEmissions} tCO2`);
      return result;
    } catch (error) {
      logger.error('计算园区总碳排放失败:', error);
      throw error;
    }
  }

  /**
   * 生成国家标准指标报告
   * @param {string} parkId - 园区ID
   * @param {string} timeRange - 时间范围
   * @returns {Object} 国家标准指标报告
   */
  async generateNationalIndicatorReport(parkId, timeRange = '1M') {
    try {
      // 计算所有核心指标
      const unitEnergyCarbon = await this.calculateUnitEnergyCarbon(parkId, timeRange);
      const cleanEnergyRatio = await this.calculateCleanEnergyRatio(parkId, timeRange);
      const totalEmissions = await this.calculateTotalParkEmissions(parkId, timeRange);

      // 生成综合报告
      const report = {
        report_id: this.generateReportId(parkId, timeRange),
        park_id: parkId,
        report_period: {
          time_range: timeRange,
          start_time: unitEnergyCarbon.start_time,
          end_time: unitEnergyCarbon.end_time
        },
        generation_time: new Date().toISOString(),
        core_indicators: {
          unit_energy_carbon: {
            value: unitEnergyCarbon.unit_energy_carbon,
            unit: unitEnergyCarbon.unit,
            target_compliance: unitEnergyCarbon.compliance_status,
            performance_rating: unitEnergyCarbon.benchmark_comparison.performance_rating
          },
          clean_energy_ratio: {
            value: cleanEnergyRatio.clean_energy_ratio,
            unit: cleanEnergyRatio.unit,
            renewable_ratio: cleanEnergyRatio.renewable_ratio,
            target_compliance: cleanEnergyRatio.compliance_status,
            performance_rating: cleanEnergyRatio.benchmark_comparison.performance_rating
          },
          total_emissions: {
            value: totalEmissions.total_emissions,
            unit: totalEmissions.unit,
            scope1: totalEmissions.breakdown.by_scope.scope1,
            scope2: totalEmissions.breakdown.by_scope.scope2,
            target_compliance: totalEmissions.compliance_status,
            performance_rating: totalEmissions.benchmark_comparison.performance_rating
          }
        },
        comprehensive_assessment: {
          overall_score: this.calculateOverallScore(unitEnergyCarbon, cleanEnergyRatio, totalEmissions),
          compliance_level: this.assessOverallCompliance(unitEnergyCarbon, cleanEnergyRatio, totalEmissions),
          improvement_areas: this.identifyImprovementAreas(unitEnergyCarbon, cleanEnergyRatio, totalEmissions),
          achievement_highlights: this.identifyAchievements(unitEnergyCarbon, cleanEnergyRatio, totalEmissions)
        },
        detailed_analysis: {
          unit_energy_carbon: unitEnergyCarbon,
          clean_energy_ratio: cleanEnergyRatio,
          total_emissions: totalEmissions
        },
        recommendations: await this.generateRecommendations(parkId, unitEnergyCarbon, cleanEnergyRatio, totalEmissions),
        certification_info: {
          calculation_standard: '《通知》国家标准',
          verification_method: 'Automated Calculation Engine',
          data_quality_score: await this.assessDataQuality(parkId, timeRange),
          certification_level: this.determineCertificationLevel(unitEnergyCarbon, cleanEnergyRatio, totalEmissions)
        }
      };

      // 保存报告历史
      this.calculationHistory.set(report.report_id, report);

      logger.info(`生成国家标准指标报告完成: ${report.report_id}`);
      this.emit('report_generated', report);

      return report;
    } catch (error) {
      logger.error('生成国家标准指标报告失败:', error);
      throw error;
    }
  }

  /**
   * 计算所有指标
   */
  async calculateAllIndicators() {
    try {
      const parks = await this.getAllParks();
      const timeRanges = ['1d', '1w', '1M', '3M', '1Y'];

      for (const park of parks) {
        for (const timeRange of timeRanges) {
          await this.calculateUnitEnergyCarbon(park.id, timeRange);
          await this.calculateCleanEnergyRatio(park.id, timeRange);
          await this.calculateTotalParkEmissions(park.id, timeRange);
        }
      }
    } catch (error) {
      logger.error('计算所有指标失败:', error);
    }
  }

  // 辅助计算方法
  getStartTime(timeRange) {
    const now = new Date();
    const unit = timeRange.slice(-1);
    const value = parseInt(timeRange.slice(0, -1));

    switch (unit) {
      case 'd': return new Date(now.getTime() - value * MATH_CONSTANTS.TWENTY_FOUR * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.MILLISECONDS_PER_SECOND);
      case 'w': return new Date(now.getTime() - value * MATH_CONSTANTS.SEVEN * MATH_CONSTANTS.TWENTY_FOUR * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.MILLISECONDS_PER_SECOND);
      case 'M': return new Date(now.getTime() - value * MATH_CONSTANTS.THIRTY * MATH_CONSTANTS.TWENTY_FOUR * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.MILLISECONDS_PER_SECOND);
      case 'Y': return new Date(now.getTime() - value * MATH_CONSTANTS.THREE_HUNDRED_SIXTY_FIVE * MATH_CONSTANTS.TWENTY_FOUR * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.MILLISECONDS_PER_SECOND);
      default: return new Date(now.getTime() - MATH_CONSTANTS.TWENTY_FOUR * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.SIXTY * MATH_CONSTANTS.MILLISECONDS_PER_SECOND);
    }
  }

  calculateTypeRatio(value, total) {
    return total > 0 ? ((value || 0) / total * MATH_CONSTANTS.ONE_HUNDRED).toFixed(MATH_CONSTANTS.DECIMAL_PLACES) : 0;
  }

  calculateAvoidedEmissions(cleanEnergyAmount) {
    return cleanEnergyAmount * this.emissionFactors.electricity.grid;
  }

  calculateCarbonEconomicValue(avoidedEmissions) {
    const carbonPrice = 50; // 假设碳价 50元/tCO2
    return (avoidedEmissions * carbonPrice).toFixed(MATH_CONSTANTS.DECIMAL_PLACES);
  }

  calculatePerformanceRating(value, indicatorType) {
    // 简化的评级逻辑，实际应根据行业基准
    const thresholds = {
      unit_energy_carbon: { excellent: 0.5, good: 1.0, average: 1.5, poor: 2.0 },
      clean_energy_ratio: { excellent: 50, good: 30, average: 20, poor: 10 },
      total_emissions: { excellent: 1000, good: 5000, average: 10000, poor: 20000 }
    };

    const threshold = thresholds[indicatorType];
    if (!threshold) return 'unknown';

    if (indicatorType === 'unit_energy_carbon' || indicatorType === 'total_emissions') {
      if (value <= threshold.excellent) return 'excellent';
      if (value <= threshold.good) return 'good';
      if (value <= threshold.average) return 'average';
      return 'poor';
    } else {
      if (value >= threshold.excellent) return 'excellent';
      if (value >= threshold.good) return 'good';
      if (value >= threshold.average) return 'average';
      return 'poor';
    }
  }

  assessComplianceStatus(value, indicatorType) {
    const targets = {
      unit_energy_carbon: 1.0, // 示例目标值
      clean_energy_ratio: this.nationalTargets.clean_energy_ratio,
      total_emissions: 10000 // 示例目标值
    };

    const target = targets[indicatorType];
    if (!target) return 'unknown';

    if (indicatorType === 'clean_energy_ratio') {
      return value >= target ? 'compliant' : 'non_compliant';
    } else {
      return value <= target ? 'compliant' : 'non_compliant';
    }
  }

  calculateOverallScore(unitEnergyCarbon, cleanEnergyRatio, totalEmissions) {
    const scores = {
      excellent: 100,
      good: 80,
      average: 60,
      poor: 40
    };

    const score1 = scores[unitEnergyCarbon.benchmark_comparison.performance_rating] || 0;
    const score2 = scores[cleanEnergyRatio.benchmark_comparison.performance_rating] || 0;
    const score3 = scores[totalEmissions.benchmark_comparison.performance_rating] || 0;

    return Math.round((score1 + score2 + score3) / 3);
  }

  assessOverallCompliance(unitEnergyCarbon, cleanEnergyRatio, totalEmissions) {
    const compliantCount = [
      unitEnergyCarbon.compliance_status === 'compliant',
      cleanEnergyRatio.compliance_status === 'compliant',
      totalEmissions.compliance_status === 'compliant'
    ].filter(Boolean).length;

    if (compliantCount === 3) return 'fully_compliant';
    if (compliantCount >= 2) return 'mostly_compliant';
    if (compliantCount >= 1) return 'partially_compliant';
    return 'non_compliant';
  }

  generateReportId(parkId, timeRange) {
    return `NI_${parkId}_${timeRange}_${Date.now()}`;
  }

  // 模拟数据获取方法
  async calculateEnergyActivityEmissions(parkId, startTime, endTime) {
    return {
      total: 1500.5,
      scope1: 800.3,
      scope2: 700.2,
      by_type: {
        electricity: 700.2,
        natural_gas: 500.1,
        coal: 200.1,
        oil: 100.1
      }
    };
  }

  async calculateIndustrialProcessEmissions(parkId, startTime, endTime) {
    return {
      total: 300.5,
      by_type: {
        cement: 150.2,
        steel: 100.1,
        chemical: 50.2
      }
    };
  }

  async calculateOtherEmissions(parkId, startTime, endTime) {
    return {
      total: 100.2,
      by_type: {
        transportation: 60.1,
        waste: 30.1,
        other: 10.0
      }
    };
  }

  async getGDPData(parkId, startTime, endTime) {
    return {
      total: 50000, // 万元
      by_sector: {
        manufacturing: 30000,
        services: 15000,
        other: 5000
      }
    };
  }

  async getTotalEnergyConsumption(parkId, startTime, endTime) {
    return {
      total: 10000, // MWh
      by_type: {
        electricity: 6000,
        natural_gas: 2000,
        coal: 1500,
        oil: 500
      }
    };
  }

  async getCleanEnergyConsumption(parkId, startTime, endTime) {
    return {
      total: 3000,
      renewable: 2500,
      nuclear: 500,
      by_type: {
        solar: 1000,
        wind: 800,
        hydro: 500,
        biomass: 200,
        nuclear: 500
      }
    };
  }

  async getIndustryBenchmarks() {
    return [
      {
        industry_code: 'manufacturing',
        unit_energy_carbon: 1.2,
        clean_energy_ratio: 20,
        total_emissions: 8000
      }
    ];
  }

  async getAllParks() {
    return [{ id: 'park_001', name: '示例园区' }];
  }

  async getIndustryAverage(parkId, indicator) {
    const averages = {
      unit_energy_carbon: 1.5,
      clean_energy_ratio: 18,
      total_emissions: 12000
    };
    return averages[indicator] || 0;
  }

  async calculateTrendAnalysis(parkId, indicator, timeRange) {
    return {
      trend: 'improving',
      change_rate: -5.2,
      historical_data: []
    };
  }

  async getEmissionsBySector(parkId, startTime, endTime) {
    return {
      manufacturing: 1200,
      commercial: 400,
      residential: 200,
      transportation: 100
    };
  }

  async calculateEmissionIntensity(emissions, type, parkId, startTime, endTime) {
    const denominators = {
      gdp: 50000,
      area: 100,
      employee: 1000,
      output: 80000
    };

    const denominator = denominators[type] || 1;
    return (emissions / denominator).toFixed(MATH_CONSTANTS.DECIMAL_PLACES);
  }

  async getSimilarParksComparison(parkId, emissions) {
    return {
      average: 1800,
      percentile: 75,
      ranking: 'top_25%'
    };
  }

  async calculateReductionPotential(parkId, emissions) {
    return {
      technical_potential: 300,
      economic_potential: 200,
      recommended_actions: ['提高能效', '增加清洁能源', '优化工艺']
    };
  }

  async generateRecommendations(parkId, unitEnergyCarbon, cleanEnergyRatio, totalEmissions) {
    return [
      {
        category: 'energy_efficiency',
        priority: 'high',
        description: '提升设备能效，降低单位能耗',
        potential_impact: '减排200tCO2/年'
      },
      {
        category: 'clean_energy',
        priority: 'medium',
        description: '增加可再生能源装机容量',
        potential_impact: '提升清洁能源占比至30%'
      }
    ];
  }

  async assessDataQuality(parkId, timeRange) {
    return 95; // 数据质量评分
  }

  determineCertificationLevel(unitEnergyCarbon, cleanEnergyRatio, totalEmissions) {
    const ratings = [
      unitEnergyCarbon.benchmark_comparison.performance_rating,
      cleanEnergyRatio.benchmark_comparison.performance_rating,
      totalEmissions.benchmark_comparison.performance_rating
    ];

    if (ratings.every(r => r === 'excellent')) return 'platinum';
    if (ratings.filter(r => r === 'excellent' || r === 'good').length >= 2) return 'gold';
    if (ratings.filter(r => r !== 'poor').length >= 2) return 'silver';
    return 'bronze';
  }

  identifyImprovementAreas(unitEnergyCarbon, cleanEnergyRatio, totalEmissions) {
    const areas = [];

    if (unitEnergyCarbon.benchmark_comparison.performance_rating === 'poor') {
      areas.push('单位能耗碳排放过高，需要提升能源效率');
    }

    if (cleanEnergyRatio.benchmark_comparison.performance_rating === 'poor') {
      areas.push('清洁能源占比偏低，需要增加可再生能源使用');
    }

    if (totalEmissions.benchmark_comparison.performance_rating === 'poor') {
      areas.push('总碳排放量过高，需要全面减排措施');
    }

    return areas;
  }

  identifyAchievements(unitEnergyCarbon, cleanEnergyRatio, totalEmissions) {
    const achievements = [];

    if (unitEnergyCarbon.benchmark_comparison.performance_rating === 'excellent') {
      achievements.push('单位能耗碳排放达到优秀水平');
    }

    if (cleanEnergyRatio.benchmark_comparison.performance_rating === 'excellent') {
      achievements.push('清洁能源消费占比表现优异');
    }

    if (totalEmissions.benchmark_comparison.performance_rating === 'excellent') {
      achievements.push('总碳排放控制效果显著');
    }

    return achievements;
  }
}

export default NationalIndicatorEngine;

import { dbPromise } from '../database.js';
import logger from '../utils/logger.js';
import nodemailer from 'nodemailer';
import twilio from 'twilio';
import { WebhookClient } from 'discord.js';
import NotificationPreferences from '../models/NotificationPreferences.js';

/**
 * 通知服务 - 处理各类告警通知的发送
 */
class NotificationService {
  constructor() {
    this.transporters = {
      email: this.initializeEmailTransporter(),
      sms: this.initializeSmsClient(),
      discord: this.initializeDiscordWebhook()
    };
    this.preferencesCache = new Map(); // { userId => preferences }
    this.initialize();
  }

  /**
   * 初始化通知服务
   */
  async initialize() {
    try {
      // 预加载用户通知偏好设置
      await this.loadNotificationPreferences();
      logger.info('通知服务初始化完成');
    } catch (error) {
      logger.error('通知服务初始化失败:', error);
    }
  }

  /**
   * 初始化邮件传输器
   */
  initializeEmailTransporter() {
    try {
      // 从环境变量获取邮件配置
      const { SMTP_HOST, SMTP_PORT, SMTP_USER, SMTP_PASS, SMTP_FROM } = process.env;

      if (!SMTP_HOST || !SMTP_PORT || !SMTP_USER || !SMTP_PASS) {
        logger.warn('邮件服务配置不完整，邮件通知功能将不可用');
        return null;
      }

      return nodemailer.createTransport({
        host: SMTP_HOST,
        port: parseInt(SMTP_PORT),
        secure: parseInt(SMTP_PORT) === 465,
        auth: {
          user: SMTP_USER,
          pass: SMTP_PASS
        },
        from: SMTP_FROM || SMTP_USER
      });
    } catch (error) {
      logger.error('初始化邮件传输器失败:', error);
      return null;
    }
  }

  /**
   * 初始化SMS客户端
   */
  initializeSmsClient() {
    try {
      // 从环境变量获取Twilio配置
      const { TWILIO_ACCOUNT_SID, TWILIO_AUTH_TOKEN, TWILIO_PHONE_NUMBER } = process.env;

      if (!TWILIO_ACCOUNT_SID || !TWILIO_AUTH_TOKEN || !TWILIO_PHONE_NUMBER) {
        logger.warn('SMS服务配置不完整，SMS通知功能将不可用');
        return null;
      }

      return twilio(TWILIO_ACCOUNT_SID, TWILIO_AUTH_TOKEN);
    } catch (error) {
      logger.error('初始化SMS客户端失败:', error);
      return null;
    }
  }

  /**
   * 初始化Discord Webhook
   */
  initializeDiscordWebhook() {
    try {
      const { DISCORD_WEBHOOK_URL } = process.env;

      if (!DISCORD_WEBHOOK_URL) {
        logger.warn('Discord Webhook URL未配置，Discord通知功能将不可用');
        return null;
      }

      return new WebhookClient({ url: DISCORD_WEBHOOK_URL });
    } catch (error) {
      logger.error('初始化Discord Webhook失败:', error);
      return null;
    }
  }

  /**
   * 加载用户通知偏好设置
   */
  async loadNotificationPreferences() {
    try {
      const db = await dbPromise;
      const preferences = await db.raw('SELECT * FROM notification_preferences');
      preferences.forEach((pref) => {
        try {
          pref.channels = JSON.parse(pref.channels);
          pref.severity_filters = JSON.parse(pref.severity_filters);
          this.preferencesCache.set(pref.user_id, pref);
        } catch (error) {
          logger.error(`解析用户通知偏好失败 (用户ID: ${pref.user_id}):`, error);
        }
      });
      logger.info(`加载了 ${preferences.length} 条用户通知偏好设置`);
    } catch (error) {
      logger.error('加载用户通知偏好设置失败:', error);
    }
  }

  /**
   * 获取用户的通知偏好
   * @param {string} userId - 用户ID
   */
  async getNotificationPreferences(userId) {
    // 先从缓存获取
    let prefs = this.preferencesCache.get(userId);

    if (!prefs) {
      // 缓存未命中，从数据库获取
      try {
        const db = await dbPromise;
        const result = await db.raw('SELECT * FROM notification_preferences WHERE user_id = ?', [
          userId
        ]);
        const row = result.rows ? result.rows[0] : undefined;

        if (row) {
          row.channels = JSON.parse(row.channels);
          row.severity_filters = JSON.parse(row.severity_filters);
          this.preferencesCache.set(userId, row);
          prefs = row;
        } else {
          // 使用默认偏好
          prefs = NotificationPreferences.getDefaultPreferences(userId);
          await this.saveNotificationPreferences(prefs);
        }
      } catch (error) {
        logger.error(`获取用户通知偏好失败 (用户ID: ${userId}):`, error);
        prefs = NotificationPreferences.getDefaultPreferences(userId);
      }
    }

    return prefs;
  }

  /**
   * 保存用户通知偏好
   * @param {Object} preferences - 用户通知偏好对象
   */
  async saveNotificationPreferences(preferences) {
    try {
      const db = await dbPromise;
      const result = await db.raw('SELECT id FROM notification_preferences WHERE user_id = ?', [
        preferences.user_id
      ]);
      const existing = result.rows ? result.rows[0] : undefined;

      const channelsJson = JSON.stringify(preferences.channels);
      const filtersJson = JSON.stringify(preferences.severity_filters);
      const now = new Date().toISOString();

      if (existing) {
        // 更新现有偏好
        const db = await dbPromise;
        await db.run(
          `UPDATE notification_preferences SET
            channels = ?, severity_filters = ?, updated_at = ?
           WHERE user_id = ?`,
          [channelsJson, filtersJson, now, preferences.user_id]
        );
      } else {
        // 创建新偏好
        const db = await dbPromise;
        await db.run(
          `INSERT INTO notification_preferences (
            user_id, channels, severity_filters, created_at, updated_at
          ) VALUES (?, ?, ?, ?, ?)`,
          [preferences.user_id, channelsJson, filtersJson, now, now]
        );
      }

      // 更新缓存
      this.preferencesCache.set(preferences.user_id, preferences);
      return true;
    } catch (error) {
      logger.error(`保存用户通知偏好失败 (用户ID: ${preferences.user_id}):`, error);
      return false;
    }
  }

  /**
   * 发送告警通知
   * @param {Object} alert - 告警对象
   * @param {Array} users - 需要通知的用户列表
   */
  async sendAlertNotifications(alert, users) {
    if (!alert || !users || users.length === 0) {
      logger.warn('告警信息或用户列表为空，无法发送通知');
      return;
    }

    try {
      // 记录告警到数据库
      const alertId = await this.recordAlert(alert);

      // 对每个用户处理通知
      for (const user of users) {
        try {
          const prefs = await this.getNotificationPreferences(user.id);

          // 检查告警级别是否符合用户偏好
          if (!prefs.severity_filters.includes(alert.severity)) {
            logger.debug(`用户 ${user.id} 已过滤 ${alert.severity} 级别的告警通知`);
            continue;
          }

          // 根据用户偏好的渠道发送通知
          for (const channel of prefs.channels) {
            switch (channel) {
              case 'email':
                await this.sendEmailNotification(user, alert);
                break;
              case 'sms':
                await this.sendSmsNotification(user, alert);
                break;
              case 'discord':
                await this.sendDiscordNotification(user, alert);
                break;
              default:
                logger.warn(`未知的通知渠道: ${channel}`);
            }
          }

          // 记录用户通知状态
          await this.recordUserAlert(user.id, alertId);
        } catch (error) {
          logger.error(`处理用户 ${user.id} 的告警通知失败:`, error);
          continue;
        }
      }
    } catch (error) {
      logger.error('发送告警通知失败:', error);
    }
  }

  /**
   * 记录告警到数据库
   * @param {Object} alert - 告警对象
   * @returns {number} - 告警ID
   */
  async recordAlert(alert) {
    const now = new Date().toISOString();
    const db = await dbPromise;
    const result = await db.run(
      `INSERT INTO alerts (
        device_id, type, severity, message, status, created_at, updated_at
      ) VALUES (?, ?, ?, ?, ?, ?, ?)`,
      [alert.deviceId, alert.type, alert.severity, alert.message, 'new', now, now]
    );
    return result.lastID;
  }

  /**
   * 记录用户告警通知状态
   * @param {string} userId - 用户ID
   * @param {number} alertId - 告警ID
   */
  async recordUserAlert(userId, alertId) {
    const now = new Date().toISOString();
    const db = await dbPromise;
    await db.run(
      `INSERT INTO user_alerts (user_id, alert_id, status, created_at)
       VALUES (?, ?, ?, ?)`,
      [userId, alertId, 'pending', now]
    );
  }

  /**
   * 发送邮件通知
   * @param {Object} user - 用户对象
   * @param {Object} alert - 告警对象
   */
  async sendEmailNotification(user, alert) {
    if (!this.transporters.email || !user.email) {
      logger.warn('邮件传输器未初始化或用户邮箱不存在，无法发送邮件通知');
      return;
    }

    try {
      const subject = `[${alert.severity.toUpperCase()}] 设备告警通知 - ${alert.deviceId}`;
      const html = `
        <h2>设备告警通知</h2>
        <p><strong>设备ID:</strong> ${alert.deviceId}</p>
        <p><strong>告警类型:</strong> ${alert.type}</p>
        <p><strong>严重级别:</strong> ${alert.severity}</p>
        <p><strong>告警信息:</strong> ${alert.message}</p>
        <p><strong>发生时间:</strong> ${new Date(alert.timestamp).toLocaleString()}</p>
        <p>请及时处理此告警。</p>
      `;

      await this.transporters.email.sendMail({
        to: user.email,
        subject,
        html
      });

      logger.info(`已向用户 ${user.id} 发送邮件告警通知`);
      await this.updateUserAlertStatus(user.id, alert.id, 'sent');
    } catch (error) {
      logger.error(`发送邮件通知失败 (用户ID: ${user.id}):`, error);
      await this.updateUserAlertStatus(user.id, alert.id, 'failed');
    }
  }

  /**
   * 发送SMS通知
   * @param {Object} user - 用户对象
   * @param {Object} alert - 告警对象
   */
  async sendSmsNotification(user, alert) {
    if (!this.transporters.sms || !user.phone) {
      logger.warn('SMS客户端未初始化或用户手机号不存在，无法发送SMS通知');
      return;
    }

    try {
      const message = `[${alert.severity.toUpperCase()}] 设备${alert.deviceId}告警: ${alert.message} 时间:${new Date(alert.timestamp).toLocaleString()}`;

      await this.transporters.sms.messages.create({
        body: message,
        from: process.env.TWILIO_PHONE_NUMBER,
        to: user.phone
      });

      logger.info(`已向用户 ${user.id} 发送SMS告警通知`);
      await this.updateUserAlertStatus(user.id, alert.id, 'sent');
    } catch (error) {
      logger.error(`发送SMS通知失败 (用户ID: ${user.id}):`, error);
      await this.updateUserAlertStatus(user.id, alert.id, 'failed');
    }
  }

  /**
   * 发送Discord通知
   * @param {Object} alert - 告警对象
   */
  async sendDiscordNotification(user, alert) {
    if (!this.transporters.discord) {
      logger.warn('Discord Webhook未初始化，无法发送Discord通知');
      return;
    }

    try {
      const embed = {
        title: `设备告警通知 [${alert.severity.toUpperCase()}]`,
        color: this.getSeverityColor(alert.severity),
        fields: [
          { name: '设备ID', value: alert.deviceId, inline: true },
          { name: '告警类型', value: alert.type, inline: true },
          { name: '严重级别', value: alert.severity, inline: true },
          { name: '告警信息', value: alert.message },
          { name: '发生时间', value: new Date(alert.timestamp).toLocaleString() }
        ],
        timestamp: new Date().toISOString()
      };

      await this.transporters.discord.send({ embeds: [embed] });
      logger.info('已发送Discord告警通知');
    } catch (error) {
      logger.error('发送Discord通知失败:', error);
    }
  }

  /**
   * 根据告警级别获取颜色代码
   * @param {string} severity - 告警级别
   * @returns {number} - 颜色代码
   */
  getSeverityColor(severity) {
    const colors = {
      critical: 0xff0000,
      high: 0xffa500,
      medium: 0xffff00,
      low: 0x00ff00
    };
    return colors[severity] || 0x808080;
  }

  /**
   * 更新用户告警状态
   * @param {string} userId - 用户ID
   * @param {number} alertId - 告警ID
   * @param {string} status - 状态
   */
  async updateUserAlertStatus(userId, alertId, status) {
    const db = await dbPromise;
    await db.run(
      `UPDATE user_alerts SET status = ?, updated_at = ?
       WHERE user_id = ? AND alert_id = ?`,
      [status, new Date().toISOString(), userId, alertId]
    );
  }

  /**
   * 发送通知
   * @param {Object} notification - 通知对象
   * @param {string} notification.type - 通知类型 (system, alert, warning, info)
   * @param {string|string[]} notification.recipients - 接收者ID或'admin'表示管理员
   * @param {string} notification.title - 通知标题
   * @param {string} notification.message - 通知内容
   * @param {string} [notification.alert_id] - 关联的告警ID
   * @param {string} [notification.severity] - 告警级别 (critical, high, medium, low)
   */
  async sendNotification(notification) {
    try {
      const { type, recipients, title, message, alert_id, severity = 'medium' } = notification;

      // 处理接收者
      const userIds = await this.resolveRecipients(recipients);
      if (!userIds.length) {
        logger.warn('未找到有效的通知接收者');
        return false;
      }

      // 为每个接收者发送通知
      const results = [];
      for (const userId of userIds) {
        const prefs = await this.getNotificationPreferences(userId);

        // 检查该级别是否在用户的过滤范围内
        if (!prefs.severity_filters.includes(severity) && !prefs.severity_filters.includes('all')) {
          logger.debug(`用户 ${userId} 的通知偏好过滤了 ${severity} 级别的通知`);
          continue;
        }

        // 为每个启用的渠道发送通知
        for (const [channel, config] of Object.entries(prefs.channels)) {
          if (config.enabled) {
            const result = await this.sendViaChannel({
              channel,
              userId,
              contact: config.contact,
              title,
              message,
              alertId: alert_id,
              severity,
              type
            });
            results.push(result);
          }
        }
      }

      return results.every((r) => r.success);
    } catch (error) {
      logger.error('发送通知失败:', error);
      return false;
    }
  }

  /**
   * 解析接收者列表
   * @param {string|string[]} recipients - 接收者ID或'admin'表示管理员
   */
  async resolveRecipients(recipients) {
    try {
      if (!recipients) {
        return [];
      }

      // 标准化为数组
      const recipientsArray = Array.isArray(recipients) ? recipients : [recipients];
      const userIds = new Set();

      for (const recipient of recipientsArray) {
        if (recipient === 'admin') {
          // 获取所有管理员
          const db = await dbPromise;
          const admins = await db.raw('SELECT id FROM users WHERE role = "admin"');
          admins.forEach((admin) => userIds.add(admin.id));
        } else if (recipient.includes('@')) {
          // 直接指定邮箱
          // 这里可以添加通过邮箱查找用户的逻辑
          logger.warn('直接邮箱指定功能尚未实现');
        } else {
          // 用户ID
          userIds.add(recipient);
        }
      }

      return Array.from(userIds);
    } catch (error) {
      logger.error('解析通知接收者失败:', error);
      return [];
    }
  }

  /**
   * 通过指定渠道发送通知
   * @param {Object} params - 发送参数
   */
  async sendViaChannel(params) {
    const { channel, userId, contact, title, message, alertId, severity } = params;
    const result = { channel, userId, success: false, error: null };

    try {
      switch (channel) {
        case 'email':
          await this.sendEmail(contact, title, message);
          break;
        case 'sms':
          await this.sendSms(contact, message);
          break;
        case 'discord':
          await this.sendDiscord(title, message, severity);
          break;
        case 'in_app':
          await this.saveInAppNotification(userId, title, message, alertId, severity);
          break;
        default:
          throw new Error(`不支持的通知渠道: ${channel}`);
      }

      result.success = true;
      logger.info(`通知已发送 (渠道: ${channel}, 用户: ${userId})`);
    } catch (error) {
      result.error = error.message;
      logger.error(`通知发送失败 (渠道: ${channel}, 用户: ${userId}):`, error);
    }

    // 记录通知发送状态
    await this.logNotificationAttempt(params, result);
    return result;
  }

  /**
   * 发送邮件通知
   * @param {string} to - 收件人邮箱
   * @param {string} subject - 邮件主题
   * @param {string} body - 邮件内容
   */
  async sendEmail(to, subject, body) {
    if (!this.transporters.email) {
      throw new Error('邮件传输器未初始化');
    }

    // 构建HTML邮件内容
    const htmlBody = `
      <div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto; padding: 20px;">
        <h2 style="color: #2c3e50;">${subject}</h2>
        <div style="background-color: #f8f9fa; padding: 20px; border-radius: 5px;">
          ${body}
        </div>
        <p style="color: #7f8c8d; margin-top: 20px;">这是系统自动发送的通知邮件，请勿直接回复。</p>
      </div>
    `;

    // 发送邮件
    return await this.transporters.email.sendMail({
      from: process.env.EMAIL_FROM,
      to,
      subject,
      html: htmlBody
    });
  }
}

export default NotificationService;

/**
 * 实时数据质量监控与预警服务
 * 实现数据质量的实时监控、异常检测、自动预警和质量评分
 * 为零碳园区数字孪生系统提供全面的数据质量保障
 */

import { EventEmitter } from 'events';
import logger from '../../shared/utils/logger.js';
import { MATH_CONSTANTS } from '../../shared/constants/MathConstants.js';

class RealTimeDataQualityService extends EventEmitter {
    constructor() {
        super();

        // 数据质量规则
        this.qualityRules = new Map();

        // 监控任务
        this.monitoringTasks = new Map();

        // 质量指标
        this.qualityMetrics = new Map();

        // 异常检测器
        this.anomalyDetectors = new Map();

        // 预警配置
        this.alertConfigs = new Map();

        // 质量报告
        this.qualityReports = new Map();

        // 实时质量状态
        this.realTimeStatus = new Map();

        // 质量趋势数据
        this.qualityTrends = new Map();

        // 修复建议
        this.repairSuggestions = new Map();

        this.startTime = Date.now();
        this.init();
    }

    /**
     * 初始化数据质量监控服务
     */
    async init() {
        try {
            logger.info('📊 实时数据质量监控服务启动中...');

            // 初始化质量规则
            await this.initializeQualityRules();

            // 初始化异常检测器
            await this.initializeAnomalyDetectors();

            // 初始化预警配置
            await this.initializeAlertConfigs();

            // 启动实时监控
            await this.startRealTimeMonitoring();

            // 初始化质量指标
            await this.initializeQualityMetrics();

            logger.info('✅ 实时数据质量监控服务启动完成');
            this.emit('quality:ready');
        } catch (error) {
            return false;
        }
    }

    async getDetectionData(detector) {
        // 模拟获取异常检测数据
        const baseData = await this.getTestData('ems_energy_data');
        return baseData.map(record => ({
            ...record,
            timestamp: record.measurement_time,
            value: record.consumption_amount
        }));
    }

    detectZScoreAnomalies(data, parameters) {
        const anomalies = [];
        if (data.length < parameters.min_samples) return anomalies;

        const values = data.map(d => d.value || 0);
        const mean = values.reduce((a, b) => a + b, 0) / values.length;
        const variance = values.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / values.length;
        const stdDev = Math.sqrt(variance);

        data.forEach((record, index) => {
            const zScore = Math.abs((record.value - mean) / stdDev);
            if (zScore > parameters.threshold) {
                anomalies.push({
                    type: 'statistical_outlier',
                    record_id: record.meter_id || index,
                    value: record.value,
                    z_score: zScore,
                    threshold: parameters.threshold,
                    severity: zScore > parameters.threshold * 1.5 ? 'high' : 'medium',
                    description: `统计异常值检测: Z-Score ${zScore.toFixed(2)} 超过阈值 ${parameters.threshold}`
                });
            }
        });

        return anomalies;
    }

    detectTrendAnomalies(data, parameters) {
        const anomalies = [];
        if (data.length < parameters.window_size) return anomalies;

        for (let i = parameters.window_size; i < data.length; i++) {
            const window = data.slice(i - parameters.window_size, i);
            const windowMean = window.reduce((sum, d) => sum + d.value, 0) / window.length;
            const currentValue = data[i].value;

            const deviation = Math.abs(currentValue - windowMean) / windowMean;
            if (deviation > parameters.deviation_threshold) {
                anomalies.push({
                    type: 'trend_anomaly',
                    record_id: data[i].meter_id || i,
                    value: currentValue,
                    expected_value: windowMean,
                    deviation: deviation,
                    threshold: parameters.deviation_threshold,
                    severity: deviation > parameters.deviation_threshold * 2 ? 'high' : 'medium',
                    description: `趋势异常检测: 偏差 ${(deviation * 100).toFixed(2)}% 超过阈值 ${(parameters.deviation_threshold * 100).toFixed(2)}%`
                });
            }
        }

        return anomalies;
    }

    detectPatternAnomalies(data, parameters) {
        const anomalies = [];
        // 简化的模式异常检测
        const values = data.map(d => d.value || 0);
        const mean = values.reduce((a, b) => a + b, 0) / values.length;
        const threshold = mean * 2; // 简化阈值

        data.forEach((record, index) => {
            if (record.value > threshold) {
                anomalies.push({
                    type: 'pattern_anomaly',
                    record_id: record.meter_id || index,
                    value: record.value,
                    threshold: threshold,
                    severity: 'medium',
                    description: `模式异常检测: 值 ${record.value} 超过模式阈值 ${threshold.toFixed(2)}`
                });
            }
        });

        return anomalies;
    }

    detectRuleViolations(data, parameters) {
        const anomalies = [];

        data.forEach((record, index) => {
            // 检查业务规则违反
            if (record.value <= 0) {
                anomalies.push({
                    type: 'rule_violation',
                    record_id: record.meter_id || index,
                    value: record.value,
                    rule: 'value_must_be_positive',
                    severity: 'high',
                    description: `业务规则违反: 值必须大于0，当前值为 ${record.value}`
                });
            }

            // 检查时间规则
            const recordTime = new Date(record.timestamp).getTime();
            const currentTime = Date.now();
            if (recordTime > currentTime + 300000) { // 5分钟未来时间容错
                anomalies.push({
                    type: 'rule_violation',
                    record_id: record.meter_id || index,
                    timestamp: record.timestamp,
                    rule: 'timestamp_cannot_be_future',
                    severity: 'medium',
                    description: `业务规则违反: 时间戳不能是未来时间`
                });
            }
        });

        return anomalies;
    }

    async evaluateAlertConditions(alertConfig) {
        const conditions = alertConfig.trigger_conditions;

        // 检查质量分数条件
        if (conditions.quality_score) {
            const latestCheck = this.realTimeStatus.get('latest_check');
            if (latestCheck && latestCheck.quality_scores) {
                for (const category of conditions.category || []) {
                    const categoryScore = latestCheck.quality_scores[category];
                    if (categoryScore && this.evaluateCondition(categoryScore.final_score, conditions.quality_score)) {
                        return true;
                    }
                }
            }
        }

        return false;
    }

    evaluateCondition(value, condition) {
        switch (condition.operator) {
            case '<':
                return value < condition.value;
            case '<=':
                return value <= condition.value;
            case '>':
                return value > condition.value;
            case '>=':
                return value >= condition.value;
            case '==':
                return value === condition.value;
            case '!=':
                return value !== condition.value;
            default:
                return false;
        }
    }

    async triggerAlert(alertConfig) {
        const alert = {
            id: `alert_${Date.now()}`,
            config_id: alertConfig.id,
            name: alertConfig.name,
            severity: alertConfig.severity,
            triggered_at: new Date().toISOString(),
            status: 'active',
            description: alertConfig.description
        };

        logger.warn(`🚨 触发预警: ${alert.name} (${alert.severity})`);

        // 更新触发统计
        alertConfig.trigger_count++;
        alertConfig.last_triggered = alert.triggered_at;

        this.emit('alert:triggered', alert);

        return alert;
    }

    calculateOverallQualityScore(qualityScores) {
        const categoryWeights = {
            energy: 0.25,
            carbon: 0.30,
            production: 0.20,
            indicators: 0.25
        };

        let totalWeight = 0;
        let weightedSum = 0;

        for (const [category, scoreData] of Object.entries(qualityScores)) {
            const weight = categoryWeights[category] || 0;
            if (weight > 0 && scoreData.final_score !== undefined) {
                totalWeight += weight;
                weightedSum += scoreData.final_score * weight;
            }
        }

        return totalWeight > 0 ? weightedSum / totalWeight : 0;
    }

    calculateAnomalyDetectionRate() {
        // 模拟异常检测率计算
        const totalDetections = Array.from(this.anomalyDetectors.values())
            .reduce((sum, detector) => sum + detector.detection_count, 0);
        const totalRecords = 10000; // 模拟总记录数
        return totalRecords > 0 ? totalDetections / totalRecords : 0;
    }

    calculateDataFreshnessScore() {
        // 模拟数据时效性评分计算
        return 0.95;
    }

    /**
     * 生成质量报告
     */
    async generateQualityReport() {
        const report = {
            id: `quality_report_${Date.now()}`,
            generated_at: new Date().toISOString(),
            period: {
                start: new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString(),
                end: new Date().toISOString()
            },
            summary: {
                overall_quality_score: this.qualityMetrics.get('overall_quality_score')?.current_value || 0,
                total_rules_executed: Array.from(this.qualityRules.values())
                    .reduce((sum, rule) => sum + rule.execution_count, 0),
                total_anomalies_detected: Array.from(this.anomalyDetectors.values())
                    .reduce((sum, detector) => sum + detector.detection_count, 0),
                alerts_triggered: Array.from(this.alertConfigs.values())
                    .reduce((sum, config) => sum + config.trigger_count, 0)
            },
            quality_metrics: Object.fromEntries(
                Array.from(this.qualityMetrics.entries()).map(([id, metric]) => [
                    id,
                    {
                        current_value: metric.current_value,
                        target_value: metric.target_value,
                        trend: metric.trend,
                        achievement_rate: metric.target_value > 0 ? 
                            (metric.current_value / metric.target_value) : 0
                    }
                ])
            ),
            recommendations: this.generateQualityRecommendations()
        };

        this.qualityReports.set(report.id, report);
        logger.info(`📊 质量报告已生成: ${report.id}`);

        return report;
    }

    generateQualityRecommendations() {
        const recommendations = [];

        // 基于质量指标生成建议
        for (const [metricId, metric] of this.qualityMetrics) {
            if (metric.current_value < metric.target_value * 0.9) {
                recommendations.push({
                    type: 'quality_improvement',
                    metric: metricId,
                    priority: 'high',
                    description: `${metric.name}低于目标值90%，建议加强数据质量管控`,
                    suggested_actions: [
                        '增加数据验证规则',
                        '提高数据采集频率',
                        '加强数据源监控'
                    ]
                });
            }
        }

        return recommendations;
    }

    /**
     * 获取服务状态
     */
    getServiceStatus() {
        return {
            service_name: 'RealTimeDataQualityService',
            status: 'running',
            uptime: Date.now() - this.startTime,
            statistics: {
                quality_rules: this.qualityRules.size,
                anomaly_detectors: this.anomalyDetectors.size,
                alert_configs: this.alertConfigs.size,
                quality_metrics: this.qualityMetrics.size,
                monitoring_tasks: this.monitoringTasks.size
            },
            latest_metrics: Object.fromEntries(
                Array.from(this.qualityMetrics.entries()).map(([id, metric]) => [
                    id,
                    {
                        value: metric.current_value,
                        trend: metric.trend,
                        last_updated: metric.last_updated
                    }
                ])
            )
        };
    }

    /**
     * 停止服务
     */
    async stop() {
        logger.info('🛑 停止实时数据质量监控服务...');

        // 清理监控任务
        for (const [taskName, task] of this.monitoringTasks) {
            clearInterval(task);
            logger.info(`✅ 已停止监控任务: ${taskName}`);
        }

        this.monitoringTasks.clear();
        this.emit('quality:stopped');

        logger.info('✅ 实时数据质量监控服务已停止');
    }
}

