import { createRequire } from 'module';
import { v4 as uuidv4 } from 'uuid';
import { dbPromise } from '../database.js';
import { alertManager as _alertManager } from '../alerts/AlertManager.js';

const require = createRequire(import.meta.url);
const mqtt = require('mqtt');

/**
 * 数据采集器类
 * 负责MQTT数据接收、验证、处理和存储
 */
class DataCollector {

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 22 行)

  constructor(mqttConfig = {}) {
    this.mqttClient = null;
    this.config = {
      host: mqttConfig.host || process.env.MQTT_HOST || 'localhost',
      port: mqttConfig.port || process.env.MQTT_PORT || 1883,
      username: mqttConfig.username || process.env.MQTT_USERNAME,
      password: mqttConfig.password || process.env.MQTT_PASSWORD,
      ...mqttConfig
    };

    this.topics = {
      energy: 'energy/realtime/+',
      carbon: 'carbon/realtime/+',
      device_status: 'device/status/+',
      sensor_data: 'sensor/data/+'
    };

    this.dataBuffer = new Map(); // 数据缓冲区
    this.isConnected = false;
    this.lastMessageTime = null;
    this.connectionTime = null;
  }

  /**
   * 初始化MQTT连接
   */
  async initialize() {
    try {
      const mqttUrl = `mqtt://${this.config.host}:${this.config.port}`;

      this.mqttClient = mqtt.connect(mqttUrl, {
        username: this.config.username,
        password: this.config.password,
        keepalive: 60,
        reconnectPeriod: 5000,
        connectTimeout: 30000
      });

      this.mqttClient.on('connect', () => {
        console.log('✅ MQTT数据采集器已连接');
        this.isConnected = true;
        this.connectionTime = new Date().toISOString();
        this.subscribeToTopics();
      });

      this.mqttClient.on('message', (topic, message) => {
        this.lastMessageTime = new Date().toISOString();
        this.handleMessage(topic, message);
      });

      this.mqttClient.on('error', error => {
        console.error('❌ MQTT连接错误:', error.message);
        this.isConnected = false;
      });

      this.mqttClient.on('close', () => {
        console.log('🔌 MQTT连接已断开');
        this.isConnected = false;
      });

      this.mqttClient.on('reconnect', () => {
        console.log('🔄 正在重新连接MQTT...');
      });
    } catch (error) {
      console.error('MQTT初始化失败:', error);
      throw error;
    }
  }

  /**
   * 订阅所有主题
   */
  subscribeToTopics() {
    Object.entries(this.topics).forEach(([_key, topic]) => {
      this.mqttClient.subscribe(topic, err => {
        if (err) {
          console.error(`❌ 订阅主题失败 ${topic}:`, err.message);
        } else {
          console.log(`📡 已订阅主题: ${topic}`);
        }
      });
    });
  }

  /**
   * 处理接收到的MQTT消息
   */
  async handleMessage(topic, message) {
    try {
      const payload = JSON.parse(message.toString());
      const topicParts = topic.split('/');
      const [dataType, , deviceId] = topicParts;

      // 验证数据格式
      if (!this.validatePayload(payload, dataType)) {
        console.error(`❌ 数据格式验证失败 - 主题: ${topic}`);
        return;
      }

      // 根据数据类型处理
      switch (dataType) {
        case 'energy':
          await this.handleEnergyData(deviceId, payload);
          break;
        case 'sensor':
          await this.handleSensorData(deviceId, payload);
          break;
        case 'device':
          await this.handleDeviceStatus(deviceId, payload);
          break;
        default:
          console.warn(`⚠️ 未知数据类型: ${dataType}`);
      }
    } catch (error) {
      console.error(`消息处理失败 - 主题: ${topic}`, error);
    }
  }

  /**
   * 验证数据载荷格式
   */
  validatePayload(payload, dataType) {
    const requiredFields = {
      energy: ['timestamp', 'value', 'unit', 'type'],
      sensor: ['timestamp', 'value', 'unit', 'sensor_id'],
      device: ['timestamp', 'status']
    };

    const required = requiredFields[dataType];
    if (!required) {return false;}

    // 检查必填字段
    if (!required.every(field => Object.prototype.hasOwnProperty.call(payload, field))) {
      return false;
    }

    // 数据类型和范围验证
    switch (dataType) {
      case 'energy':
        return this.validateEnergyData(payload);
      case 'sensor':
        return this.validateSensorData(payload);
      case 'device':
        return this.validateDeviceData(payload);
      default:
        return false;
    }
  }

  /**
   * 验证能源数据
   */
  validateEnergyData(payload) {
    // 验证时间戳
    const timestamp = new Date(payload.timestamp);
    if (isNaN(timestamp.getTime())) {
      console.error('无效的时间戳格式');
      return false;
    }

    // 验证时间戳不能是未来时间
    if (timestamp > new Date()) {
      console.error('时间戳不能是未来时间');
      return false;
    }

    // 验证时间戳不能太旧（超过24小时）
    const dayAgo = new Date(Date.now() - 24 * 60 * 60 * 1000);
    if (timestamp < dayAgo) {
      console.warn('数据时间戳过旧，可能是历史数据');
    }

    // 验证数值
    if (typeof payload.value !== 'number' || isNaN(payload.value)) {
      console.error('能源数据值必须是有效数字');
      return false;
    }

    // 验证数值范围（不能为负数，除非是特殊类型如净能耗）
    if (payload.value < 0 && !['net_energy', 'power_factor'].includes(payload.type)) {
      console.error('能源数据值不能为负数');
      return false;
    }

    // 验证数值上限（防止异常大的数值）
    const maxValues = {
      electricity: 10000, // kWh
      water: 1000, // m³
      gas: 1000, // m³
      power: 5000, // kW
      temperature: 100, // °C
      humidity: 100 // %
    };

    const maxValue = maxValues[payload.type] || 100000;
    if (payload.value > maxValue) {
      console.error(`能源数据值超出合理范围: ${payload.value} > ${maxValue}`);
      return false;
    }

    // 验证单位
    const validUnits = {
      electricity: ['kWh', 'MWh', 'kW', 'MW'],
      water: ['m³', 'L', 'gal'],
      gas: ['m³', 'L', 'ft³'],
      power: ['kW', 'MW', 'W'],
      temperature: ['°C', '°F', 'K'],
      humidity: ['%']
    };

    const allowedUnits = validUnits[payload.type] || [];
    if (allowedUnits.length > 0 && !allowedUnits.includes(payload.unit)) {
      console.error(`无效的单位: ${payload.unit} for type ${payload.type}`);
      return false;
    }

    return true;
  }

  /**
   * 验证传感器数据
   */
  validateSensorData(payload) {
    // 验证时间戳
    const timestamp = new Date(payload.timestamp);
    if (isNaN(timestamp.getTime()) || timestamp > new Date()) {
      return false;
    }

    // 验证传感器ID格式
    if (typeof payload.sensor_id !== 'string' || payload.sensor_id.length === 0) {
      console.error('无效的传感器ID');
      return false;
    }

    // 验证数值
    if (typeof payload.value !== 'number' || isNaN(payload.value)) {
      console.error('传感器数据值必须是有效数字');
      return false;
    }

    return true;
  }

  /**
   * 验证设备数据
   */
  validateDeviceData(payload) {
    // 验证时间戳
    const timestamp = new Date(payload.timestamp);
    if (isNaN(timestamp.getTime()) || timestamp > new Date()) {
      return false;
    }

    // 验证状态值
    const validStatuses = [0, 1, 2, 3]; // 0:离线, 1:在线, 2:故障, 3:维护
    if (!validStatuses.includes(payload.status)) {
      console.error(`无效的设备状态: ${payload.status}`);
      return false;
    }

    return true;
  }

  /**
   * 处理能源数据
   */
  async handleEnergyData(deviceId, payload) {
    try {
      const energyDataId = uuidv4();
      const timestamp = new Date(payload.timestamp);

      // 验证设备是否存在
      const device = await this.getDevice(deviceId);
      if (!device) {
        console.warn(`⚠️ 设备不存在: ${deviceId}`);
        return;
      }

      // 数据清洗和异常检测
      const cleanedData = await this.cleanEnergyData(deviceId, payload);
      if (!cleanedData) {
        console.warn(`⚠️ 数据清洗失败，跳过数据: ${deviceId}`);
        return;
      }

      // 异常检测
      const anomalyResult = await this.detectAnomaly(deviceId, cleanedData);
      if (anomalyResult.isAnomaly) {
        console.warn(`🚨 检测到异常数据: ${deviceId}, 原因: ${anomalyResult.reason}`);
        // 可以选择是否存储异常数据，这里我们标记但仍然存储
        cleanedData.is_anomaly = true;
        cleanedData.anomaly_reason = anomalyResult.reason;
      }

      // 存储能源数据
      await this.saveEnergyData({
        id: energyDataId,
        device_id: deviceId,
        sensor_id: payload.sensor_id || null,
        data_type: this.getEnergyDataType(payload.type),
        value: cleanedData.value,
        unit: cleanedData.unit,
        timestamp: timestamp.toISOString(),
        created_at: new Date().toISOString(),
        is_anomaly: cleanedData.is_anomaly || false,
        anomaly_reason: cleanedData.anomaly_reason || null
      });

      // 计算并存储碳排放数据
      await this.calculateAndStoreCarbonEmission(energyDataId, payload.type, cleanedData.value);

      // 检查是否需要触发告警
      await this.checkAlertRules(deviceId, payload.sensor_id, cleanedData);

      console.log(
        `✅ 能源数据已保存 - 设备: ${deviceId}, 值: ${cleanedData.value}${cleanedData.unit}`
      );
    } catch (error) {
      console.error('能源数据处理失败:', error);
    }
  }

  /**
   * 数据清洗
   */
  async cleanEnergyData(deviceId, payload) {
    try {
      let cleanedValue = payload.value;

      // 1. 去除明显的错误值（如负数电量）
      if (payload.type === 'electricity' && cleanedValue < 0) {
        console.warn(`修正负数电量值: ${cleanedValue} -> 0`);
        cleanedValue = 0;
      }

      // 2. 单位标准化
      const standardizedData = this.standardizeUnit(cleanedValue, payload.unit, payload.type);
      cleanedValue = standardizedData.value;
      const standardUnit = standardizedData.unit;

      // 3. 数值平滑（移动平均）
      const smoothedValue = await this.applySmoothingFilter(deviceId, payload.type, cleanedValue);

      return {
        value: smoothedValue,
        unit: standardUnit,
        type: payload.type
      };
    } catch (error) {
      console.error('数据清洗失败:', error);
      return null;
    }
  }

  /**
   * 单位标准化
   */
  standardizeUnit(value, unit, type) {
    const conversions = {
      electricity: {
        MWh: { factor: 1000, standardUnit: 'kWh' },
        Wh: { factor: 0.001, standardUnit: 'kWh' },
        MW: { factor: 1000, standardUnit: 'kW' },
        W: { factor: 0.001, standardUnit: 'kW' }
      },
      water: {
        L: { factor: 0.001, standardUnit: 'm³' },
        gal: { factor: 0.00378541, standardUnit: 'm³' }
      },
      temperature: {
        '°F': { factor: f => ((f - 32) * 5) / 9, standardUnit: '°C' },
        K: { factor: k => k - 273.15, standardUnit: '°C' }
      }
    };

    const typeConversions = conversions[type];
    if (!typeConversions || !typeConversions[unit]) {
      return { value, unit }; // 无需转换
    }

    const conversion = typeConversions[unit];
    const convertedValue =
      typeof conversion.factor === 'function' ?
        conversion.factor(value) :
        value * conversion.factor;

    return {
      value: Math.round(convertedValue * 1000) / 1000, // 保留3位小数
      unit: conversion.standardUnit
    };
  }

  /**
   * 应用平滑滤波器
   */
  async applySmoothingFilter(deviceId, dataType, value) {
    try {
      // 获取最近的几个数据点
      const recentData = await this.getRecentEnergyData(deviceId, dataType, 5);

      if (recentData.length < 2) {
        return value; // 数据不足，不进行平滑
      }

      // 计算移动平均
      const values = [...recentData.map(d => d.value), value];
      const windowSize = Math.min(3, values.length);
      const recentValues = values.slice(-windowSize);

      const average = recentValues.reduce((sum, v) => sum + v, 0) / recentValues.length;

      // 如果当前值与平均值差异过大，使用加权平均
      const deviation = Math.abs(value - average) / average;
      if (deviation > 0.5) {
        // 偏差超过50%
        return Math.round((value * 0.3 + average * 0.7) * 1000) / 1000;
      }

      return value;
    } catch (error) {
      console.error('平滑滤波失败:', error);
      return value;
    }
  }

  /**
   * 异常检测
   */
  async detectAnomaly(deviceId, data) {
    try {
      // 获取历史数据进行统计分析
      const historicalData = await this.getRecentEnergyData(deviceId, data.type, 50);

      if (historicalData.length < 10) {
        return { isAnomaly: false }; // 历史数据不足
      }

      const values = historicalData.map(d => d.value);
      const mean = values.reduce((sum, v) => sum + v, 0) / values.length;
      const variance = values.reduce((sum, v) => sum + Math.pow(v - mean, 2), 0) / values.length;
      const stdDev = Math.sqrt(variance);

      // Z-score异常检测
      const zScore = Math.abs(data.value - mean) / stdDev;
      if (zScore > 3) {
        // 3-sigma规则
        return {
          isAnomaly: true,
          reason: `Z-score异常: ${zScore.toFixed(2)} (阈值: 3.0)`
        };
      }

      // 检查是否为突然的大幅变化
      const lastValue = values[values.length - 1];
      const changeRate = Math.abs(data.value - lastValue) / lastValue;
      if (changeRate > 2.0) {
        // 变化超过200%
        return {
          isAnomaly: true,
          reason: `突然变化: ${(changeRate * 100).toFixed(1)}% (阈值: 200%)`
        };
      }

      return { isAnomaly: false };
    } catch (error) {
      console.error('异常检测失败:', error);
      return { isAnomaly: false };
    }
  }

  /**
   * 获取最近的能源数据
   */
  getRecentEnergyData(deviceId, dataType, limit = 10) {
    return new Promise((resolve, reject) => {
      const sql = `
        SELECT value, timestamp 
        FROM energy_data 
        WHERE device_id = ? AND data_type = ?
        ORDER BY timestamp DESC 
        LIMIT ?
      `;

      (async () => {
        try {
          const db = await dbPromise;
          const result = await db.raw(sql, [deviceId, this.getEnergyDataType(dataType), limit]);
          resolve(result.rows || []);
        } catch (err) {
          reject(err);
        }
      })();
    });
  }

  /**
   * 检查告警规则
   */
  async checkAlertRules(deviceId, sensorId, data) {
    try {
      // 获取相关的告警规则
      const rules = await this.getActiveAlertRules(deviceId, sensorId);

      for (const rule of rules) {
        const shouldAlert = this.evaluateAlertCondition(rule, data.value);

        if (shouldAlert) {
          await this.createAlert(rule, deviceId, sensorId, data.value);
        }
      }
    } catch (error) {
      console.error('告警规则检查失败:', error);
    }
  }

  /**
   * 获取活跃的告警规则
   */
  getActiveAlertRules(deviceId, sensorId) {
    return new Promise((resolve, reject) => {
      let sql = `
        SELECT * FROM alert_rules 
        WHERE is_active = 1 AND (
          device_id = ? OR device_id IS NULL
        )
      `;
      const params = [deviceId];

      if (sensorId) {
        sql += ' AND (sensor_id = ? OR sensor_id IS NULL)';
        params.push(sensorId);
      }

      (async () => {
        try {
          const db = await dbPromise;
          const result = await db.raw(sql, params);
          resolve(result.rows || []);
        } catch (err) {
          reject(err);
        }
      })();
    });
  }

  /**
   * 评估告警条件
   */
  evaluateAlertCondition(rule, currentValue) {
    const { condition_operator, threshold_value } = rule;

    switch (condition_operator) {
      case '>':
        return currentValue > threshold_value;
      case '<':
        return currentValue < threshold_value;
      case '>=':
        return currentValue >= threshold_value;
      case '<=':
        return currentValue <= threshold_value;
      case '=':
        return Math.abs(currentValue - threshold_value) < 0.001;
      case '!=':
        return Math.abs(currentValue - threshold_value) >= 0.001;
      default:
        return false;
    }
  }

  /**
   * 创建告警
   */
  async createAlert(rule, deviceId, sensorId, currentValue) {
    try {
      const alertId = uuidv4();
      const message = `${rule.name}: 当前值 ${currentValue} ${rule.condition_operator} 阈值 ${rule.threshold_value}`;

      const sql = `
        INSERT INTO alerts (
          id, rule_id, device_id, sensor_id, alert_level, message,
          current_value, threshold_value, created_at
        ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, CURRENT_TIMESTAMP)
      `;

      await new Promise((resolve, reject) => {
        (async () => {
          try {
            const db = await dbPromise;
            await db.raw(
              sql,
              [
                alertId,
                rule.id,
                deviceId,
                sensorId,
                rule.severity,
                message,
                currentValue,
                rule.threshold_value
              ]
            );
            resolve();
          } catch (err) {
            reject(err);
          }
        })();
      });

      console.log(`🚨 告警已创建: ${message}`);
    } catch (error) {
      console.error('创建告警失败:', error);
    }
  }

  /**
   * 处理传感器数据
   */
  async handleSensorData(deviceId, payload) {
    try {
      // 验证传感器是否存在
      const sensor = await this.getSensor(payload.sensor_id);
      if (!sensor) {
        console.warn(`⚠️ 传感器不存在: ${payload.sensor_id}`);
        return;
      }

      // 检查数据是否在合理范围内
      if (!this.isValueInRange(payload.value, sensor.range)) {
        console.warn(`⚠️ 传感器数据超出范围: ${payload.value}`);
      }

      // 存储传感器数据（可以扩展为独立的传感器数据表）
      console.log(`📊 传感器数据 - ${payload.sensor_id}: ${payload.value}${payload.unit}`);
    } catch (error) {
      console.error('传感器数据处理失败:', error);
    }
  }

  /**
   * 处理设备状态数据
   */
  async handleDeviceStatus(deviceId, payload) {
    try {
      // 更新设备状态
      await this.updateDeviceStatus(deviceId, payload.status);

      console.log(`🔧 设备状态更新 - ${deviceId}: ${payload.status}`);
    } catch (error) {
      console.error('设备状态处理失败:', error);
    }
  }

  /**
   * 获取设备信息
   */
  getDevice(deviceId) {
    return new Promise((resolve, reject) => {
      (async () => {
        try {
          const db = await dbPromise;
          const result = await db.raw('SELECT * FROM devices WHERE id = ?', [deviceId]);
          resolve(result.rows ? result.rows[0] : undefined);
        } catch (err) {
          reject(err);
        }
      })();
    });
  }

  /**
   * 获取传感器信息
   */
  getSensor(sensorId) {
    return new Promise((resolve, reject) => {
      (async () => {
        try {
          const db = await dbPromise;
          const result = await db.raw('SELECT * FROM sensors WHERE id = ?', [sensorId]);
          resolve(result.rows ? result.rows[0] : undefined);
        } catch (err) {
          reject(err);
        }
      })();
    });
  }

  /**
   * 保存能源数据
   */
  saveEnergyData(data) {
    return new Promise((resolve, reject) => {
      const sql = `
        INSERT INTO energy_data 
        (id, device_id, sensor_id, data_type, value, unit, timestamp, created_at, is_anomaly, anomaly_reason, quality_score)
        VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
      `;

      (async () => {
        try {
          const db = await dbPromise;
          await db.raw(
            sql,
            [
              data.id,
              data.device_id,
              data.sensor_id,
              data.data_type,
              data.value,
              data.unit,
              data.timestamp,
              data.created_at,
              data.is_anomaly ? 1 : 0,
              data.anomaly_reason || null,
              data.quality_score || 1.0
            ]
          );
          resolve();
        } catch (err) {
          reject(err);
        }
      })();
    });
  }

  /**
   * 计算并存储碳排放数据
   */
  async calculateAndStoreCarbonEmission(energyDataId, energyType, energyValue) {
    try {
      // 获取碳排放因子
      const carbonFactor = await this.getCarbonFactor(energyType);
      if (!carbonFactor) {
        console.warn(`⚠️ 未找到碳排放因子: ${energyType}`);
        return;
      }

      // 计算碳排放量
      const emissionValue = energyValue * carbonFactor.factor_value;

      // 存储碳排放数据
      const carbonDataId = uuidv4();
      await this.saveCarbonData({
        id: carbonDataId,
        energy_data_id: energyDataId,
        carbon_factor_id: carbonFactor.id,
        emission_value: emissionValue,
        emission_unit: carbonFactor.unit,
        calculation_method: 'direct_factor',
        created_at: new Date().toISOString()
      });

      console.log(`🌱 碳排放计算完成 - ${emissionValue.toFixed(4)} ${carbonFactor.unit}`);
    } catch (error) {
      console.error('碳排放计算失败:', error);
    }
  }

  /**
   * 获取碳排放因子
   */
  getCarbonFactor(energyType) {
    return new Promise((resolve, reject) => {
      const sql = `
        SELECT * FROM carbon_factors 
        WHERE energy_type = ? AND is_active = 1 
        AND (valid_to IS NULL OR valid_to > date('now'))
        ORDER BY valid_from DESC LIMIT 1
      `;

      (async () => {
        try {
          const db = await dbPromise;
          const result = await db.raw(sql, [energyType]);
          resolve(result.rows ? result.rows[0] : undefined);
        } catch (err) {
          reject(err);
        }
      })();
    });
  }

  /**
   * 保存碳排放数据
   */
  saveCarbonData(data) {
    return new Promise((resolve, reject) => {
      const sql = `
        INSERT INTO carbon_data 
        (id, energy_data_id, carbon_factor_id, emission_value, emission_unit, calculation_method, created_at)
        VALUES (?, ?, ?, ?, ?, ?, ?)
      `;

      (async () => {
        try {
          const db = await dbPromise;
          await db.raw(
            sql,
            [
              data.id,
              data.energy_data_id,
              data.carbon_factor_id,
              data.emission_value,
              data.emission_unit,
              data.calculation_method,
              data.created_at
            ]
          );
          resolve();
        } catch (err) {
          reject(err);
        }
      })();
    });
  }

  /**
   * 更新设备状态
   */
  updateDeviceStatus(deviceId, status) {
    return new Promise((resolve, reject) => {
      const sql = 'UPDATE devices SET status = ?, updated_at = CURRENT_TIMESTAMP WHERE id = ?';

      (async () => {
        try {
          const db = await dbPromise;
          await db.raw(sql, [status, deviceId]);
          resolve();
        } catch (err) {
          reject(err);
        }
      })();
    });
  }

  /**
   * 获取能源数据类型编码
   */
  getEnergyDataType(type) {
    const typeMap = {
      electricity: 1,
      natural_gas: 2,
      diesel: 3,
      solar: 4,
      wind: 5
    };
    return typeMap[type] || 0;
  }

  /**
   * 检查数值是否在合理范围内
   */
  isValueInRange(value, range) {
    if (!range) {return true;}

    try {
      const [min, max] = range.split('-').map(Number);
      return value >= min && value <= max;
    } catch {
      return true;
    }
  }

  /**
   * 获取连接状态
   */
  getConnectionStatus() {
    return {
      connected: this.isConnected,
      topics: Object.keys(this.topics).length,
      bufferSize: this.dataBuffer.size
    };
  }

  /**
   * 断开连接
   */
  disconnect() {
    if (this.mqttClient) {
      this.mqttClient.end();
      console.log('🔌 MQTT数据采集器已断开连接');
    }
  }

  // 启动数据采集器
  start() {
    this.initialize();
    console.log('数据采集器已启动');
  }

  // 停止数据采集器
  stop() {
    if (this.mqttClient) {
      this.mqttClient.end();
      console.log('数据采集器已停止');
    }
  }

  // 获取详细连接状态
  getDetailedConnectionStatus() {
    return {
      mqtt_connected: this.mqttClient && this.isConnected,
      mqtt_broker: process.env.MQTT_BROKER_URL || 'mqtt://localhost:1883',
      subscribed_topics: this.topics,
      last_message_time: this.lastMessageTime,
      connection_time: this.connectionTime
    };
  }
}

