  updateDeviceStatus(data) {
    const { deviceId, status, device } = data;

    this.sceneManager.updateDeviceStatus(deviceId, status, device);

    // 触发自定义事件
    this.dispatchEvent('deviceStatusUpdated', {
      deviceId,
      status,
      device
    });
