  // 处理告警
  handleAlert(alert) {
    const { deviceId, level, message } = alert;

    // 添加到告警列表
    this.alerts.unshift({
      ...alert,
      id: `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
    });

    // 保持最近100条告警
    if (this.alerts.length > 100) {
      this.alerts = this.alerts.slice(0, 100);
    }

    // 在场景中显示告警效果
    this.showAlertInScene(deviceId, level);

    // 触发自定义事件
    this.dispatchEvent('alert', alert);
  }

  // 在场景中显示告警效果
  showAlertInScene(deviceId, level) {
    // 可以添加闪烁效果、颜色变化等
    const device = this.sceneManager.devices.get(deviceId);
    if (device) {
      // 根据告警级别设置颜色
      let color;
      switch (level) {
        case 'critical':
          color = 0xff0000;
          break;
        case 'warning':
          color = 0xffa500;
          break;
        default:
          color = 0xffff00;
      }

      // 临时改变设备颜色
      const originalColor = device.material.color.getHex();
      device.material.color.setHex(color);

      // 3秒后恢复原色
      setTimeout(() => {
        device.material.color.setHex(originalColor);
      }, 3000);
    }
  }

