  const [error, setError] = useState(null);
  const [timeRange, setTimeRange] = useState('24h');

  // 获取储能设备列表
  useEffect(() => {
    const fetchDevices = async () => {
      try {
        const data = await getEnergyStorageDevices();
        setDevices(data);
        if (data.length > 0) {
          setSelectedDevice(data[0]);
        }
      } catch (err) {
        setError('获取储能设备失败: ' + err.message);
        console.error(err);
      }
    };

    fetchDevices();
  }, []);

  // 当选择设备或时间范围变化时，重新生成优化策略
  useEffect(() => {
    if (selectedDevice) {
      generateStrategies();
    }
  }, [selectedDevice, timeRange]);

  // 生成优化策略
  const generateStrategies = async () => {
    try {
      setLoading(true);
      setError(null);

      // 计算时间范围
      const endTime = new Date();
      const startTime = new Date();

      switch (timeRange) {
        case '24h':
          startTime.setHours(endTime.getHours() - 24);
          break;
        case '7d':
          startTime.setDate(endTime.getDate() - 7);
          break;
        case '30d':
          startTime.setMonth(endTime.getMonth() - 1);
          break;
        default:
          startTime.setHours(endTime.getHours() - 24);
      }

      // 获取预测数据（实际应用中应从API获取）
      // 这里使用模拟数据
      const predictions = generateMockSOCData(timeRange);

      // 生成优化策略
      const result = await generateOptimizationStrategies(
        predictions,
        '1h',
        selectedDevice.id,
        startTime.toISOString(),
        endTime.toISOString()
      );

      setStrategies(result);
    } catch (err) {
      setError('生成优化策略失败: ' + err.message);
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // 生成模拟SOC数据
  const generateMockSOCData = (range) => {
    const dataPoints = range === '24h' ? 24 : range === '7d' ? 168 : 720;
    const data = [];

    for (let i = 0; i < dataPoints; i++) {
      // 生成0.3-0.7之间的随机SOC值，添加一些波动趋势
      const baseValue = 0.5 + Math.sin((i / 12) * Math.PI) * 0.2;
      const noise = (Math.random() - 0.5) * 0.1;
      const value = Math.max(0.2, Math.min(0.8, baseValue + noise));
      data.push(value);
    }

    return data;
  };

  // 准备图表数据

  // TODO: 考虑将此函数拆分为更小的函数 (当前 33 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 33 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 33 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 33 行)

