import BaseController from './BaseController.js';
import MaintenanceRecord from '../../../domain/entities/MaintenanceRecord.js';
import Device from '../../../domain/entities/Device.js';
import logger from '../../../utils/logger.js';

class MaintenanceController extends BaseController {
  /**
   * 获取维护记录列表
   */
  async getMaintenanceRecords(req, res) {
    try {
      const {
        page = 1,
        limit = 20,
        deviceId,
        maintenanceType,
        status,
        startDate,
        endDate,
        priority
      } = req.query;

      const filters = {};
      if (deviceId) {filters.deviceId = deviceId;}
      if (maintenanceType) {filters.maintenanceType = maintenanceType;}
      if (status) {filters.status = status;}
      if (priority) {filters.priority = priority;}
      if (startDate || endDate) {
        filters.scheduledDate = {};
        if (startDate) {filters.scheduledDate.$gte = new Date(startDate);}
        if (endDate) {filters.scheduledDate.$lte = new Date(endDate);}
      }

      const offset = (page - 1) * limit;
      const maintenanceRecords = await MaintenanceRecord.findWithPagination(
        filters,
        offset,
        parseInt(limit)
      );
      const total = await MaintenanceRecord.countDocuments(filters);

      return this.success(res, '获取维护记录成功', {
        data: maintenanceRecords,
        pagination: {
          page: parseInt(page),
          limit: parseInt(limit),
          total,
          pages: Math.ceil(total / limit)
        }
      });
    } catch (error) {
      logger.error('获取维护记录错误:', error);
      return this.error(res, '获取维护记录失败', 500);
    }
  }

  /**
   * 获取单个维护记录
   */
  async getMaintenanceRecord(req, res) {
    try {
      const maintenanceRecord = await MaintenanceRecord.findByIdWithDevice(req.params.id);
      if (!maintenanceRecord) {
        return this.error(res, '维护记录不存在', 404);
      }

      return this.success(res, '获取维护记录成功', { maintenanceRecord });
    } catch (error) {
      logger.error('获取维护记录错误:', error);
      return this.error(res, '获取维护记录失败', 500);
    }
  }

  /**
   * 创建维护记录
   */
  async createMaintenanceRecord(req, res) {
    try {
      // 验证设备是否存在
      const device = await Device.findById(req.body.deviceId);
      if (!device) {
        return this.error(res, '设备不存在', 404);
      }

      const maintenanceData = {
        ...req.body,
        createdBy: req.user.userId,
        createdAt: new Date()
      };

      const newMaintenanceRecord = await MaintenanceRecord.create(maintenanceData);

      return this.success(
        res,
        '维护记录创建成功',
        { maintenanceRecord: newMaintenanceRecord },
        201
      );
    } catch (error) {
      logger.error('创建维护记录错误:', error);
      return this.error(res, '创建维护记录失败', 500);
    }
  }

  /**
   * 更新维护记录
   */
  async updateMaintenanceRecord(req, res) {
    try {
      const maintenanceRecord = await MaintenanceRecord.findById(req.params.id);
      if (!maintenanceRecord) {
        return this.error(res, '维护记录不存在', 404);
      }

      const updateData = {
        ...req.body,
        updatedBy: req.user.userId,
        updatedAt: new Date()
      };

      const updatedRecord = await maintenanceRecord.update(updateData);

      return this.success(res, '维护记录更新成功', { maintenanceRecord: updatedRecord });
    } catch (error) {
      logger.error('更新维护记录错误:', error);
      return this.error(res, '更新维护记录失败', 500);
    }
  }

  /**
   * 删除维护记录
   */
  async deleteMaintenanceRecord(req, res) {
    try {
      const maintenanceRecord = await MaintenanceRecord.findById(req.params.id);
      if (!maintenanceRecord) {
        return this.error(res, '维护记录不存在', 404);
      }

      await maintenanceRecord.delete();

      return this.success(res, '维护记录删除成功');
    } catch (error) {
      logger.error('删除维护记录错误:', error);
      return this.error(res, '删除维护记录失败', 500);
    }
  }

  /**
   * 获取维护统计数据
   */
  async getMaintenanceStats(req, res) {
    try {
      const { startDate, endDate, deviceId, maintenanceType } = req.query;

      const filters = {};
      if (deviceId) {filters.deviceId = deviceId;}
      if (maintenanceType) {filters.maintenanceType = maintenanceType;}
      if (startDate || endDate) {
        filters.scheduledDate = {};
        if (startDate) {filters.scheduledDate.$gte = new Date(startDate);}
        if (endDate) {filters.scheduledDate.$lte = new Date(endDate);}
      }

      const stats = await MaintenanceRecord.getStatistics(filters);

      return this.success(res, '获取维护统计成功', { stats });
    } catch (error) {
      logger.error('获取维护统计错误:', error);
      return this.error(res, '获取维护统计失败', 500);
    }
  }

  /**
   * 获取维护计划
   */
  async getMaintenanceSchedule(req, res) {
    try {
      const { startDate, endDate, deviceId, status = 'scheduled' } = req.query;

      const filters = { status };
      if (deviceId) {filters.deviceId = deviceId;}
      if (startDate || endDate) {
        filters.scheduledDate = {};
        if (startDate) {filters.scheduledDate.$gte = new Date(startDate);}
        if (endDate) {filters.scheduledDate.$lte = new Date(endDate);}
      }

      const schedule = await MaintenanceRecord.findScheduled(filters);

      return this.success(res, '获取维护计划成功', { schedule });
    } catch (error) {
      logger.error('获取维护计划错误:', error);
      return this.error(res, '获取维护计划失败', 500);
    }
  }

  /**
   * 完成维护任务
   */
  async completeMaintenance(req, res) {
    try {
      const maintenanceRecord = await MaintenanceRecord.findById(req.params.id);
      if (!maintenanceRecord) {
        return this.error(res, '维护记录不存在', 404);
      }

      if (maintenanceRecord.status === 'completed') {
        return this.error(res, '维护任务已完成', 400);
      }

      const completionData = {
        status: 'completed',
        completedDate: new Date(),
        completedBy: req.user.userId,
        notes: req.body.notes || '',
        actualCost: req.body.actualCost,
        actualDuration: req.body.actualDuration,
        updatedAt: new Date()
      };

      const completedRecord = await maintenanceRecord.update(completionData);

      return this.success(res, '维护任务完成', { maintenanceRecord: completedRecord });
    } catch (error) {
      logger.error('完成维护任务错误:', error);
      return this.error(res, '完成维护任务失败', 500);
    }
  }

  /**
   * 获取设备维护历史
   */
  async getDeviceMaintenanceHistory(req, res) {
    try {
      const { deviceId } = req.params;
      const { page = 1, limit = 20, maintenanceType, status } = req.query;

      const filters = { deviceId };
      if (maintenanceType) {filters.maintenanceType = maintenanceType;}
      if (status) {filters.status = status;}

      const offset = (page - 1) * limit;
      const history = await MaintenanceRecord.findWithPagination(filters, offset, parseInt(limit));
      const total = await MaintenanceRecord.countDocuments(filters);

      return this.success(res, '获取设备维护历史成功', {
        data: history,
        pagination: {
          page: parseInt(page),
          limit: parseInt(limit),
          total,
          pages: Math.ceil(total / limit)
        }
      });
    } catch (error) {
      logger.error('获取设备维护历史错误:', error);
      return this.error(res, '获取设备维护历史失败', 500);
    }
  }

  /**
   * 导出维护记录
   */
  async exportMaintenanceRecords(req, res) {
    try {
      const { startDate, endDate, deviceId, maintenanceType, status } = req.query;

      const filters = {};
      if (deviceId) {filters.deviceId = deviceId;}
      if (maintenanceType) {filters.maintenanceType = maintenanceType;}
      if (status) {filters.status = status;}
      if (startDate || endDate) {
        filters.scheduledDate = {};
        if (startDate) {filters.scheduledDate.$gte = new Date(startDate);}
        if (endDate) {filters.scheduledDate.$lte = new Date(endDate);}
      }

      const csvData = await MaintenanceRecord.exportToCSV(filters);

      res.setHeader('Content-Type', 'text/csv');
      res.setHeader('Content-Disposition', 'attachment; filename=maintenance-records.csv');
      res.send(csvData);
    } catch (error) {
      logger.error('导出维护记录错误:', error);
      return this.error(res, '导出维护记录失败', 500);
    }
  }
}

export default new MaintenanceController();

import Device from '../../core/entities/Device.js';
import StorageDevice from '../../core/entities/storageDevice.js';
import { validationResult } from 'express-validator';
import db, { dbPromise } from '../../infrastructure/database/index.js';

/**
 * 获取设备列表，支持通过查询参数进行过滤
 * @param {object} req - Express 请求对象
 * @param {object} res - Express 响应对象
 * @param {function} next - Express next 中间件函数
 */
export const getDevices = async (req, res, next) => {
  try {
    const db = await dbPromise;
    // 基础查询语句
    let sql = 'SELECT * FROM devices';
    const params = [];
    const conditions = [];

    // (可选) 这里可以根据 req.query 添加过滤条件
    // 例如: if (req.query.status) { ... }

    if (conditions.length > 0) {
      sql += ` WHERE ${conditions.join(' AND ')}`;
    }

    const devices = await db.raw(sql, params);

    res.status(200).json({
      message: '设备列表获取成功',
      data: devices
    });
  } catch (error) {
    // --- 这是关键的修复 ---
    // 我们不再直接响应，而是调用 next(error)
    // 将错误传递给在 index.js 中注册的全局错误处理器进行记录。
    next(error);
  }
};

/**
 * 根据ID获取设备
 * @param {Object} req - 请求对象
 * @param {Object} res - 响应对象
 */
export const getDeviceById = async (req, res) => {
  try {
    const { id } = req.params;
    const device = await Device.findById(id);

    if (!device) {
      return res.status(404).json({ message: '设备不存在' });
    }

    // 如果是储能设备，获取对应的储能参数
    if (device.type === 'energy_storage') {
      const storageParams = await StorageDevice.findByDeviceId(device.id);
      device.storage_params = storageParams;
    }

    res.status(200).json(device);
  } catch (error) {
    res.status(500).json({
      message: '获取设备详情失败',
      error: error.message
    });
  }
};

/**
 * 创建新设备
 * @param {Object} req - 请求对象
 * @param {Object} res - 响应对象
 */
export const createDevice = async (req, res, next) => {
  try {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.status(400).json({ errors: errors.array() });
    }

    // 提取并清洗设备基本信息
    const {
      name,
      type,
      location,
      status,
      manufacturer,
      model,
      serial_number,
      installation_date,
      last_maintenance_date,
      notes,
      energy_storage_params,
      solar_panel_params,
      ev_charger_params
    } = req.body;

    const deviceData = {
      name: String(name).trim(),
      type: String(type).trim(),
      location: String(location).trim(),
      status: String(status).trim(),
      manufacturer: String(manufacturer).trim(),
      model: String(model).trim(),
      serial_number: String(serial_number).trim(),
      installation_date: installation_date ? new Date(installation_date).toISOString() : null,
      last_maintenance_date: last_maintenance_date
        ? new Date(last_maintenance_date).toISOString()
        : null,
      notes: notes ? String(notes).trim() : null
    };

    const device = await Device.create(deviceData);

    // 根据设备类型处理不同的参数
    if (device.type === 'energy_storage' && energy_storage_params) {
      // 验证储能设备参数
      const {
        capacity,
        efficiency,
        min_soc,
        max_soc,
        charge_rate,
        discharge_rate,
        battery_type,
        cycle_life
      } = energy_storage_params;
      if (
        !capacity ||
        !efficiency ||
        !min_soc ||
        !max_soc ||
        !charge_rate ||
        !discharge_rate ||
        !battery_type ||
        !cycle_life
      ) {
        return res.status(400).json({ message: '储能设备参数不完整' });
      }
      const storageParams = {
        device_id: device.id,
        capacity: Number(capacity),
        efficiency: Number(efficiency),
        min_soc: Number(min_soc),
        max_soc: Number(max_soc),
        charge_rate: Number(charge_rate),
        discharge_rate: Number(discharge_rate),
        battery_type: String(battery_type).trim(),
        cycle_life: Number(cycle_life)
      };
      await StorageDevice.create(storageParams);
    } else if (device.type === 'solar_panel' && solar_panel_params) {
      // 验证太阳能板参数
      const {
        peak_power,
        efficiency,
        orientation,
        tilt_angle,
        installation_date: spInstallationDate
      } = solar_panel_params;
      if (!peak_power || !efficiency || !orientation || !tilt_angle || !spInstallationDate) {
        return res.status(400).json({ message: '太阳能板参数不完整' });
      }
      // 假设有一个 SolarPanel 模型
      // await SolarPanel.create({ device_id: device.id, ...solar_panel_params });
      res.status(501).json({ message: '太阳能板参数配置功能待实现' });
      return;
    } else if (device.type === 'ev_charger' && ev_charger_params) {
      // 验证电动汽车充电桩参数
      const { max_power, connector_type, num_ports, charging_standards } = ev_charger_params;
      if (!max_power || !connector_type || !num_ports || !charging_standards) {
        return res.status(400).json({ message: '电动汽车充电桩参数不完整' });
      }
      // 假设有一个 EvCharger 模型
      // await EvCharger.create({ device_id: device.id, ...ev_charger_params });
      res.status(501).json({ message: '电动汽车充电桩参数配置功能待实现' });
      return;
    }

    res.status(200).json({
      message: '设备更新成功',
      device: existingDevice
    });
  } catch (error) {
    next(error);
  }
};

