/**
 * 文件上传验证
 */
export const validateFileUpload =
  (allowedTypes = [], maxSize = 10 * 1024 * 1024) =>
    (req, res, next) => {
      if (!req.file && !req.files) {
        return next(new ValidationError('未找到上传的文件'));
      }

      const files = req.files || [req.file];

      for (const file of files) {
      // 检查文件类型
        if (allowedTypes.length > 0 && !allowedTypes.includes(file.mimetype)) {
          return next(
            new ValidationError(
              `不支持的文件类型: ${file.mimetype}，支持的类型: ${allowedTypes.join(', ')}`
            )
          );
        }

        // 检查文件大小
        if (file.size > maxSize) {
          return next(
            new ValidationError(`文件大小超过限制: ${file.size} bytes，最大允许: ${maxSize} bytes`)
          );
        }

        // 检查文件名
        if (!/^[a-zA-Z0-9._-]+$/.test(file.originalname)) {
          return next(new ValidationError('文件名包含非法字符'));
        }
      }

      next();
    };

/**
 * 请求头验证
 */
export const validateHeaders =
  (requiredHeaders = []) =>
    (req, res, next) => {
      for (const header of requiredHeaders) {
        if (!req.headers[header.toLowerCase()]) {
          return next(new ValidationError(`缺少必需的请求头: ${header}`));
        }
      }
      next();
    };

/**
 * 内容类型验证
 */
export const validateContentType =
  (allowedTypes = ['application/json']) =>
    (req, res, next) => {
      const contentType = req.headers['content-type'];

      if (!contentType) {
        return next(new ValidationError('缺少Content-Type请求头'));
      }

      const isAllowed = allowedTypes.some((type) =>
        contentType.toLowerCase().includes(type.toLowerCase())
      );

      if (!isAllowed) {
        return next(
          new ValidationError(
            `不支持的Content-Type: ${contentType}，支持的类型: ${allowedTypes.join(', ')}`
          )
        );
      }

      next();
    };

export default {
  validateRequest,
  energyDataSchema,
  carbonDataSchema,
  batteryDataSchema,
  userRegistrationSchema,
  userLoginSchema,
  dataQuerySchema,
  deviceSchema,
  alertRuleSchema,
  reportGenerationSchema,
  idParamSchema,
  deviceIdParamSchema,
  validateFileUpload,
  validateHeaders,
  validateContentType
};

