        if (existingModel) {
          return responseFormatter.error(res, '模型名称已存在', 409);
        }
      }

      const updateData = {
        ...req.body,
        updatedBy: req.user.userId,
        updatedAt: new Date()
      };

      const updatedModel = await model.update(updateData);

      responseFormatter.success(res, '数字孪生模型更新成功', { model: updatedModel });
