/**
 * JWT认证中间件增强版
 * 支持多种token来源、token刷新、黑名单检查
 */
export class JWTAuthenticator {
  constructor(options = {}) {
    this.secret = options.secret || process.env.JWT_SECRET || 'default-secret';
    this.refreshSecret =
      options.refreshSecret || process.env.JWT_REFRESH_SECRET || 'default-refresh-secret';
    this.tokenExpiry = options.tokenExpiry || '15m';
    this.refreshTokenExpiry = options.refreshTokenExpiry || '7d';
    this.blacklistedTokens = new Set(); // 简单的内存黑名单，生产环境应使用Redis
    this.algorithm = options.algorithm || 'HS256';
  }

  /**
   * 生成访问令牌
   */
  generateAccessToken(payload) {
    return jwt.sign(payload, this.secret, {
      expiresIn: this.tokenExpiry,
      algorithm: this.algorithm,
      issuer: 'zero-carbon-system',
      audience: 'zero-carbon-users'
    });
  }

  /**
   * 生成刷新令牌
   */
  generateRefreshToken(payload) {
    return jwt.sign(payload, this.refreshSecret, {
      expiresIn: this.refreshTokenExpiry,
      algorithm: this.algorithm,
      issuer: 'zero-carbon-system',
      audience: 'zero-carbon-users'
    });
  }

  /**
   * 验证访问令牌
   */
  verifyAccessToken(token) {
    try {
      if (this.blacklistedTokens.has(token)) {
        throw new AuthenticationError('令牌已被撤销');
      }

      return jwt.verify(token, this.secret, {
        algorithms: [this.algorithm],
        issuer: 'zero-carbon-system',
        audience: 'zero-carbon-users'
      });
    } catch (error) {
      if (error instanceof jwt.TokenExpiredError) {
        throw new AuthenticationError('令牌已过期');
      } else if (error instanceof jwt.JsonWebTokenError) {
        throw new AuthenticationError('无效的令牌');
      }
      throw error;
    }
  }

  /**
   * 验证刷新令牌
   */
  verifyRefreshToken(token) {
    try {
      return jwt.verify(token, this.refreshSecret, {
        algorithms: [this.algorithm],
        issuer: 'zero-carbon-system',
        audience: 'zero-carbon-users'
      });
    } catch (error) {
      if (error instanceof jwt.TokenExpiredError) {
        throw new AuthenticationError('刷新令牌已过期');
      } else if (error instanceof jwt.JsonWebTokenError) {
        throw new AuthenticationError('无效的刷新令牌');
      }
      throw error;
    }
  }

  /**
   * 从请求中提取令牌
   */
  extractToken(req) {
    // 1. 从Authorization头提取
    const authHeader = req.headers.authorization;
    if (authHeader && authHeader.startsWith('Bearer ')) {
      return authHeader.substring(7);
    }

    // 2. 从Cookie提取
    if (req.cookies && req.cookies.accessToken) {
      return req.cookies.accessToken;
    }

    // 3. 从查询参数提取（不推荐，仅用于特殊情况）
    if (req.query.token) {
      return req.query.token;
    }

    return null;
  }

  /**
   * 撤销令牌（加入黑名单）
   */
  revokeToken(token) {
    this.blacklistedTokens.add(token);
    logger.info('令牌已撤销', { tokenHash: this.hashToken(token) });
  }

  /**
   * 令牌哈希（用于日志记录，避免泄露完整令牌）
   */
  hashToken(token) {
    const crypto = require('crypto');
    return crypto.createHash('sha256').update(token).digest('hex').substring(0, 16);
  }

  /**
   * 认证中间件
   */
  authenticate(_options = {}) {
    return (req, res, next) => {
      try {
        const token = this.extractToken(req);

        if (!token) {
          throw new AuthenticationError('缺少认证令牌');
        }

        const decoded = this.verifyAccessToken(token);
        req.user = decoded;
        req.token = token;

        // 记录认证成功日志
        logger.info('用户认证成功', {
          userId: decoded.id,
          username: decoded.username,
          role: decoded.role,
          ip: req.ip,
          userAgent: req.get('User-Agent')
        });

        next();
      } catch (error) {
        logger.warn('认证失败', {
          error: error.message,
          ip: req.ip,
          userAgent: req.get('User-Agent'),
          path: req.path
        });
        next(error);
      }
    };
  }

  /**
   * 角色授权中间件
   */
  authorize(requiredRoles = []) {
    return (req, res, next) => {
      try {
        if (!req.user) {
          throw new AuthenticationError('用户未认证');
        }

        const userRole = req.user.role;
        const hasPermission = Array.isArray(requiredRoles)
          ? requiredRoles.includes(userRole)
          : requiredRoles === userRole;

        if (!hasPermission) {
          throw new AuthorizationError('权限不足', {
            requiredRoles,
            userRole
          });
        }

        next();
      } catch (error) {
        logger.warn('授权失败', {
          error: error.message,
          userId: req.user?.id,
          userRole: req.user?.role,
          requiredRoles,
          path: req.path
        });
        next(error);
      }
    };
  }
}

/**
 * 安全头中间件
 */
export const securityHeaders = () => (req, res, next) => {
  // 设置安全响应头
  res.setHeader('X-Content-Type-Options', 'nosniff');
  res.setHeader('X-Frame-Options', 'DENY');
  res.setHeader('X-XSS-Protection', '1; mode=block');
  res.setHeader('Referrer-Policy', 'strict-origin-when-cross-origin');
  res.setHeader('Permissions-Policy', 'geolocation=(), microphone=(), camera=()');

  // 移除可能泄露服务器信息的头
  res.removeHeader('X-Powered-By');
  res.removeHeader('Server');

  next();
};

/**
 * API速率限制中间件
 */
export const createRateLimiter = (options = {}) => {
  const defaultOptions = {
    windowMs: 15 * 60 * 1000, // 15分钟
    max: 100, // 每个IP最多100个请求
    message: {
      error: {
        code: 'RATE_LIMIT_EXCEEDED',
        message: '请求过于频繁，请稍后再试'
      }
    },
    standardHeaders: true,
    legacyHeaders: false,
    skip: (req) =>
      // 跳过健康检查和静态资源
      req.path === '/health' || req.path.startsWith('/static/'),
    keyGenerator: (req) =>
      // 对认证用户使用用户ID，对匿名用户使用IP
      req.user?.id || req.ip,
    onLimitReached: (req, _res, _options) => {
      logger.warn('速率限制触发', {
        ip: req.ip,
        userId: req.user?.id,
        path: req.path,
        method: req.method,
        userAgent: req.get('User-Agent')
      });
    }
  };

  return rateLimit({ ...defaultOptions, ...options });
};

/**
 * 输入大小限制中间件
 */
export const inputSizeLimit = (options = {}) => {
  const maxBodySize = options.maxBodySize || 1024 * 1024; // 1MB
  const maxQueryLength = options.maxQueryLength || 1000;
  const maxHeaderSize = options.maxHeaderSize || 8192;

  return (req, res, next) => {
    try {
      // 检查请求体大小
      const contentLength = parseInt(req.get('Content-Length') || '0');
      if (contentLength > maxBodySize) {
        throw new ValidationError('请求体过大', {
          maxSize: maxBodySize,
          actualSize: contentLength
        });
      }

      // 检查查询字符串长度
      const queryString = req.url.split('?')[1] || '';
      if (queryString.length > maxQueryLength) {
        throw new ValidationError('查询参数过长', {
          maxLength: maxQueryLength,
          actualLength: queryString.length
        });
      }

      // 检查头部大小
      const headerSize = JSON.stringify(req.headers).length;
      if (headerSize > maxHeaderSize) {
        throw new ValidationError('请求头过大', {
          maxSize: maxHeaderSize,
          actualSize: headerSize
        });
      }

      next();
    } catch (error) {
      next(error);
    }
  };
};

/**
 * IP白名单中间件
 */
export const ipWhitelist =
  (allowedIPs = []) =>
    (req, res, next) => {
      const clientIP = req.ip || req.connection.remoteAddress;

      if (allowedIPs.length > 0 && !allowedIPs.includes(clientIP)) {
        logger.warn('IP访问被拒绝', {
          ip: clientIP,
          path: req.path,
          userAgent: req.get('User-Agent')
        });

        throw new AuthorizationError('访问被拒绝');
      }

      next();
    };

/**
 * 请求ID中间件（用于请求追踪）
 */
export const requestId = () => (req, res, next) => {
  const requestId =
    req.get('X-Request-ID') || `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  req.requestId = requestId;
  res.setHeader('X-Request-ID', requestId);

  next();
};

// 导出默认的JWT认证器实例
export const defaultJWTAuth = new JWTAuthenticator({
  secret: process.env.JWT_SECRET,
  refreshSecret: process.env.JWT_REFRESH_SECRET,
  tokenExpiry: process.env.JWT_EXPIRY || '15m',
  refreshTokenExpiry: process.env.JWT_REFRESH_EXPIRY || '7d'
});

/**
 * 应用程序错误类
 * 提供统一的错误处理机制
 */
class AppError extends Error {
  constructor(message, statusCode = 500, errorCode = 'INTERNAL_SERVER_ERROR', details = {}) {
    super(message);
    this.name = 'AppError';
    this.statusCode = statusCode;
    this.errorCode = errorCode;
    this.details = details;
    this.isOperational = true;
    this.timestamp = new Date().toISOString();

    // 捕获堆栈跟踪
    Error.captureStackTrace(this, this.constructor);
  }

  /**
   * 转换为JSON格式
   */
  toJSON() {
    return {
      name: this.name,
      message: this.message,
      statusCode: this.statusCode,
      errorCode: this.errorCode,
      details: this.details,
      timestamp: this.timestamp,
      stack: process.env.NODE_ENV === 'development' ? this.stack : undefined
    };
  }
}

/**
 * 预定义的错误类型
 */
class ValidationError extends AppError {
  constructor(message, details = {}) {
    super(message, 400, 'VALIDATION_ERROR', details);
  }
}

class AuthenticationError extends AppError {
  constructor(message = '认证失败') {
    super(message, 401, 'AUTHENTICATION_ERROR');
  }
}

class AuthorizationError extends AppError {
  constructor(message = '权限不足') {
    super(message, 403, 'AUTHORIZATION_ERROR');
  }
}

class NotFoundError extends AppError {
  constructor(message = '资源未找到') {
    super(message, 404, 'NOT_FOUND');
  }
}

class ConflictError extends AppError {
  constructor(message = '资源冲突') {
    super(message, 409, 'CONFLICT');
  }
}

class DatabaseError extends AppError {
  constructor(message = '数据库操作失败', details = {}) {
    super(message, 500, 'DATABASE_ERROR', details);
  }
}

class ExternalServiceError extends AppError {
  constructor(message = '外部服务错误', details = {}) {
    super(message, 502, 'EXTERNAL_SERVICE_ERROR', details);
  }
}

export {
  AppError,
  ValidationError,
  AuthenticationError,
  AuthorizationError,
  NotFoundError,
  ConflictError,
  DatabaseError,
  ExternalServiceError
};

export default AppError;

import fs from 'fs';
import path from 'path';
import { createWriteStream } from 'fs';
import EventEmitter from 'events';

/**
 * 日志聚合器
 * 提供日志收集、分析、存储和查询功能
 */
class LogAggregator extends EventEmitter {
  // TODO: 考虑将此函数拆分为更小的函数 (当前 30 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 30 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 30 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 30 行)

  constructor(options = {}) {
    super();

    this.options = {
      logDir: options.logDir || './logs',
      maxFileSize: options.maxFileSize || 100 * 1024 * 1024, // 100MB
      maxFiles: options.maxFiles || 10,
      rotateInterval: options.rotateInterval || 24 * 60 * 60 * 1000, // 24小时
      enableAnalysis: options.enableAnalysis !== false,
      enableAlerts: options.enableAlerts !== false,
      ...options
    };

    this.logStreams = new Map();
    this.logStats = {
      totalLogs: 0,
      errorCount: 0,
      warningCount: 0,
      infoCount: 0,
      debugCount: 0,
      lastLogTime: null,
      startTime: new Date()
    };

    this.alertRules = new Map();
    this.logBuffer = [];
    this.bufferSize = options.bufferSize || 1000;

    this.init();
  }

  /**
   * 初始化日志聚合器
   */
  init() {
    // 确保日志目录存在
    this.ensureLogDir();

    // 设置日志轮转
    this.setupLogRotation();

    // 设置默认告警规则
    this.setupDefaultAlertRules();

    // 启动分析任务
    if (this.options.enableAnalysis) {
      this.startAnalysisTask();
    }

    console.log('📊 日志聚合器已启动');
  }

  /**
   * 确保日志目录存在
   */
  ensureLogDir() {
    if (!fs.existsSync(this.options.logDir)) {
      fs.mkdirSync(this.options.logDir, { recursive: true });
    }
  }

  /**
   * 记录日志
   */
  log(level, message, metadata = {}) {
    const logEntry = {
      timestamp: new Date().toISOString(),
      level: level.toUpperCase(),
      message,
      metadata,
      requestId: metadata.requestId,
      userId: metadata.userId,
      ip: metadata.ip,
      userAgent: metadata.userAgent,
      duration: metadata.duration,
      statusCode: metadata.statusCode,
      error: metadata.error
    };

    // 更新统计信息
    this.updateStats(logEntry);

    // 添加到缓冲区
    this.logBuffer.push(logEntry);

    // 写入日志文件
    this.writeToFile(logEntry);

    // 检查告警规则
    if (this.options.enableAlerts) {
      this.checkAlertRules(logEntry);
    }

    // 触发事件
    this.emit('log', logEntry);

    // 如果缓冲区满了，清理旧日志
    if (this.logBuffer.length > this.bufferSize) {
      this.logBuffer = this.logBuffer.slice(-this.bufferSize);
    }
  }

  /**
   * 更新统计信息
   */
  updateStats(logEntry) {
    this.logStats.totalLogs++;
    this.logStats.lastLogTime = logEntry.timestamp;

    switch (logEntry.level) {
      case 'ERROR':
        this.logStats.errorCount++;
        break;
      case 'WARN':
        this.logStats.warningCount++;
        break;
      case 'INFO':
        this.logStats.infoCount++;
        break;
      case 'DEBUG':
        this.logStats.debugCount++;
        break;
    }
  }

  /**
   * 写入日志文件
   */
  writeToFile(logEntry) {
    const fileName = this.getLogFileName(logEntry.level);
    const filePath = path.join(this.options.logDir, fileName);

    if (!this.logStreams.has(fileName)) {
      this.logStreams.set(fileName, createWriteStream(filePath, { flags: 'a' }));
    }

    const stream = this.logStreams.get(fileName);
    const logLine = `${JSON.stringify(logEntry)}\n`;

    stream.write(logLine);
  }

  /**
   * 获取日志文件名
   */
  getLogFileName(level) {
    const [date] = new Date().toISOString().split('T');
    return `${level.toLowerCase()}-${date}.log`;
  }

  /**
   * 设置日志轮转
   */
  setupLogRotation() {
    this.rotationTimer = setInterval(() => {
      this.rotateLogFiles();
    }, this.options.rotateInterval);
  }

  /**
   * 轮转日志文件
   */
  rotateLogFiles() {
    console.log('🔄 开始日志轮转...');

    // 关闭当前流
    for (const [_fileName, stream] of this.logStreams) {
      stream.end();
    }
    this.logStreams.clear();

    // 压缩旧日志文件
    this.compressOldLogs();

    // 清理过期日志
    this.cleanupOldLogs();

    console.log('✅ 日志轮转完成');
  }

  /**
   * 压缩旧日志文件
   */
  compressOldLogs() {
    // 这里可以添加日志压缩逻辑
    // 例如使用 gzip 压缩
  }

  /**
   * 清理过期日志
   */
  cleanupOldLogs() {
    const files = fs.readdirSync(this.options.logDir);
    const logFiles = files.filter((file) => file.endsWith('.log'));

    if (logFiles.length > this.options.maxFiles) {
      // 按修改时间排序
      const sortedFiles = logFiles
        .map((file) => ({
          name: file,
          path: path.join(this.options.logDir, file),
          mtime: fs.statSync(path.join(this.options.logDir, file)).mtime
        }))
        .sort((a, b) => a.mtime - b.mtime);

      // 删除最旧的文件
      const filesToDelete = sortedFiles.slice(0, sortedFiles.length - this.options.maxFiles);

      const deletedFiles = [];
      for (const file of filesToDelete) {
        fs.unlinkSync(file.path);
        deletedFiles.push(file.name);
      }

      if (deletedFiles.length > 0) {
        console.log(`🗑️  删除过期日志文件: ${deletedFiles.join(', ')}`);
      }
    }
  }

  /**
   * 设置默认告警规则
   */
  setupDefaultAlertRules() {
    // 错误率告警
    this.addAlertRule('high_error_rate', {
      condition: (stats) => {
        const errorRate = stats.errorCount / stats.totalLogs;
        return errorRate > 0.05; // 错误率超过5%
      },
      message: '错误率过高',
      cooldown: 5 * 60 * 1000 // 5分钟冷却期
    });

    // 响应时间告警
    this.addAlertRule('slow_response', {
      condition: (logEntry) => logEntry.metadata.duration && logEntry.metadata.duration > 5000, // 响应时间超过5秒
      message: '响应时间过长',
      cooldown: 60 * 1000 // 1分钟冷却期
    });

    // 频繁错误告警
    this.addAlertRule('frequent_errors', {
      condition: () => {
        const recentErrors = this.logBuffer
          .filter((log) => log.level === 'ERROR')
          .filter((log) => new Date() - new Date(log.timestamp) < 5 * 60 * 1000); // 5分钟内
        return recentErrors.length > 10; // 5分钟内超过10个错误
      },
      message: '频繁出现错误',
      cooldown: 10 * 60 * 1000 // 10分钟冷却期
    });
  }

  /**
   * 添加告警规则
   */
  addAlertRule(name, rule) {
    this.alertRules.set(name, {
      ...rule,
      lastTriggered: 0
    });
  }

  /**
   * 检查告警规则
   */
  checkAlertRules(logEntry) {
    const now = Date.now();

    for (const [name, rule] of this.alertRules) {
      // 检查冷却期
      if (now - rule.lastTriggered < rule.cooldown) {
        continue;
      }

      // 检查条件
      let shouldAlert = false;

      if (typeof rule.condition === 'function') {
        try {
          shouldAlert = rule.condition(logEntry, this.logStats, this.logBuffer);
        } catch (error) {
          console.error(`告警规则 ${name} 执行失败:`, error);
        }
      }

      if (shouldAlert) {
        this.triggerAlert(name, rule, logEntry);
        rule.lastTriggered = now;
      }
    }
  }

  // TODO: 考虑将此函数拆分为更小的函数 (当前 21 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 21 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 21 行)

  // TODO: 考虑将此函数拆分为更小的函数 (当前 21 行)

  /**
   * 触发告警
   */
  triggerAlert(ruleName, rule, logEntry) {
    const alert = {
      ruleName,
      message: rule.message,
      timestamp: new Date().toISOString(),
      logEntry,
      stats: { ...this.logStats }
    };

    console.warn(`🚨 告警触发: ${alert.message}`);

    // 触发告警事件
    this.emit('alert', alert);

    // 记录告警日志
    this.log('warn', `告警触发: ${alert.message}`, {
      alert: true,
      ruleName,
      originalLog: logEntry
    });
  }

  /**
   * 启动分析任务
   */
  startAnalysisTask() {
    // 每分钟执行一次分析
    this.analysisTimer = setInterval(() => {
      this.performAnalysis();
    }, 60 * 1000);
  }

  /**
   * 执行日志分析
   */
  performAnalysis() {
    const analysis = {
      timestamp: new Date().toISOString(),
      stats: { ...this.logStats },
      trends: this.analyzeTrends(),
      topErrors: this.getTopErrors(),
      slowEndpoints: this.getSlowEndpoints(),
      userActivity: this.analyzeUserActivity()
    };

    // 触发分析事件
    this.emit('analysis', analysis);

    // 保存分析结果
    this.saveAnalysis(analysis);
  }

  /**
   * 分析趋势
   */
  analyzeTrends() {
    const now = new Date();
    const oneHourAgo = new Date(now - 60 * 60 * 1000);

    const recentLogs = this.logBuffer.filter((log) => new Date(log.timestamp) > oneHourAgo);

    const hourlyStats = {
      total: recentLogs.length,
      errors: recentLogs.filter((log) => log.level === 'ERROR').length,
      warnings: recentLogs.filter((log) => log.level === 'WARN').length,
      avgResponseTime: this.calculateAverageResponseTime(recentLogs)
    };

    return {
      hourly: hourlyStats,
      errorRate: hourlyStats.errors / hourlyStats.total || 0,
      trend: this.calculateTrend(recentLogs)
    };
  }

  /**
   * 获取最常见错误
   */
  getTopErrors() {
    const errorLogs = this.logBuffer.filter((log) => log.level === 'ERROR');
    const errorCounts = new Map();

    for (const log of errorLogs) {
      const errorKey = log.message || log.metadata.error?.message || 'Unknown Error';
      errorCounts.set(errorKey, (errorCounts.get(errorKey) || 0) + 1);
    }

    return Array.from(errorCounts.entries())
      .sort((a, b) => b[1] - a[1])
      .slice(0, 10)
      .map(([error, count]) => ({ error, count }));
  }

  /**
   * 获取慢端点
   */
  getSlowEndpoints() {
    const logsWithDuration = this.logBuffer.filter(
      (log) => log.metadata.duration && log.metadata.url
    );

    const endpointStats = new Map();

    for (const log of logsWithDuration) {
      const endpoint = `${log.metadata.method} ${log.metadata.url}`;

      if (!endpointStats.has(endpoint)) {
        endpointStats.set(endpoint, {
          count: 0,
          totalDuration: 0,
          maxDuration: 0
        });
      }

      const stats = endpointStats.get(endpoint);
      stats.count++;
      stats.totalDuration += log.metadata.duration;
      stats.maxDuration = Math.max(stats.maxDuration, log.metadata.duration);
    }

    return Array.from(endpointStats.entries())
      .map(([endpoint, stats]) => ({
        endpoint,
        avgDuration: stats.totalDuration / stats.count,
        maxDuration: stats.maxDuration,
        count: stats.count
      }))
      .sort((a, b) => b.avgDuration - a.avgDuration)
      .slice(0, 10);
  }

  /**
   * 分析用户活动
   */
  analyzeUserActivity() {
    const userLogs = this.logBuffer.filter((log) => log.userId);
    const userStats = new Map();

    for (const log of userLogs) {
      if (!userStats.has(log.userId)) {
        userStats.set(log.userId, {
          requests: 0,
          errors: 0,
          lastActivity: log.timestamp
        });
      }

      const stats = userStats.get(log.userId);
      stats.requests++;

      if (log.level === 'ERROR') {
        stats.errors++;
      }

      if (new Date(log.timestamp) > new Date(stats.lastActivity)) {
        stats.lastActivity = log.timestamp;
      }
    }

    return {
      activeUsers: userStats.size,
      topUsers: Array.from(userStats.entries())
        .sort((a, b) => b[1].requests - a[1].requests)
        .slice(0, 10)
        .map(([userId, stats]) => ({ userId, ...stats }))
    };
  }

  /**
   * 计算平均响应时间
   */
  calculateAverageResponseTime(logs) {
    const logsWithDuration = logs.filter((log) => log.metadata.duration);

    if (logsWithDuration.length === 0) {
      return 0;
    }

    const totalDuration = logsWithDuration.reduce((sum, log) => sum + log.metadata.duration, 0);

    return totalDuration / logsWithDuration.length;
  }

  /**
   * 计算趋势
   */
  calculateTrend(logs) {
    if (logs.length < 2) {
      return 'stable';
    }

    const midpoint = Math.floor(logs.length / 2);
    const firstHalf = logs.slice(0, midpoint);
    const secondHalf = logs.slice(midpoint);

    const firstHalfErrors = firstHalf.filter((log) => log.level === 'ERROR').length;
    const secondHalfErrors = secondHalf.filter((log) => log.level === 'ERROR').length;

    const firstHalfRate = firstHalfErrors / firstHalf.length;
    const secondHalfRate = secondHalfErrors / secondHalf.length;

    if (secondHalfRate > firstHalfRate * 1.2) {
      return 'increasing';
    } else if (secondHalfRate < firstHalfRate * 0.8) {
      return 'decreasing';
    }
    return 'stable';
  }

  /**
   * 保存分析结果
   */
  async saveAnalysis(analysis) {
    const fileName = `analysis-${new Date().toISOString().split('T')[0]}.json`;
    const filePath = path.join(this.options.logDir, fileName);

    let existingData = [];

    try {
      await fs.promises.access(filePath);
      try {
        const content = await fs.promises.readFile(filePath, 'utf8');
        existingData = JSON.parse(content);
      } catch (error) {
        console.error('读取分析文件失败:', error);
      }
    } catch {
      // 文件不存在，使用空数组
    }

    existingData.push(analysis);

    try {
      await fs.promises.writeFile(filePath, JSON.stringify(existingData, null, 2));
    } catch (error) {
      console.error('保存分析结果失败:', error);
    }
  }

  /**
   * 查询日志
   */
  async queryLogs(options = {}) {
    const { level, startTime, endTime, userId, requestId, limit = 100, offset = 0 } = options;

    let results = [...this.logBuffer];

    // 应用过滤条件
    if (level) {
      results = results.filter((log) => log.level === level.toUpperCase());
    }

    if (startTime) {
      results = results.filter((log) => new Date(log.timestamp) >= new Date(startTime));
    }

    if (endTime) {
      results = results.filter((log) => new Date(log.timestamp) <= new Date(endTime));
    }

    if (userId) {
      results = results.filter((log) => log.userId === userId);
    }

    if (requestId) {
      results = results.filter((log) => log.requestId === requestId);
    }

    // 排序（最新的在前）
    results.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));

    // 分页
    const total = results.length;
    results = results.slice(offset, offset + limit);

    return {
      logs: results,
      total,
      offset,
      limit,
      hasMore: offset + limit < total
    };
  }

  /**
   * 获取统计信息
   */
  getStats() {
    return {
      ...this.logStats,
      uptime: Date.now() - this.logStats.startTime.getTime(),
      bufferSize: this.logBuffer.length,
      activeStreams: this.logStreams.size
    };
  }

  /**
   * 创建日志中间件
   */
  createMiddleware() {
    return (req, res, next) => {
      const startTime = Date.now();

      // 记录请求开始
      this.log('info', `${req.method} ${req.url}`, {
        requestId: req.requestId,
        userId: req.user?.id,
        ip: req.ip,
        userAgent: req.get('User-Agent'),
        method: req.method,
        url: req.url,
        query: req.query,
        body: req.method !== 'GET' ? req.body : undefined
      });

      // 监听响应结束
      res.on('finish', () => {
        const duration = Date.now() - startTime;

        this.log('info', `${req.method} ${req.url} - ${res.statusCode}`, {
          requestId: req.requestId,
          userId: req.user?.id,
          ip: req.ip,
          userAgent: req.get('User-Agent'),
          method: req.method,
          url: req.url,
          statusCode: res.statusCode,
          duration,
          responseSize: res.get('Content-Length')
        });
      });

      next();
    };
  }

  /**
   * 关闭日志聚合器
   */
  close() {
    console.log('🔄 关闭日志聚合器...');

    // 清理定时器
    if (this.rotationTimer) {
      clearInterval(this.rotationTimer);
      this.rotationTimer = null;
    }
    if (this.analysisTimer) {
      clearInterval(this.analysisTimer);
      this.analysisTimer = null;
    }

    // 关闭所有流
    for (const [_fileName, stream] of this.logStreams) {
      stream.end();
    }

    this.logStreams.clear();

    console.log('✅ 日志聚合器已关闭');
  }
}

// 创建默认实例
