  // æ ‡å‡†å¯¹æ ‡åˆ†ææ–¹æ³•
  async analyzeStandardCompliance(standard, parkData, parkId) {
    const complianceResult = {
      standard_code: standard.code,
      standard_name: standard.name,
      compliance_score: 0,
      category_results: [],
      overall_status: 'non_compliant'
    };

    let totalScore = 0;
    let totalWeight = 0;

    // éå†æ ‡å‡†è¦æ±‚çš„å„ä¸ªç±»åˆ«
    for (const [reqKey, requirement] of Object.entries(standard.requirements)) {
      const categoryResult = {
        requirement_name: requirement.name,
        categories: [],
        category_score: 0
      };

      let categoryTotalScore = 0;
      let categoryTotalWeight = 0;

      // éå†ç±»åˆ«ä¸‹çš„å…·ä½“è¦æ±‚
      for (const [catKey, category] of Object.entries(requirement.categories)) {
        const indicatorResults = [];
        let indicatorTotalScore = 0;

        // è¯„ä¼°å„é¡¹æŒ‡æ ‡
        for (const indicator of category.indicators) {
          const indicatorResult = await this.evaluateIndicator(
            indicator,
            parkData,
            parkId
          );
          indicatorResults.push(indicatorResult);
          indicatorTotalScore += indicatorResult.score;
        }

        const avgIndicatorScore = indicatorTotalScore / category.indicators.length;

        categoryResult.categories.push({
          category_code: catKey,
          category_name: category.name,
          description: category.description,
          indicator_results: indicatorResults,
          category_score: avgIndicatorScore,
          compliance_status: this.determineComplianceStatus(avgIndicatorScore)
        });

        categoryTotalScore += avgIndicatorScore;
        categoryTotalWeight += 1;
      }

      categoryResult.category_score = categoryTotalScore / categoryTotalWeight;
      complianceResult.category_results.push(categoryResult);

      totalScore += categoryResult.category_score;
      totalWeight += 1;
    }

    complianceResult.compliance_score = totalScore / totalWeight;
    complianceResult.overall_status = this.determineComplianceStatus(complianceResult.compliance_score);

    return complianceResult;
  }

  async evaluateIndicator(indicator, parkData, parkId) {
    try {
      // ä»å›­åŒºæ•°æ®ä¸­è·å–æŒ‡æ ‡å€¼
      const actualValue = await this.getIndicatorValue(indicator, parkData, parkId);

      if (actualValue === null || actualValue === undefined) {
        return {
          indicator_name: indicator.name,
          target_value: indicator.target_value || indicator.min_value,
          actual_value: null,
          score: 0,
          status: 'no_data',
          gap: null
        };
      }

      // è®¡ç®—æŒ‡æ ‡å¾—åˆ†
      const score = this.calculateIndicatorScore(indicator, actualValue);

      // è®¡ç®—å·®è·
      const gap = this.calculateIndicatorGap(indicator, actualValue);

      return {
        indicator_name: indicator.name,
        unit: indicator.unit,
        target_value: indicator.target_value || indicator.min_value,
        actual_value: actualValue,
        score: score,
        status: this.determineIndicatorStatus(score),
        gap: gap,
        improvement_potential: this.calculateImprovementPotential(indicator, actualValue)
      };
    } catch (error) {
      logger.error(`æŒ‡æ ‡è¯„ä¼°å¤±è´¥: ${indicator.name}`, error);
      return {
        indicator_name: indicator.name,
        score: 0,
        status: 'error',
        error: error.message
      };
    }
  }

  async getIndicatorValue(indicator, parkData, parkId) {
    // æ ¹æ®æŒ‡æ ‡åç§°ä»å›­åŒºæ•°æ®ä¸­æå–ç›¸åº”çš„å€¼
    const indicatorMappings = {
      'ç³»ç»Ÿé›†æˆåº¦': () => this.calculateSystemIntegration(parkData),
      'æ™ºèƒ½åŒ–æ°´å¹³': () => this.calculateIntelligenceLevel(parkData),
      'é¢„æµ‹å‡†ç¡®ç‡': () => this.calculatePredictionAccuracy(parkData),
      'å…‰ä¼å‘ç”µæ•ˆç‡': () => this.calculatePVEfficiency(parkData),
      'ç¢³å‡æ’é‡': () => this.calculateCarbonReduction(parkData),
      'å¯å†ç”Ÿèƒ½æºå æ¯”': () => this.calculateRenewableEnergyRatio(parkData),
      'æŠ•èµ„å›æŠ¥ç‡': () => this.calculateROI(parkData),
      'æ ¸ç®—è¦†ç›–ç‡': () => this.calculateAccountingCoverage(parkData),
      'æ•°æ®é‡‡é›†è¦†ç›–ç‡': () => this.calculateDataCollectionCoverage(parkData)
    };

    const calculator = indicatorMappings[indicator.name];
    if (calculator) {
      return await calculator();
    }

    // å¦‚æœæ²¡æœ‰æ‰¾åˆ°å¯¹åº”çš„è®¡ç®—æ–¹æ³•ï¼Œè¿”å›æ¨¡æ‹Ÿå€¼
    return this.generateSimulatedValue(indicator);
  }

  calculateIndicatorScore(indicator, actualValue) {
    // æ ¹æ®æŒ‡æ ‡ç±»å‹è®¡ç®—å¾—åˆ†
    if (indicator.min_value !== undefined) {
      // æœ€å°å€¼ç±»å‹æŒ‡æ ‡
      if (actualValue >= indicator.target_value) {
        return 1.0;
      } else if (actualValue >= indicator.min_value) {
        return 0.6 + 0.4 * (actualValue - indicator.min_value) / (indicator.target_value - indicator.min_value);
      } else {
        return 0.3 * actualValue / indicator.min_value;
      }
    } else if (indicator.max_value !== undefined) {
      // æœ€å¤§å€¼ç±»å‹æŒ‡æ ‡
      if (actualValue <= indicator.target_value) {
        return 1.0;
      } else if (actualValue <= indicator.max_value) {
        return 0.6 + 0.4 * (indicator.max_value - actualValue) / (indicator.max_value - indicator.target_value);
      } else {
        return 0.3 * indicator.max_value / actualValue;
      }
    }

    return 0.5; // é»˜è®¤å¾—åˆ†
  }

  // ç”³æŠ¥ææ–™ç”Ÿæˆæ–¹æ³•
  async autoFillSections(sections, processedData) {
    const filledSections = [];

    for (const section of sections) {
      const filledSection = {
        section_id: section.section_id,
        name: section.name,
        required: section.required,
        fields: [],
        completion_rate: 0
      };

      let filledFields = 0;

      for (const field of section.fields) {
        const filledField = await this.autoFillField(field, processedData);
        filledSection.fields.push(filledField);

        if (filledField.value !== null && filledField.value !== '') {
          filledFields++;
        }
      }

      filledSection.completion_rate = filledFields / section.fields.length;
      filledSections.push(filledSection);
    }

    return filledSections;
  }

  async autoFillField(field, processedData) {
    const filledField = {
      field_id: field.field_id,
      name: field.name,
      type: field.type,
      unit: field.unit,
      required: field.required,
      value: null,
      auto_filled: false,
      confidence: 0,
      data_source: null
    };

    // æ ¹æ®å­—æ®µåç§°è‡ªåŠ¨å¡«å……
    const fieldMappings = {
      'é¡¹ç›®åç§°': () => processedData.project?.name || processedData.park?.name + 'æ™ºèƒ½å…‰ä¼é¡¹ç›®',
      'ç”³æŠ¥å•ä½': () => processedData.organization?.name,
      'é¡¹ç›®åœ°å€': () => processedData.project?.address || processedData.park?.address,
      'é¡¹ç›®è§„æ¨¡': () => processedData.project?.capacity || processedData.energy?.total_capacity,
      'æ€»æŠ•èµ„': () => processedData.project?.investment || this.estimateInvestment(processedData),
      'å‘ç”µé‡': () => processedData.energy?.annual_generation,
      'ç¢³å‡æ’é‡': () => processedData.carbon?.annual_reduction,
      'å›­åŒºåç§°': () => processedData.park?.name,
      'å›­åŒºé¢ç§¯': () => processedData.park?.area,
      'æ€»èƒ½è€—': () => processedData.energy?.total_consumption,
      'å¯å†ç”Ÿèƒ½æºå æ¯”': () => processedData.energy?.renewable_ratio
    };

    const filler = fieldMappings[field.name];
    if (filler) {
      try {
        const value = await filler();
        if (value !== null && value !== undefined) {
          filledField.value = value;
          filledField.auto_filled = true;
          filledField.confidence = 0.9;
          filledField.data_source = 'park_data';
        }
      } catch (error) {
        logger.error(`å­—æ®µè‡ªåŠ¨å¡«å……å¤±è´¥: ${field.name}`, error);
      }
    }

    // å¦‚æœæ— æ³•è‡ªåŠ¨å¡«å……ï¼Œè®¾ç½®é»˜è®¤å€¼æˆ–æç¤º
    if (filledField.value === null) {
      if (field.required) {
        filledField.value = `[è¯·å¡«å†™${field.name}]`;
        filledField.confidence = 0;
      }
    }

    return filledField;
  }

