   */
  calculateMaintenanceScore(maintenanceHistory) {
    if (!maintenanceHistory || maintenanceHistory.length === 0) {
      return 0.5;
    }

    const now = new Date();
    const lastMaintenance = new Date(maintenanceHistory[0].completed_date);
    const daysSinceLastMaintenance = (now - lastMaintenance) / (1000 * 60 * 60 * 24);

    // åŸºäºæœ€åç»´æŠ¤æ—¶é—´è®¡ç®—è¯„åˆ†
    if (daysSinceLastMaintenance < 30) {
      return 1.0;
    }
    if (daysSinceLastMaintenance < 60) {
      return 0.8;
    }
    // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
