  /**
   * è®¡ç®—æ•…éšœæ¦‚ç‡
   */
  calculateFailureProbability(historicalData, maintenanceHistory, currentHealth) {
    let probability = 0;

    // åŸºäºå¥åº·åº¦è®¡ç®—åŸºç¡€æ¦‚ç‡
    probability += (1 - currentHealth.overall_health) * 0.4;

    // åŸºäºå†å²æ•…éšœé¢‘ç‡
    const recentFailures = maintenanceHistory.filter(
      (record) => record.plan_type === 'corrective' || record.plan_type === 'emergency'
    ).length;
    probability += Math.min(recentFailures * 0.1, 0.3);

    // åŸºäºè®¾å¤‡è¿è¡Œæ—¶é—´
    const avgRuntime =
      historicalData.reduce((sum, data) => sum + (data.runtime || 0), 0) / historicalData.length;
    // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
    // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
    // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
    // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
    if (avgRuntime > 8000) {
      // å¹´è¿è¡Œè¶…è¿‡8000å°æ—¶
      probability += 0.2;
