        reduction_potential: await this.calculateReductionPotential(parkId, totalEmissions),
        compliance_status: this.assessComplianceStatus(totalEmissions, 'total_emissions')
      };

      // ç¼“å­˜ç»“æœ
      this.indicatorCache.set(`total_emissions_${parkId}_${timeRange}`, result);

      logger.info(`è®¡ç®—å›­åŒºæ€»ç¢³æ’æ”¾å®Œæˆ: ${parkId}, å€¼: ${totalEmissions} tCO2`);
      return result;
    } catch (error) {
      logger.error('è®¡ç®—å›­åŒºæ€»ç¢³æ’æ”¾å¤±è´¥:', error);
      throw error;
    }
  }

  /**
   * ç”Ÿæˆå›½å®¶æ ‡å‡†æŒ‡æ ‡æŠ¥å‘Š
   * @param {string} parkId - å›­åŒºID
   * @param {string} timeRange - æ—¶é—´èŒƒå›´
