  calculateCarbonEconomicValue(avoidedEmissions) {
    const carbonPrice = 50; // å‡è®¾ç¢³ä»· 50å…ƒ/tCO2
    return (avoidedEmissions * carbonPrice).toFixed(MATH_CONSTANTS.DECIMAL_PLACES);
