class NotificationService {
  constructor() {
    this.transporters = {
      email: this.initializeEmailTransporter(),
      sms: this.initializeSmsClient(),
      discord: this.initializeDiscordWebhook()
    };
    this.preferencesCache = new Map(); // { userId => preferences }
    this.initialize();
