  /**
   * åˆå§‹åŒ–é€šçŸ¥æœåŠ¡
   */
  async initialize() {
    try {
      // é¢„åŠ è½½ç”¨æˆ·é€šçŸ¥åå¥½è®¾ç½®
      await this.loadNotificationPreferences();
      logger.info('é€šçŸ¥æœåŠ¡åˆå§‹åŒ–å®Œæˆ');
    } catch (error) {
      logger.error('é€šçŸ¥æœåŠ¡åˆå§‹åŒ–å¤±è´¥:', error);
    }
  }

  /**
   * åˆå§‹åŒ–é‚®ä»¶ä¼ è¾“å™¨
   */
  initializeEmailTransporter() {
    try {
      // ä»ç¯å¢ƒå˜é‡è·å–é‚®ä»¶é…ç½®
      const { SMTP_HOST, SMTP_PORT, SMTP_USER, SMTP_PASS, SMTP_FROM } = process.env;

      if (!SMTP_HOST || !SMTP_PORT || !SMTP_USER || !SMTP_PASS) {
        logger.warn('é‚®ä»¶æœåŠ¡é…ç½®ä¸å®Œæ•´ï¼Œé‚®ä»¶é€šçŸ¥åŠŸèƒ½å°†ä¸å¯ç”¨');
        return null;
      }

      return nodemailer.createTransport({
        host: SMTP_HOST,
        port: parseInt(SMTP_PORT),
        secure: parseInt(SMTP_PORT) === 465,
        auth: {
          user: SMTP_USER,
          pass: SMTP_PASS
        },
        from: SMTP_FROM || SMTP_USER
      });
    } catch (error) {
      logger.error('åˆå§‹åŒ–é‚®ä»¶ä¼ è¾“å™¨å¤±è´¥:', error);
      return null;
    }
  }

  /**
   * åˆå§‹åŒ–SMSå®¢æˆ·ç«¯
   */
  initializeSmsClient() {
    try {
      // ä»ç¯å¢ƒå˜é‡è·å–Twilioé…ç½®
