      return;
    }

    try {
      const embed = {
        title: `è®¾å¤‡å‘Šè­¦é€šçŸ¥ [${alert.severity.toUpperCase()}]`,
        color: this.getSeverityColor(alert.severity),
        fields: [
          { name: 'è®¾å¤‡ID', value: alert.deviceId, inline: true },
          { name: 'å‘Šè­¦ç±»å‹', value: alert.type, inline: true },
          { name: 'ä¸¥é‡çº§åˆ«', value: alert.severity, inline: true },
          { name: 'å‘Šè­¦ä¿¡æ¯', value: alert.message },
          { name: 'å‘ç”Ÿæ—¶é—´', value: new Date(alert.timestamp).toLocaleString() }
        ],
        timestamp: new Date().toISOString()
      };

      await this.transporters.discord.send({ embeds: [embed] });
      logger.info('å·²å‘é€Discordå‘Šè­¦é€šçŸ¥');
