  /**
   * æ ¹æ®å‘Šè­¦çº§åˆ«è·å–é¢œè‰²ä»£ç 
   * @param {string} severity - å‘Šè­¦çº§åˆ«
   * @returns {number} - é¢œè‰²ä»£ç 
   */
  getSeverityColor(severity) {
    const colors = {
      critical: 0xff0000,
      high: 0xffa500,
      medium: 0xffff00,
      low: 0x00ff00
    };
    return colors[severity] || 0x808080;
  }

  /**
   * æ›´æ–°ç”¨æˆ·å‘Šè­¦çŠ¶æ€
   * @param {string} userId - ç”¨æˆ·ID
   * @param {number} alertId - å‘Šè­¦ID
   * @param {string} status - çŠ¶æ€
   */
  async updateUserAlertStatus(userId, alertId, status) {
    const db = await dbPromise;
    await db.run(
      `UPDATE user_alerts SET status = ?, updated_at = ?
       WHERE user_id = ? AND alert_id = ?`,
      [status, new Date().toISOString(), userId, alertId]
