  /**
   * å‘é€é€šçŸ¥
   * @param {Object} notification - é€šçŸ¥å¯¹è±¡
   * @param {string} notification.type - é€šçŸ¥ç±»å‹ (system, alert, warning, info)
   * @param {string|string[]} notification.recipients - æ¥æ”¶è€…IDæˆ–'admin'è¡¨ç¤ºç®¡ç†å‘˜
   * @param {string} notification.title - é€šçŸ¥æ ‡é¢˜
   * @param {string} notification.message - é€šçŸ¥å†…å®¹
   * @param {string} [notification.alert_id] - å…³è”çš„å‘Šè­¦ID
   * @param {string} [notification.severity] - å‘Šè­¦çº§åˆ« (critical, high, medium, low)
   */
  async sendNotification(notification) {
    try {
      const { type, recipients, title, message, alert_id, severity = 'medium' } = notification;

      // å¤„ç†æ¥æ”¶è€…
      const userIds = await this.resolveRecipients(recipients);
      if (!userIds.length) {
        logger.warn('æœªæ‰¾åˆ°æœ‰æ•ˆçš„é€šçŸ¥æ¥æ”¶è€…');
        return false;
      }

      // ä¸ºæ¯ä¸ªæ¥æ”¶è€…å‘é€é€šçŸ¥
      const results = [];
      for (const userId of userIds) {
        const prefs = await this.getNotificationPreferences(userId);

        // æ£€æŸ¥è¯¥çº§åˆ«æ˜¯å¦åœ¨ç”¨æˆ·çš„è¿‡æ»¤èŒƒå›´å†…
        if (!prefs.severity_filters.includes(severity) && !prefs.severity_filters.includes('all')) {
          logger.debug(`ç”¨æˆ· ${userId} çš„é€šçŸ¥åå¥½è¿‡æ»¤äº† ${severity} çº§åˆ«çš„é€šçŸ¥`);
          continue;
        }

        // ä¸ºæ¯ä¸ªå¯ç”¨çš„æ¸ é“å‘é€é€šçŸ¥
        for (const [channel, config] of Object.entries(prefs.channels)) {
          if (config.enabled) {
            const result = await this.sendViaChannel({
              channel,
              userId,
              contact: config.contact,
              title,
              message,
              alertId: alert_id,
              severity,
              type
            });
            results.push(result);
          }
        }
      }

      return results.every((r) => r.success);
    } catch (error) {
      logger.error('å‘é€é€šçŸ¥å¤±è´¥:', error);
      return false;
    }
  }

  /**
   * è§£ææ¥æ”¶è€…åˆ—è¡¨
   * @param {string|string[]} recipients - æ¥æ”¶è€…IDæˆ–'admin'è¡¨ç¤ºç®¡ç†å‘˜
   */
  async resolveRecipients(recipients) {
    try {
      if (!recipients) {
        return [];
      }

      // æ ‡å‡†åŒ–ä¸ºæ•°ç»„
      const recipientsArray = Array.isArray(recipients) ? recipients : [recipients];
      const userIds = new Set();

      for (const recipient of recipientsArray) {
        if (recipient === 'admin') {
          // è·å–æ‰€æœ‰ç®¡ç†å‘˜
          const db = await dbPromise;
          const admins = await db.raw('SELECT id FROM users WHERE role = "admin"');
          admins.forEach((admin) => userIds.add(admin.id));
        } else if (recipient.includes('@')) {
          // ç›´æ¥æŒ‡å®šé‚®ç®±
          // è¿™é‡Œå¯ä»¥æ·»åŠ é€šè¿‡é‚®ç®±æŸ¥æ‰¾ç”¨æˆ·çš„é€»è¾‘
          logger.warn('ç›´æ¥é‚®ç®±æŒ‡å®šåŠŸèƒ½å°šæœªå®ç°');
        } else {
          // ç”¨æˆ·ID
          userIds.add(recipient);
        }
      }

      return Array.from(userIds);
    } catch (error) {
      logger.error('è§£æé€šçŸ¥æ¥æ”¶è€…å¤±è´¥:', error);
      return [];
    }
  }

  /**
   * é€šè¿‡æŒ‡å®šæ¸ é“å‘é€é€šçŸ¥
   * @param {Object} params - å‘é€å‚æ•°
   */
  async sendViaChannel(params) {
    const { channel, userId, contact, title, message, alertId, severity } = params;
    const result = { channel, userId, success: false, error: null };

    try {
      switch (channel) {
        case 'email':
          await this.sendEmail(contact, title, message);
          break;
        case 'sms':
          await this.sendSms(contact, message);
          break;
        case 'discord':
          await this.sendDiscord(title, message, severity);
          break;
        case 'in_app':
          await this.saveInAppNotification(userId, title, message, alertId, severity);
          break;
        default:
          throw new Error(`ä¸æ”¯æŒçš„é€šçŸ¥æ¸ é“: ${channel}`);
      }

      result.success = true;
      logger.info(`é€šçŸ¥å·²å‘é€ (æ¸ é“: ${channel}, ç”¨æˆ·: ${userId})`);
    } catch (error) {
      result.error = error.message;
      logger.error(`é€šçŸ¥å‘é€å¤±è´¥ (æ¸ é“: ${channel}, ç”¨æˆ·: ${userId}):`, error);
    }

    // è®°å½•é€šçŸ¥å‘é€çŠ¶æ€
    await this.logNotificationAttempt(params, result);
    return result;
  }

  /**
   * å‘é€é‚®ä»¶é€šçŸ¥
   * @param {string} to - æ”¶ä»¶äººé‚®ç®±
   * @param {string} subject - é‚®ä»¶ä¸»é¢˜
   * @param {string} body - é‚®ä»¶å†…å®¹
   */
  async sendEmail(to, subject, body) {
    if (!this.transporters.email) {
      throw new Error('é‚®ä»¶ä¼ è¾“å™¨æœªåˆå§‹åŒ–');
    }

    // æ„å»ºHTMLé‚®ä»¶å†…å®¹
    const htmlBody = `
      <div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto; padding: 20px;">
        <h2 style="color: #2c3e50;">${subject}</h2>
        <div style="background-color: #f8f9fa; padding: 20px; border-radius: 5px;">
          ${body}
        </div>
        <p style="color: #7f8c8d; margin-top: 20px;">è¿™æ˜¯ç³»ç»Ÿè‡ªåŠ¨å‘é€çš„é€šçŸ¥é‚®ä»¶ï¼Œè¯·å‹¿ç›´æ¥å›å¤ã€‚</p>
      </div>
    `;

    // å‘é€é‚®ä»¶
    return await this.transporters.email.sendMail({
      from: process.env.EMAIL_FROM,
      to,
      subject,
      html: htmlBody
    });
  }
}

