    detectZScoreAnomalies(data, parameters) {
        const anomalies = [];
        if (data.length < parameters.min_samples) return anomalies;

        const values = data.map(d => d.value || 0);
        const mean = values.reduce((a, b) => a + b, 0) / values.length;
        const variance = values.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / values.length;
        const stdDev = Math.sqrt(variance);

        data.forEach((record, index) => {
            const zScore = Math.abs((record.value - mean) / stdDev);
            if (zScore > parameters.threshold) {
                anomalies.push({
                    type: 'statistical_outlier',
                    record_id: record.meter_id || index,
                    value: record.value,
                    z_score: zScore,
                    threshold: parameters.threshold,
                    severity: zScore > parameters.threshold * 1.5 ? 'high' : 'medium',
                    description: `ç»Ÿè®¡å¼‚å¸¸å€¼æ£€æµ‹: Z-Score ${zScore.toFixed(2)} è¶…è¿‡é˜ˆå€¼ ${parameters.threshold}`
                });
            }
        });

        return anomalies;
    }

    detectTrendAnomalies(data, parameters) {
        const anomalies = [];
        if (data.length < parameters.window_size) return anomalies;

        for (let i = parameters.window_size; i < data.length; i++) {
            const window = data.slice(i - parameters.window_size, i);
            const windowMean = window.reduce((sum, d) => sum + d.value, 0) / window.length;
            const currentValue = data[i].value;

            const deviation = Math.abs(currentValue - windowMean) / windowMean;
            if (deviation > parameters.deviation_threshold) {
                anomalies.push({
                    type: 'trend_anomaly',
                    record_id: data[i].meter_id || i,
                    value: currentValue,
                    expected_value: windowMean,
                    deviation: deviation,
                    threshold: parameters.deviation_threshold,
                    severity: deviation > parameters.deviation_threshold * 2 ? 'high' : 'medium',
                    description: `è¶‹åŠ¿å¼‚å¸¸æ£€æµ‹: åå·® ${(deviation * 100).toFixed(2)}% è¶…è¿‡é˜ˆå€¼ ${(parameters.deviation_threshold * 100).toFixed(2)}%`
                });
            }
        }

        return anomalies;
    }

    detectPatternAnomalies(data, parameters) {
        const anomalies = [];
        // ç®€åŒ–çš„æ¨¡å¼å¼‚å¸¸æ£€æµ‹
        const values = data.map(d => d.value || 0);
        const mean = values.reduce((a, b) => a + b, 0) / values.length;
        const threshold = mean * 2; // ç®€åŒ–é˜ˆå€¼

        data.forEach((record, index) => {
            if (record.value > threshold) {
                anomalies.push({
                    type: 'pattern_anomaly',
                    record_id: record.meter_id || index,
                    value: record.value,
                    threshold: threshold,
                    severity: 'medium',
                    description: `æ¨¡å¼å¼‚å¸¸æ£€æµ‹: å€¼ ${record.value} è¶…è¿‡æ¨¡å¼é˜ˆå€¼ ${threshold.toFixed(2)}`
                });
            }
        });

        return anomalies;
    }

    detectRuleViolations(data, parameters) {
        const anomalies = [];

        data.forEach((record, index) => {
            // æ£€æŸ¥ä¸šåŠ¡è§„åˆ™è¿å
            if (record.value <= 0) {
                anomalies.push({
                    type: 'rule_violation',
                    record_id: record.meter_id || index,
                    value: record.value,
                    rule: 'value_must_be_positive',
