            .reduce((sum, detector) => sum + detector.detection_count, 0);
        const totalRecords = 10000; // æ¨¡æ‹Ÿæ€»è®°å½•æ•°
        return totalRecords > 0 ? totalDetections / totalRecords : 0;
    }

