        // åŠŸç‡é¢„æµ‹
        power_forecast: powerForecast,

        // æŠ•æ ‡ç­–ç•¥
        bidding_strategy: biddingStrategy,

        // æŠ•æ ‡æäº¤
        bid_submission: bidSubmission,

        // å¸‚åœºå‡ºæ¸…
        market_clearing: marketClearing,

        // äº¤æ˜“åˆ†æ
        trading_analysis: tradingAnalysis,

        // è´¢åŠ¡ç»“æœ
        financial_results: {
