        // é£é™©æŒ‡æ ‡
        risk_metrics: {
          price_risk: tradingAnalysis.price_risk,
          volume_risk: tradingAnalysis.volume_risk,
          execution_risk: tradingAnalysis.execution_risk
        }
      };

      // å­˜å‚¨äº¤æ˜“ç»“æœ
      this.marketPositions.set(tradingId, result);

      logger.info(`ç”µåŠ›å¸‚åœºäº¤æ˜“å®Œæˆ: ${tradingId}, æ”¶ç›Š: ${result.financial_results.profit}ä¸‡å…ƒ`);
      this.emit('trading_completed', result);

      return result;
    } catch (error) {
      logger.error('æ‰§è¡Œç”µåŠ›å¸‚åœºäº¤æ˜“å¤±è´¥:', error);
      throw error;
    }
  }

