// è·å–ç”µä»·æ•°æ®
async function getElectricityPrices(startTime, endTime) {
  try {
    // å®é™…åº”ç”¨ä¸­åº”ä»APIè·å–å®æ—¶ç”µä»·æ•°æ®
    // æ­¤å¤„ä½¿ç”¨æ¨¡æ‹Ÿæ•°æ®
    const prices = [];
    const start = new Date(startTime);
    const end = new Date(endTime);

    // ç”Ÿæˆæ¯å°æ—¶ç”µä»· (0.5-1.5å…ƒ/åº¦)
    for (let d = new Date(start); d <= new Date(end); d.setHours(d.getHours() + 1)) {
      const hour = d.getHours();
      // æ¨¡æ‹Ÿå³°è°·ç”µä»·: å³°æ—¶(8-22ç‚¹)é«˜ä»·ï¼Œè°·æ—¶(22-8ç‚¹)ä½ä»·
      const price =
        hour >= 8 && hour <= 22
          ? 1.2 + Math.random() * 0.3 // å³°æ—¶: 1.2-1.5å…ƒ
          : 0.5 + Math.random() * 0.3; // è°·æ—¶: 0.5-0.8å…ƒ

      prices.push({
        timestamp: d.toISOString(),
        price: parseFloat(price.toFixed(2))
      });
    }
    return prices;
  } catch (error) {
    console.error('è·å–ç”µä»·æ•°æ®å¤±è´¥:', error.message);
    return [];
  }
}

// è·å–ç¢³æ’æ”¾å› å­æ•°æ®
async function getCarbonIntensity(startTime, endTime) {
  try {
    // å®é™…åº”ç”¨ä¸­åº”ä»APIè·å–å®æ—¶ç¢³æ’æ”¾å› å­æ•°æ®
    // æ­¤å¤„ä½¿ç”¨æ¨¡æ‹Ÿæ•°æ®
    const intensities = [];
    const start = new Date(startTime);
    const end = new Date(endTime);

    // ç”Ÿæˆæ¯å°æ—¶ç¢³æ’æ”¾å› å­ (300-800 gCO2/kWh)
    for (let d = new Date(start); d <= new Date(end); d.setHours(d.getHours() + 1)) {
      const hour = d.getHours();
      // æ¨¡æ‹Ÿç¢³æ’æ”¾å› å­å˜åŒ–
      const intensity =
        500 + Math.sin((hour / 24) * Math.PI * 2) * 150 + (Math.random() * 100 - 50);

      intensities.push({
        timestamp: d.toISOString(),
        intensity: parseFloat(intensity.toFixed(0))
      });
    }
    return intensities;
  } catch (error) {
    console.error('è·å–ç¢³æ’æ”¾å› å­æ•°æ®å¤±è´¥:', error.message);
    return [];
  }
}

// è®¡ç®—å……æ”¾ç”µæˆæœ¬æ•ˆç›Š
function calculateCostBenefit(soc, price, carbonIntensity, currentState) {
  // ç”µæ± å‚æ•°
  const batteryCapacity = 1000; // ç”µæ± å®¹é‡ kWh
  const chargeEfficiency = 0.9; // å……ç”µæ•ˆç‡
  const dischargeEfficiency = 0.9; // æ”¾ç”µæ•ˆç‡
  const carbonPrice = 0.05; // ç¢³ä»· å…ƒ/gCO2

  // è®¡ç®—æ½œåœ¨æ”¶ç›Š
  if (currentState === 'low' || soc < 0.3) {
    // éœ€è¦å……ç”µæ—¶ï¼Œè®¡ç®—æˆæœ¬
    const requiredEnergy = batteryCapacity * (0.8 - soc);
    const actualEnergyNeeded = requiredEnergy / chargeEfficiency;
    const cost = actualEnergyNeeded * price;
    const carbonEmission = actualEnergyNeeded * carbonIntensity;
    const carbonCost = carbonEmission * carbonPrice;
    const totalCost = cost + carbonCost;

    return {
      action: 'charge',
      cost: totalCost,
      carbonEmission,
      benefitScore: price < 0.7 ? 8 : price < 0.9 ? 5 : 2 // ä»·æ ¼è¶Šä½ï¼Œå……ç”µæ”¶ç›Šè¶Šé«˜
    };
  } else if (currentState === 'high' || soc > 0.7) {
    // éœ€è¦æ”¾ç”µæ—¶ï¼Œè®¡ç®—æ”¶ç›Š
    const availableEnergy = batteryCapacity * (soc - 0.2);
    const actualEnergyAvailable = availableEnergy * dischargeEfficiency;
    const revenue = actualEnergyAvailable * price;
    const carbonReduction = actualEnergyAvailable * carbonIntensity;
    const carbonBenefit = carbonReduction * carbonPrice;
    const totalBenefit = revenue + carbonBenefit;

    return {
      action: 'discharge',
      benefit: totalBenefit,
      carbonReduction,
      benefitScore: price > 1.2 ? 9 : price > 1.0 ? 6 : 3 // ä»·æ ¼è¶Šé«˜ï¼Œæ”¾ç”µæ”¶ç›Šè¶Šé«˜
    };
  }

  return { action: 'none', benefitScore: 0 };
}

// ç”Ÿæˆä¼˜åŒ–ç­–ç•¥
async function _generateOptimizationStrategies(
  predictions,
  interval,
  buildingId,
  startTime,
  endTime
) {
  if (!predictions || predictions.length === 0) {
    throw new Error('æ— æ•ˆçš„é¢„æµ‹æ•°æ®');
  }

  if (!interval || typeof interval !== 'string') {
    interval = '1h'; // é»˜è®¤é—´éš”ä¸º1å°æ—¶
  }

  // è·å–ç”µä»·å’Œç¢³æ’æ”¾å› å­æ•°æ®
  const [electricityPrices, carbonIntensities] = await Promise.all([
    getElectricityPrices(startTime, endTime),
    getCarbonIntensity(startTime, endTime)
  ]);

  const strategies = [];
  let currentState = 'normal';

  try {
    predictions.forEach((soc, index) => {
      if (typeof soc !== 'number') {
        throw new Error(`æ— æ•ˆçš„é¢„æµ‹å€¼åœ¨ç´¢å¼• ${index}: ${soc}`);
      }

      const timestamp = calculatePredictionTime(index, interval);
      // æŸ¥æ‰¾å¯¹åº”æ—¶é—´çš„ç”µä»·å’Œç¢³æ’æ”¾å› å­
      const priceData = electricityPrices.find((p) => p.timestamp === timestamp);
      const carbonData = carbonIntensities.find((c) => c.timestamp === timestamp);
      const price = priceData ? priceData.price : 0.8;
      const carbonIntensity = carbonData ? carbonData.intensity : 500;

      // è®¡ç®—æˆæœ¬æ•ˆç›Š
      const costBenefit = calculateCostBenefit(soc, price, carbonIntensity, currentState);

      // æ ¹æ®SOCå€¼å’Œæˆæœ¬æ•ˆç›Šç”Ÿæˆä¼˜åŒ–ç­–ç•¥
      if (soc < 0.2 || (soc < 0.3 && costBenefit.benefitScore > 7)) {
        strategies.push({
          timestamp,
          strategy: 'charge',
          description: `ç”µæ± ${buildingId}ç”µé‡è¿‡ä½(${soc.toFixed(2)}), å»ºè®®å……ç”µã€‚å½“å‰ç”µä»·: ${price.toFixed(2)}å…ƒ/åº¦, ç¢³æ’æ”¾å› å­: ${carbonIntensity}gCO2/kWh`,
          priority: costBenefit.benefitScore,
          economicImpact: `é¢„è®¡æˆæœ¬: ${costBenefit.cost ? costBenefit.cost.toFixed(2) : 'N/A'}å…ƒ`,
          carbonImpact: `é¢„è®¡ç¢³æ’æ”¾: ${costBenefit.carbonEmission ? costBenefit.carbonEmission.toFixed(2) : 'N/A'}gCO2`
        });
        currentState = 'low';
      } else if (soc > 0.8 || (soc > 0.7 && costBenefit.benefitScore > 7)) {
        strategies.push({
          timestamp,
          strategy: 'discharge',
          description: `ç”µæ± ${buildingId}ç”µé‡è¿‡é«˜(${soc.toFixed(2)}), å»ºè®®æ”¾ç”µã€‚å½“å‰ç”µä»·: ${price.toFixed(2)}å…ƒ/åº¦, ç¢³æ’æ”¾å› å­: ${carbonIntensity}gCO2/kWh`,
          priority: costBenefit.benefitScore,
          economicImpact: `é¢„è®¡æ”¶ç›Š: ${costBenefit.benefit ? costBenefit.benefit.toFixed(2) : 'N/A'}å…ƒ`,
          carbonImpact: `é¢„è®¡ç¢³å‡æ’: ${costBenefit.carbonReduction ? costBenefit.carbonReduction.toFixed(2) : 'N/A'}gCO2`
        });
        currentState = 'high';
      } else if (currentState === 'low' && soc > 0.5) {
        strategies.push({
          timestamp,
          strategy: 'stop_charge',
          description: `ç”µæ± ${buildingId}ç”µé‡å·²æ¢å¤(${soc.toFixed(2)}), åœæ­¢å……ç”µ`,
          priority: 2,
          economicImpact: 'æ— é¢å¤–æˆæœ¬'
        });
        currentState = 'normal';
      } else if (currentState === 'high' && soc < 0.6) {
        strategies.push({
          timestamp,
          strategy: 'stop_discharge',
          description: `ç”µæ± ${buildingId}ç”µé‡å·²æ¢å¤(${soc.toFixed(2)}), åœæ­¢æ”¾ç”µ`,
          priority: 2,
          economicImpact: 'æ— é¢å¤–æ”¶ç›Š'
        });
        currentState = 'normal';
      } else if (index % 24 === 0) {
        strategies.push({
          timestamp,
          strategy: 'maintenance',
          description: `ç”µæ± ${buildingId}æ¯æ—¥çŠ¶æ€ç»´æŠ¤æ£€æŸ¥`,
          priority: 3
        });
      }
    });
  } catch (error) {
    console.error(`ç”Ÿæˆä¼˜åŒ–ç­–ç•¥å¤±è´¥: ${error.message}`);
    throw error;
  }

  // æŒ‰ä¼˜å…ˆçº§æ’åº
  return strategies.sort((a, b) => a.priority - b.priority);
}

// å‡†å¤‡é¢„æµ‹è¾“å…¥æ•°æ®
function _preparePredictionInput(data, _windowSize = 24) {
  if (!data || data.length === 0) {
    throw new Error('æ— æ•ˆçš„å†å²æ•°æ®');
  }

  try {
    // ç¡®ä¿æ•°æ®æŒ‰æ—¶é—´æ’åº
    const sortedData = data.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

    // æå–SOCå€¼å¹¶è¿›è¡Œå½’ä¸€åŒ–
    const socValues = sortedData.map((d) => d.soc);

    // å¦‚æœæ•°æ®ä¸è¶³ä¸€ä¸ªçª—å£ï¼Œåˆ™ä½¿ç”¨æ‰€æœ‰å¯ç”¨æ•°æ®
    const windowSize = 24; // ä½¿ç”¨æ˜ç¡®çš„çª—å£å¤§å°
    const inputData = socValues.slice(-windowSize);

    // æ•°æ®å½’ä¸€åŒ–ï¼ˆç¡®ä¿å€¼åœ¨0-1ä¹‹é—´ï¼‰
    const normalized = normalizeData(inputData);

    // åˆ›å»ºå¼ é‡
    const tensor = tf.tensor2d([normalized], [1, normalized.length]);

    // éªŒè¯å¼ é‡å½¢çŠ¶
    if (tensor.shape[1] !== windowSize) {
      throw new Error(`è¾“å…¥æ•°æ®å½¢çŠ¶ä¸åŒ¹é…: éœ€è¦${windowSize}ä¸ªæ—¶é—´æ­¥ï¼Œä½†å¾—åˆ°${tensor.shape[1]}ä¸ª`);
    }

    return tensor;
  } catch (error) {
    console.error(`å‡†å¤‡é¢„æµ‹è¾“å…¥å¤±è´¥: ${error.message}`);
    throw error;
  }
}

// æ•°æ®å½’ä¸€åŒ–
function normalizeData(data) {
  const max = Math.max(...data);
  return data.map((value) => value / max);
}

// è®¡ç®—é¢„æµ‹æ—¶é—´
function calculatePredictionTime(baseTime, index, interval) {
  const intervalMap = {
    '1h': 60 * 60 * 1000,
    '1d': 24 * 60 * 60 * 1000,
    '1w': 7 * 24 * 60 * 60 * 1000
  };

  // å¦‚æœæ²¡æœ‰æä¾›åŸºå‡†æ—¶é—´ï¼Œä½¿ç”¨å½“å‰æ—¶é—´
  const baseTimestamp = baseTime ? new Date(baseTime).getTime() : Date.now();

  if (isNaN(baseTimestamp)) {
    throw new Error(`æ— æ•ˆçš„åŸºå‡†æ—¶é—´: ${baseTime}`);
  }

  const intervalMs = intervalMap[interval] || intervalMap['1h'];
  const ms = baseTimestamp + (index + 1) * intervalMs;

  return new Date(ms).toISOString();
}

