import NodeCache from 'node-cache';
import logger from '../../shared/utils/logger.js';

// ç¼“å­˜é…ç½®
const CACHE_CONFIG = {
  stdTTL: 300, // é»˜è®¤ç¼“å­˜è¿‡æœŸæ—¶é—´ï¼ˆç§’ï¼‰
  checkperiod: 60, // å®šæœŸæ£€æŸ¥è¿‡æœŸç¼“å­˜çš„æ—¶é—´é—´éš”ï¼ˆç§’ï¼‰
  useClones: false // ä¸å…‹éš†å¯¹è±¡ï¼Œæé«˜æ€§èƒ½ï¼ˆæ³¨æ„ï¼šå¯èƒ½å¯¼è‡´ç¼“å­˜å¯¹è±¡è¢«å¤–éƒ¨ä¿®æ”¹ï¼‰
};

// åˆ›å»ºç¼“å­˜å®ä¾‹
const cache = new NodeCache(CACHE_CONFIG);

/**
 * ç¼“å­˜æœåŠ¡ç±»
 * æä¾›åŸºç¡€çš„ç¼“å­˜æ“ä½œåŠŸèƒ½
 */
class CacheService {
  constructor() {
    // ç›‘å¬ç¼“å­˜äº‹ä»¶
    cache.on('expired', (key, _value) => {
      logger.debug(`ç¼“å­˜è¿‡æœŸ: ${key}`);
    });

    cache.on('flush', () => {
      logger.info('ç¼“å­˜å·²æ¸…ç©º');
    });
  }

  /**
   * è®¾ç½®ç¼“å­˜
   * @param {string} key - ç¼“å­˜é”®
   * @param {any} value - ç¼“å­˜å€¼
   * @param {number} [ttl] - è¿‡æœŸæ—¶é—´ï¼ˆç§’ï¼‰ï¼Œé»˜è®¤ä½¿ç”¨å…¨å±€é…ç½®
   * @returns {boolean} - æ˜¯å¦è®¾ç½®æˆåŠŸ
   */
  set(key, value, ttl = CACHE_CONFIG.stdTTL) {
    if (!key) {
      logger.error('ç¼“å­˜é”®ä¸èƒ½ä¸ºç©º');
      return false;
    }

    try {
      const success = cache.set(key, value, ttl);
      if (success) {
        logger.debug(`ç¼“å­˜è®¾ç½®æˆåŠŸ: ${key}, TTL: ${ttl}ç§’`);
      } else {
        logger.warn(`ç¼“å­˜è®¾ç½®å¤±è´¥: ${key}`);
      }
      return success;
    } catch (error) {
      logger.error(`ç¼“å­˜è®¾ç½®é”™è¯¯: ${key}`, error);
      return false;
    }
  }

  /**
   * è·å–ç¼“å­˜
   * @param {string} key - ç¼“å­˜é”®
   * @returns {any} - ç¼“å­˜å€¼ï¼Œè‹¥ä¸å­˜åœ¨åˆ™è¿”å›undefined
   */
  get(key) {
    if (!key) {
      logger.error('ç¼“å­˜é”®ä¸èƒ½ä¸ºç©º');
      return undefined;
    }

    try {
      const value = cache.get(key);
      if (value !== undefined) {
        logger.debug(`ç¼“å­˜å‘½ä¸­: ${key}`);
      } else {
        logger.debug(`ç¼“å­˜æœªå‘½ä¸­: ${key}`);
      }
      return value;
    } catch (error) {
      logger.error(`ç¼“å­˜è·å–é”™è¯¯: ${key}`, error);
      return undefined;
    }
  }

  /**
   * åˆ é™¤ç¼“å­˜
   * @param {string} key - ç¼“å­˜é”®
   * @returns {number} - è¢«åˆ é™¤çš„ç¼“å­˜æ•°é‡
   */
  del(key) {
    if (!key) {
      logger.error('ç¼“å­˜é”®ä¸èƒ½ä¸ºç©º');
      return 0;
    }

    try {
      const count = cache.del(key);
      if (count > 0) {
        logger.debug(`ç¼“å­˜åˆ é™¤æˆåŠŸ: ${key}`);
      } else {
        logger.debug(`ç¼“å­˜åˆ é™¤å¤±è´¥ï¼Œé”®ä¸å­˜åœ¨: ${key}`);
      }
      return count;
    } catch (error) {
      logger.error(`ç¼“å­˜åˆ é™¤é”™è¯¯: ${key}`, error);
      return 0;
    }
  }

  /**
   * æ‰¹é‡åˆ é™¤ç¼“å­˜
   * @param {string[]} keys - ç¼“å­˜é”®æ•°ç»„
   * @returns {number} - è¢«åˆ é™¤çš„ç¼“å­˜æ•°é‡
   */
  delMulti(keys) {
    if (!Array.isArray(keys) || keys.length === 0) {
      logger.error('ç¼“å­˜é”®æ•°ç»„ä¸èƒ½ä¸ºç©º');
      return 0;
    }

    try {
      const count = cache.del(keys);
      logger.debug(`æ‰¹é‡åˆ é™¤ç¼“å­˜æˆåŠŸï¼Œå…±åˆ é™¤ ${count} ä¸ªç¼“å­˜`);
      return count;
    } catch (error) {
      logger.error('æ‰¹é‡åˆ é™¤ç¼“å­˜é”™è¯¯', error);
      return 0;
    }
  }

  /**
   * æ¸…ç©ºæ‰€æœ‰ç¼“å­˜
   * @returns {void}
   */
  flushAll() {
    try {
      cache.flushAll();
      logger.info('æ‰€æœ‰ç¼“å­˜å·²æ¸…ç©º');
    } catch (error) {
      logger.error('æ¸…ç©ºç¼“å­˜é”™è¯¯', error);
    }
  }

  /**
   * è·å–ç¼“å­˜é”®çš„å‰©ä½™ç”Ÿå­˜æ—¶é—´
   * @param {string} key - ç¼“å­˜é”®
   * @returns {number} - å‰©ä½™æ—¶é—´ï¼ˆç§’ï¼‰ï¼Œ-1è¡¨ç¤ºæ°¸ä¸è¿‡æœŸï¼Œ-2è¡¨ç¤ºé”®ä¸å­˜åœ¨
   */
  getTtl(key) {
    if (!key) {
      logger.error('ç¼“å­˜é”®ä¸èƒ½ä¸ºç©º');
      return -2;
    }

    try {
      return cache.getTtl(key);
    } catch (error) {
      logger.error(`è·å–ç¼“å­˜TTLé”™è¯¯: ${key}`, error);
      return -2;
    }
  }

  /**
   * è®¾ç½®ç¼“å­˜é”®çš„ç”Ÿå­˜æ—¶é—´
   * @param {string} key - ç¼“å­˜é”®
   * @param {number} ttl - ç”Ÿå­˜æ—¶é—´ï¼ˆç§’ï¼‰
   * @returns {boolean} - æ˜¯å¦è®¾ç½®æˆåŠŸ
   */
  setTtl(key, ttl) {
    if (!key || typeof ttl !== 'number' || ttl <= 0) {
      logger.error('ç¼“å­˜é”®å’Œæœ‰æ•ˆçš„TTLä¸èƒ½ä¸ºç©º');
      return false;
    }

    try {
      return cache.ttl(key, ttl);
    } catch (error) {
      logger.error(`è®¾ç½®ç¼“å­˜TTLé”™è¯¯: ${key}`, error);
      return false;
    }
  }

  /**
   * è·å–ç¼“å­˜ç»Ÿè®¡ä¿¡æ¯
   * @returns {object} - ç¼“å­˜ç»Ÿè®¡æ•°æ®
   */
  getStats() {
    try {
      return cache.getStats();
    } catch (error) {
      logger.error('è·å–ç¼“å­˜ç»Ÿè®¡ä¿¡æ¯é”™è¯¯', error);
      return null;
    }
  }

  /**
   * ç¼“å­˜è£…é¥°å™¨ - ç”¨äºè£…é¥°éœ€è¦ç¼“å­˜çš„å‡½æ•°
   * @param {number} ttl - ç¼“å­˜è¿‡æœŸæ—¶é—´ï¼ˆç§’ï¼‰
   * @returns {Function} - è£…é¥°å™¨å‡½æ•°
   */
  cacheDecorator(ttl = CACHE_CONFIG.stdTTL) {
    return (target, propertyKey, descriptor) => {
      const originalMethod = descriptor.value;

      descriptor.value = async function (...args) {
        // ç”Ÿæˆç¼“å­˜é”®ï¼ˆç±»å+æ–¹æ³•å+å‚æ•°å“ˆå¸Œï¼‰
        const key = `${target.constructor.name}_${propertyKey}_${JSON.stringify(args)}`;

        // å°è¯•ä»ç¼“å­˜è·å–
        const cachedResult = this.get(key);
        if (cachedResult !== undefined) {
          return cachedResult;
        }

        // è°ƒç”¨åŸå§‹æ–¹æ³•
        const result = await originalMethod.apply(this, args);

        // è®¾ç½®ç¼“å­˜
        this.set(key, result, ttl);

        return result;
      };

      return descriptor;
    };
  }
}

