// å®šä¹‰èƒ½æºç±»å‹å¸¸é‡
export const ENERGY_TYPES = {
  ELECTRICITY: 'electricity', // ç”µåŠ›
  DIESEL: 'diesel', // æŸ´æ²¹
  NATURAL_GAS: 'natural_gas', // å¤©ç„¶æ°”
  SOLAR: 'solar' // å…‰ä¼
};

// å®šä¹‰èƒ½æºæ¶ˆè€—æ•°æ®æ ·æœ¬
export const energyConsumptionData = [
  {
    buildingId: 'B1',
    timestamp: new Date('2023-06-01T00:00:00Z'),
    energyType: ENERGY_TYPES.ELECTRICITY,
    consumption: 1500, // åƒç“¦æ—¶
    carbonEmission: 0.5 // å¨CO2
  },
  {
    buildingId: 'B1',
    timestamp: new Date('2023-06-01T00:00:00Z'),
    energyType: ENERGY_TYPES.SOLAR,
    consumption: 200, // åƒç“¦æ—¶
    carbonEmission: 0.0 // å¨CO2
  }
];

// è®¡ç®—å•ä¸ªæ•°æ®ç‚¹çš„ç¢³æ’æ”¾é‡
function calculateEmission(dataPoint) {
  // ä½¿ç”¨é»˜è®¤ç¢³æ’æ”¾å› å­ï¼ˆé¿å…æ•°æ®åº“æŸ¥è¯¢ï¼‰
  const defaultCarbonFactors = {
    electricity: 0.5968, // ç”µåŠ›ç¢³æ’æ”¾å› å­ (kg CO2/kWh)
    diesel: 2.68, // æŸ´æ²¹ç¢³æ’æ”¾å› å­ (kg CO2/L)
    natural_gas: 2.03, // å¤©ç„¶æ°”ç¢³æ’æ”¾å› å­ (kg CO2/mÂ³)
    solar: 0.0 // å…‰ä¼ç¢³æ’æ”¾å› å­ (kg CO2/kWh)
  };

  const carbonFactor = defaultCarbonFactors[dataPoint.energyType] || 0.5968;
  return dataPoint.consumption * carbonFactor;
}

// éªŒè¯æ—¶é—´èŒƒå›´æœ‰æ•ˆæ€§
function validateTimeRange(start_time, end_time) {
  // éªŒè¯å¼€å§‹æ—¶é—´æ˜¯å¦æ—©äºç»“æŸæ—¶é—´
  if (start_time >= end_time) {
    throw new Error('å¼€å§‹æ—¶é—´å¿…é¡»æ—©äºç»“æŸæ—¶é—´');
  }

  // éªŒè¯æ—¶é—´èŒƒå›´æ˜¯å¦è¶…è¿‡é™åˆ¶ï¼ˆä¾‹å¦‚ä¸èƒ½è¶…è¿‡1å¹´ï¼‰
  const oneYearInMs = 365 * 24 * 60 * 60 * 1000;
  if (end_time - start_time > oneYearInMs) {
    throw new Error('æ—¶é—´èŒƒå›´ä¸èƒ½è¶…è¿‡ä¸€å¹´');
  }
}

// è®¡ç®—æŒ‡å®šæ—¶é—´èŒƒå›´å†…çš„æ€»ç¢³æ’æ”¾é‡
function calculateTotalEmissions(startTime, endTime) {
  // è¿‡æ»¤æŒ‡å®šæ—¶é—´èŒƒå›´å†…çš„èƒ½æºæ¶ˆè€—æ•°æ®
  const filteredData = energyConsumptionData.filter(
    (dataPoint) => dataPoint.timestamp >= startTime && dataPoint.timestamp <= endTime
  );

  // è®¡ç®—æ€»ç¢³æ’æ”¾é‡
  let totalEmissions = 0;

  filteredData.forEach((dataPoint) => {
    totalEmissions += calculateEmission(dataPoint);
  });

  // è¿”å›æ€»ç¢³æ’æ”¾é‡
  return totalEmissions;
}

// è®¡ç®—è¯¦ç»†çš„ç¢³æ’æ”¾æ˜ç»†
function calculateEmissionDetails(startTime, endTime) {
  // è¿‡æ»¤æŒ‡å®šæ—¶é—´èŒƒå›´å†…çš„èƒ½æºæ¶ˆè€—æ•°æ®
  const filteredData = energyConsumptionData.filter(
    (dataPoint) => dataPoint.timestamp >= startTime && dataPoint.timestamp <= endTime
  );

  // æŒ‰èƒ½æºç±»å‹åˆ†ç±»ç»Ÿè®¡
  const emissionDetails = {};
  let totalEmissions = 0;

  filteredData.forEach((dataPoint) => {
    const emission = calculateEmission(dataPoint);
    if (!emissionDetails[dataPoint.energyType]) {
      emissionDetails[dataPoint.energyType] = {
        energyType: dataPoint.energyType,
        totalConsumption: 0,
        totalEmission: 0
      };
    }

    emissionDetails[dataPoint.energyType].totalConsumption += dataPoint.consumption;
    emissionDetails[dataPoint.energyType].totalEmission += emission;
    totalEmissions += emission;
  });

  // è¿”å›æ±‡æ€»ç»“æœ
  return {
    total: totalEmissions,
    details: Object.values(emissionDetails),
    timeRange: {
      start: startTime,
      end: endTime
    }
  };
}

// åˆ›å»ºç¢³æ’æ”¾è®¡ç®—è·¯ç”±
function setupCarbonRoutes(app, authenticateToken) {
  // ç¢³æ’æ”¾è®¡ç®—æ¥å£
  app.get('/carbon/emissions', authenticateToken(), async (req, res) => {
    try {
      // è·å–æŸ¥è¯¢å‚æ•°
      const { buildingId, startDate, endDate } = req.query;

      if (!buildingId || !startDate || !endDate) {
        res.status(400).json({ error: 'ç¼ºå°‘å¿…è¦å‚æ•°' });
        return;
      }

      // éªŒè¯æ—¶é—´èŒƒå›´
      validateTimeRange(new Date(startDate), new Date(endDate));

      // è°ƒç”¨ç¢³æ’æ”¾è®¡ç®—æ¨¡å‹
      const emissions = calculateEmissionDetails(new Date(startDate), new Date(endDate));

      // è¿”å›è®¡ç®—ç»“æœ
      res.json(emissions);
    } catch (error) {
      console.error('ç¢³æ’æ”¾è®¡ç®—é”™è¯¯:', error);
      res.status(500).json({ error: 'ç¢³æ’æ”¾è®¡ç®—å¤±è´¥' });
    }
  });

  // è¿”å›appå¯¹è±¡ä»¥ä¾¿é“¾å¼è°ƒç”¨
  return app;
}

