function createModel(inputShape) {
  const model = tf.sequential();

  model.add(
    tf.layers.lstm({
      units: 64,
      inputShape,
      returnSequences: false
    })
  );

  model.add(tf.layers.dense({ units: 32, activation: 'relu' }));
  model.add(tf.layers.dense({ units: 1 }));

  model.compile({
    optimizer: tf.train.adam(0.001),
    loss: 'meanSquaredError'
  });

  return model;
}

// è®­ç»ƒæ¨¡å‹
async function _trainModel() {
  try {
    // å‡†å¤‡è®­ç»ƒæ•°æ®
    const { xs, ys } = prepareTrainingData(historicalEnergyData);

    // åˆ›å»ºæ¨¡å‹
    const model = createModel([xs.shape[1], 1]);

    // è®­ç»ƒæ¨¡å‹
    await model.fit(xs, ys, {
      epochs: 100,
      batchSize: 32,
      shuffle: true,
      callbacks: {
        onEpochEnd: (epoch, logs) => {
          console.log(`Epoch ${epoch + 1}: Loss = ${logs.loss}`);
        }
      }
    });

    // ä¿å­˜æ¨¡å‹
    await model.save('file://./models/energy-prediction');
    console.log('èƒ½æºé¢„æµ‹æ¨¡å‹è®­ç»ƒå®Œæˆå¹¶å·²ä¿å­˜');
  } catch (error) {
    console.error('æ¨¡å‹è®­ç»ƒå¤±è´¥:', error.message);
  }
}

// åˆ›å»ºèƒ½æºé¢„æµ‹è·¯ç”±
function setupEnergyRoutes(app, authenticateToken) {
  // èƒ½æºæ¶ˆè€—é¢„æµ‹æ¥å£
  app.get('/energy/predict', authenticateToken(), async (req, res) => {
    try {
      // è·å–æŸ¥è¯¢å‚æ•°
      const { buildingId, days } = req.query;

      if (!buildingId || !days) {
        res.status(400).json({ error: 'ç¼ºå°‘å¿…è¦å‚æ•°' });
        return;
      }

      // è°ƒç”¨é¢„æµ‹æ¨¡å‹ - æš‚æ—¶è¿”å›æ¨¡æ‹Ÿæ•°æ®
      const prediction = {
        buildingId,
        days: parseInt(days),
        predictions: Array.from({ length: parseInt(days) }, (_, i) => ({
          date: new Date(Date.now() + i * 24 * 60 * 60 * 1000).toISOString(),
          predicted_consumption: Math.random() * 1000 + 500
        }))
      };

      // è¿”å›é¢„æµ‹ç»“æœ
      res.json(prediction);
    } catch (error) {
      console.error('èƒ½æºé¢„æµ‹é”™è¯¯:', error);
      res.status(500).json({ error: 'èƒ½æºé¢„æµ‹å¤±è´¥' });
    }
  });

  // è¿”å›appå¯¹è±¡ä»¥ä¾¿é“¾å¼è°ƒç”¨
  return app;
}

// å‡†å¤‡é¢„æµ‹è¾“å…¥æ•°æ®
function _preparePredictionInput(data) {
  // å®é™…å¼€å‘ä¸­éœ€è¦å°†å†å²æ•°æ®è½¬æ¢ä¸ºæ¨¡å‹è¾“å…¥æ ¼å¼
  const normalized = normalizeData(data.map((d) => d.value));
  return tf.tensor2d([normalized], [1, normalized.length]);
}

// æ•°æ®å½’ä¸€åŒ–
function normalizeData(data) {
  const max = Math.max(...data);
  return data.map((value) => value / max);
}

// è®¡ç®—é¢„æµ‹æ—¶é—´
function _calculatePredictionTime(index, interval) {
  const intervalMap = {
    '1h': 60 * 60 * 1000,
    '1d': 24 * 60 * 60 * 1000,
    '1w': 7 * 24 * 60 * 60 * 1000
  };

  const ms = new Date().getTime() + (index + 1) * (intervalMap[interval] || intervalMap['1h']);
  return new Date(ms).toISOString();
}

// è·å–å†å²èƒ½æºæ•°æ®ï¼ˆæ¨¡æ‹Ÿï¼‰
function _getHistoricalEnergyData(_device_id, _start_time, _end_time) {
  // å®é™…å¼€å‘ä¸­åº”ä»æ•°æ®åº“æŸ¥è¯¢æ•°æ®
  return new Promise((resolve) => {
    resolve(historicalEnergyData);
  });
}

