import jwt from 'jsonwebtoken';
import { promisify } from 'util';
import crypto from 'crypto';
import { AuthenticationError, AuthorizationError as _AuthorizationError } from '../../shared/utils/AppError.js';
import config from '../../shared/config/index.js';

/**
 * JWTç®¡ç†å™¨ç±»
 */
export class JWTManager {
  constructor(options = {}) {
    this.secret = options.secret || config.jwt.secret;
    this.accessTokenExpiry = options.accessTokenExpiry || config.jwt.accessTokenExpiry;
    this.refreshTokenExpiry = options.refreshTokenExpiry || config.jwt.refreshTokenExpiry;
    this.issuer = options.issuer || config.jwt.issuer;
    this.audience = options.audience || config.jwt.audience;

    // ç”¨äºå­˜å‚¨å·²æ’¤é”€çš„tokenï¼ˆåœ¨ç”Ÿäº§ç¯å¢ƒä¸­åº”ä½¿ç”¨Redisï¼‰
    this.revokedTokens = new Set();

    // ç”¨äºå­˜å‚¨åˆ·æ–°tokenï¼ˆåœ¨ç”Ÿäº§ç¯å¢ƒä¸­åº”ä½¿ç”¨æ•°æ®åº“ï¼‰
    this.refreshTokenStore = new Map();

    // å®šæœŸæ¸…ç†è¿‡æœŸçš„æ’¤é”€token
    this.startCleanupInterval();
  }

  /**
   * ç”Ÿæˆè®¿é—®tokenå’Œåˆ·æ–°token
   * @param {Object} payload - tokenè½½è·
   * @param {Object} options - é¢å¤–é€‰é¡¹
   * @returns {Object} åŒ…å«accessTokenå’ŒrefreshTokençš„å¯¹è±¡
   */

  // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 53 è¡Œ)

  // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 53 è¡Œ)

  // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 53 è¡Œ)

  // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 53 è¡Œ)

  generateTokens(payload, options = {}) {
    const tokenId = crypto.randomUUID();
    const refreshTokenId = crypto.randomUUID();

    const accessTokenPayload = {
      ...payload,
      jti: tokenId, // JWT ID
      type: 'access',
      iat: Math.floor(Date.now() / 1000)
    };

    const refreshTokenPayload = {
      userId: payload.userId,
      username: payload.username,
      jti: refreshTokenId,
      type: 'refresh',
      accessTokenId: tokenId,
      iat: Math.floor(Date.now() / 1000)
    };

    const accessToken = jwt.sign(accessTokenPayload, this.secret, {
      expiresIn: this.accessTokenExpiry,
      issuer: this.issuer,
      audience: this.audience,
      algorithm: 'HS256'
    });

    const refreshToken = jwt.sign(refreshTokenPayload, this.secret, {
      expiresIn: this.refreshTokenExpiry,
      issuer: this.issuer,
      audience: this.audience,
      algorithm: 'HS256'
    });

    // å­˜å‚¨åˆ·æ–°tokenä¿¡æ¯
    this.refreshTokenStore.set(refreshTokenId, {
      userId: payload.userId,
      accessTokenId: tokenId,
      createdAt: new Date(),
      lastUsed: new Date(),
      userAgent: options.userAgent,
      ipAddress: options.ipAddress
    });

    return {
      accessToken,
      refreshToken,
      tokenType: 'Bearer',
      expiresIn: this.parseExpiry(this.accessTokenExpiry),
      tokenId,
      refreshTokenId
    };
  }

  /**
   * éªŒè¯token
   * @param {string} token - è¦éªŒè¯çš„token
   * @param {string} expectedType - æœŸæœ›çš„tokenç±»å‹ï¼ˆaccessæˆ–refreshï¼‰
   * @returns {Object} è§£ç åçš„tokenè½½è·
   */
  async verifyToken(token, expectedType = 'access') {
    try {
      const decoded = await promisify(jwt.verify)(token, this.secret, {
        issuer: this.issuer,
        audience: this.audience,
        algorithms: ['HS256']
      });

      // æ£€æŸ¥tokenç±»å‹
      if (decoded.type !== expectedType) {
        throw new AuthenticationError(
          `æ— æ•ˆçš„tokenç±»å‹ï¼ŒæœŸæœ›: ${expectedType}ï¼Œå®é™…: ${decoded.type}`
        );
      }

      // æ£€æŸ¥tokenæ˜¯å¦å·²è¢«æ’¤é”€
      if (this.revokedTokens.has(decoded.jti)) {
        throw new AuthenticationError('Tokenå·²è¢«æ’¤é”€');
      }

      // å¦‚æœæ˜¯åˆ·æ–°tokenï¼Œæ£€æŸ¥æ˜¯å¦å­˜åœ¨äºå­˜å‚¨ä¸­
      if (expectedType === 'refresh' && !this.refreshTokenStore.has(decoded.jti)) {
        throw new AuthenticationError('åˆ·æ–°tokenæ— æ•ˆæˆ–å·²è¿‡æœŸ');
      }

      return decoded;
    } catch (error) {
      if (error.name === 'TokenExpiredError') {
        throw new AuthenticationError('Tokenå·²è¿‡æœŸ');
      } else if (error.name === 'JsonWebTokenError') {
        throw new AuthenticationError('æ— æ•ˆçš„tokenæ ¼å¼');
      } else if (error.name === 'NotBeforeError') {
        throw new AuthenticationError('Tokenå°šæœªç”Ÿæ•ˆ');
      } else if (error instanceof AuthenticationError) {
        throw error;
      } else {
        throw new AuthenticationError('TokenéªŒè¯å¤±è´¥');
      }
    }
  }

  /**
   * åˆ·æ–°è®¿é—®token
   * @param {string} refreshToken - åˆ·æ–°token
   * @param {Object} options - é¢å¤–é€‰é¡¹
   * @returns {Object} æ–°çš„tokenå¯¹
   */
  async refreshAccessToken(refreshToken, options = {}) {
    // éªŒè¯åˆ·æ–°token
    const decoded = await this.verifyToken(refreshToken, 'refresh');

    // è·å–åˆ·æ–°tokenä¿¡æ¯
    const refreshTokenInfo = this.refreshTokenStore.get(decoded.jti);
    if (!refreshTokenInfo) {
      throw new AuthenticationError('åˆ·æ–°tokenä¿¡æ¯ä¸å­˜åœ¨');
    }

    // æ’¤é”€æ—§çš„è®¿é—®token
    this.revokedTokens.add(decoded.accessTokenId);

    // æ›´æ–°åˆ·æ–°tokençš„æœ€åä½¿ç”¨æ—¶é—´
    refreshTokenInfo.lastUsed = new Date();

    // ç”Ÿæˆæ–°çš„è®¿é—®token
    const newTokens = this.generateTokens(
      {
        userId: decoded.userId,
        username: decoded.username,
        role: decoded.role
      },
      options
    );

    // ç§»é™¤æ—§çš„åˆ·æ–°token
    this.refreshTokenStore.delete(decoded.jti);

    return newTokens;
  }

  /**
   * æ’¤é”€token
   * @param {string} token - è¦æ’¤é”€çš„token
   * @param {string} tokenType - tokenç±»å‹
   */
  async revokeToken(token, tokenType = 'access') {
    try {
      const decoded = await this.verifyToken(token, tokenType);

      if (tokenType === 'access') {
        this.revokedTokens.add(decoded.jti);
      } else if (tokenType === 'refresh') {
        this.refreshTokenStore.delete(decoded.jti);
        // åŒæ—¶æ’¤é”€ç›¸å…³çš„è®¿é—®token
        const refreshTokenInfo = this.refreshTokenStore.get(decoded.jti);
        if (refreshTokenInfo) {
          this.revokedTokens.add(refreshTokenInfo.accessTokenId);
        }
      }
    } catch (error) {
      // å³ä½¿tokenæ— æ•ˆï¼Œä¹Ÿä¸æŠ›å‡ºé”™è¯¯ï¼Œå› ä¸ºæ’¤é”€æ“ä½œåº”è¯¥æ˜¯å¹‚ç­‰çš„
      console.warn('æ’¤é”€tokenæ—¶å‘ç”Ÿé”™è¯¯:', error.message);
    }
  }

  /**
   * æ’¤é”€ç”¨æˆ·çš„æ‰€æœ‰token
   * @param {number} userId - ç”¨æˆ·ID
   */
  revokeAllUserTokens(userId) {
    // æ’¤é”€æ‰€æœ‰åˆ·æ–°token
    for (const [tokenId, tokenInfo] of this.refreshTokenStore.entries()) {
      if (tokenInfo.userId === userId) {
        this.refreshTokenStore.delete(tokenId);
        this.revokedTokens.add(tokenInfo.accessTokenId);
      }
    }
  }

  /**
   * è·å–ç”¨æˆ·çš„æ´»è·ƒä¼šè¯
   * @param {number} userId - ç”¨æˆ·ID
   * @returns {Array} æ´»è·ƒä¼šè¯åˆ—è¡¨
   */
  getUserActiveSessions(userId) {
    const sessions = [];

    for (const [tokenId, tokenInfo] of this.refreshTokenStore.entries()) {
      if (tokenInfo.userId === userId) {
        sessions.push({
          tokenId,
          createdAt: tokenInfo.createdAt,
          lastUsed: tokenInfo.lastUsed,
          userAgent: tokenInfo.userAgent,
          ipAddress: tokenInfo.ipAddress
        });
      }
    }

    return sessions.sort((a, b) => b.lastUsed - a.lastUsed);
  }

  /**
   * è§£æè¿‡æœŸæ—¶é—´å­—ç¬¦ä¸²ä¸ºç§’æ•°
   * @param {string} expiry - è¿‡æœŸæ—¶é—´å­—ç¬¦ä¸²ï¼ˆå¦‚'15m', '7d'ï¼‰
   * @returns {number} ç§’æ•°
   */
  parseExpiry(expiry) {
    const units = {
      s: 1,
      m: 60,
      h: 3600,
      d: 86400,
      w: 604800
    };

    const match = expiry.match(/^(\d+)([smhdw])$/);
    if (!match) {
      throw new Error(`æ— æ•ˆçš„è¿‡æœŸæ—¶é—´æ ¼å¼: ${expiry}`);
    }

    const [, value, unit] = match;
    return parseInt(value) * units[unit];
  }

  /**
   * å¯åŠ¨æ¸…ç†å®šæ—¶å™¨
   */
  startCleanupInterval() {
    // æ¯å°æ—¶æ¸…ç†ä¸€æ¬¡è¿‡æœŸçš„æ’¤é”€token
    setInterval(() => {
      this.cleanupExpiredTokens();
    }, 3600000); // 1å°æ—¶
  }

  /**
   * æ¸…ç†è¿‡æœŸçš„token
   */
  cleanupExpiredTokens() {
    const now = new Date();
    const refreshTokenExpiry = this.parseExpiry(this.refreshTokenExpiry) * 1000;

    // æ¸…ç†è¿‡æœŸçš„åˆ·æ–°token
    for (const [tokenId, tokenInfo] of this.refreshTokenStore.entries()) {
      if (now - tokenInfo.createdAt > refreshTokenExpiry) {
        this.refreshTokenStore.delete(tokenId);
        this.revokedTokens.add(tokenInfo.accessTokenId);
      }
    }

    console.log(
      `[JWT] æ¸…ç†å®Œæˆï¼Œå½“å‰æ’¤é”€tokenæ•°é‡: ${this.revokedTokens.size}ï¼Œæ´»è·ƒåˆ·æ–°tokenæ•°é‡: ${this.refreshTokenStore.size}`
    );
  }

  /**
   * è·å–ç»Ÿè®¡ä¿¡æ¯
   * @returns {Object} ç»Ÿè®¡ä¿¡æ¯
   */
  getStats() {
    return {
      revokedTokensCount: this.revokedTokens.size,
      activeRefreshTokensCount: this.refreshTokenStore.size,
      activeUsersCount: new Set([...this.refreshTokenStore.values()].map((info) => info.userId))
        .size
    };
  }
}

/**
 * è®¤è¯ä¸­é—´ä»¶
 * @param {Array} requiredRoles - éœ€è¦çš„è§’è‰²åˆ—è¡¨
 * @returns {Function} Expressä¸­é—´ä»¶å‡½æ•°
 */
export const authenticateToken =
  (requiredRoles = []) =>
    (req, res, next) => {
    // ä»è¯·æ±‚å¤´ä¸­è·å– Authorization å­—æ®µ
      const authHeader = req.headers.authorization;
      // å¦‚æœè¯·æ±‚å¤´å­˜åœ¨ï¼Œåˆ™æå– token éƒ¨åˆ†
      const token = authHeader && authHeader.split(' ')[1]?.trim();

      // å¦‚æœ token ä¸å­˜åœ¨ï¼Œåˆ™è¿”å› 401 Unauthorized
      if (token === null) {
        return res
          .status(401)
          .json({ error: { code: 'UNAUTHORIZED', message: 'æœªæä¾›èº«ä»½éªŒè¯ä»¤ç‰Œ' } });
      }

      // ä½¿ç”¨ jwt.verify æ¥éªŒè¯ token
      jwt.verify(token, config.jwt.secret, (err, user) => {
        if (err) {
        // å¦‚æœ token æ— æ•ˆæˆ–å·²è¿‡æœŸï¼Œåˆ™è¿”å› 403 Forbidden
          console.error('JWT éªŒè¯å¤±è´¥:', err);
          return res
            .status(403)
            .json({ error: { code: 'FORBIDDEN', message: 'æ— æ•ˆæˆ–å·²è¿‡æœŸçš„ä»¤ç‰Œ' } });
        }

        // æ£€æŸ¥è§’è‰²æƒé™
        if (requiredRoles.length > 0 && (!user || !requiredRoles.includes(user.role))) {
          return res.status(403).json({
            error: { code: 'FORBIDDEN', message: `éœ€è¦ä»¥ä¸‹è§’è‰²ä¹‹ä¸€: ${requiredRoles.join(', ')}` }
          });
        }

        // å¦‚æœ token æœ‰æ•ˆï¼Œåˆ™å°†è§£ç åçš„ç”¨æˆ·ä¿¡æ¯é™„åŠ åˆ°è¯·æ±‚å¯¹è±¡ä¸Š
        req.user = user;
        // è°ƒç”¨ next() å°†æ§åˆ¶æƒäº¤ç»™ä¸‹ä¸€ä¸ªä¸­é—´ä»¶æˆ–è·¯ç”±å¤„ç†å™¨
        next();
      });
    };

/**
 * å¯é€‰è®¤è¯ä¸­é—´ä»¶ï¼ˆä¸å¼ºåˆ¶è¦æ±‚è®¤è¯ï¼‰
 */
export const optionalAuth = () => {
  const jwtManager = new JWTManager();

  return async (req, res, next) => {
    try {
      const authHeader = req.headers.authorization;

      if (authHeader) {
        const parts = authHeader.split(' ');
        if (parts.length === 2 && parts[0] === 'Bearer') {
          const [, token] = parts;
          const decoded = await jwtManager.verifyToken(token, 'access');

          req.user = {
            id: decoded.userId,
            username: decoded.username,
            role: decoded.role,
            tokenId: decoded.jti
          };
        }
      }
    } catch (error) {
      // å¯é€‰è®¤è¯å¤±è´¥æ—¶ä¸æŠ›å‡ºé”™è¯¯ï¼Œåªæ˜¯ä¸è®¾ç½®ç”¨æˆ·ä¿¡æ¯
      console.warn('å¯é€‰è®¤è¯å¤±è´¥:', error.message);
    }

    next();
  };
};

// åˆ›å»ºé»˜è®¤çš„JWTç®¡ç†å™¨å®ä¾‹
export const defaultJWTManager = new JWTManager();

export default {
  JWTManager,
  authenticateToken,
  optionalAuth,
  defaultJWTManager
};

import { performance } from 'node:perf_hooks';
import { Histogram } from 'prom-client';
import os from 'os'; // å¼•å…¥æ“ä½œç³»ç»Ÿæ¨¡å—

// å…¨å±€å˜é‡è·Ÿè¸ªæ´»åŠ¨è¿æ¥æ•°
let activeConnections = 0;

// åˆ›å»ºAPIå»¶è¿ŸæŒ‡æ ‡ç›´æ–¹å›¾
export const apiLatencyHistogram = new Histogram({
  name: 'api_latency_seconds',
  help: 'APIè¯·æ±‚å»¶è¿Ÿåˆ†å¸ƒ',
  labelNames: ['method', 'route', 'status_code'],
  buckets: [0.001, 0.005, 0.01, 0.025, 0.05, 0.1, 0.25, 0.5, 1, 2.5, 5, 10]
});

// æ€§èƒ½ç›‘æ§ä¸­é—´ä»¶
export function monitorAPILatency(req, res, next) {
  const start = performance.now();
  let connectionCounted = false;

  // å¢åŠ æ´»åŠ¨è¿æ¥æ•°
  activeConnections++;

  // å®šä¹‰å‡å°‘è¿æ¥æ•°çš„å‡½æ•°ï¼Œç¡®ä¿åªæ‰§è¡Œä¸€æ¬¡
  const decreaseConnection = () => {
    if (!connectionCounted) {
      activeConnections--;
      connectionCounted = true;
    }
  };

  res.on('finish', () => {
    const duration = performance.now() - start;
    console.log(
      `API: ${req.method} ${req.originalUrl} | å»¶è¿Ÿ: ${duration.toFixed(2)}ms | çŠ¶æ€ç : ${res.statusCode}`
    );
    // è®°å½•æ€§èƒ½æŒ‡æ ‡
    apiLatencyHistogram.observe(
      {
        method: req.method,
        route: req.route ? req.route.path : req.path,
        status_code: res.statusCode
      },
      duration / 1000
    ); // è½¬æ¢ä¸ºç§’

    // å‡å°‘æ´»åŠ¨è¿æ¥æ•°
    decreaseConnection();
  });

  res.on('close', () => {
    // è¿æ¥å…³é—­æ—¶ä¹Ÿå‡å°‘è®¡æ•°
    decreaseConnection();
  });

  next();
}

// è·å–å½“å‰æ´»åŠ¨è¿æ¥æ•°
export function getActiveConnections() {
  return activeConnections;
}

// åˆ›å»ºæ€§èƒ½æŒ‡æ ‡è·¯ç”±
export function setupPerformanceRoutes(app) {
  // è·å–ç³»ç»Ÿæ€§èƒ½æŒ‡æ ‡
  app.get('/api/performance/metrics', (req, res) => {
    const [cpuLoad] = os.loadavg();
    const metrics = {
      apiLatency: collectAPILatencyMetrics(),
