// æƒé™éªŒè¯ä¸­é—´ä»¶
export function authenticateToken() {
  return (req, res, next) => {
    // ä»è¯·æ±‚å¤´ä¸­è·å–ä»¤ç‰Œ
    const authHeader = req.headers.authorization;
    const token = authHeader && authHeader.split(' ')[1];

    // å¦‚æœæ²¡æœ‰ä»¤ç‰Œï¼Œè¿”å›401æœªæˆæƒ
    if (!token) {
      return res.status(401).json({ error: 'ç¼ºå°‘è®¿é—®ä»¤ç‰Œ' });
    }

    // éªŒè¯ä»¤ç‰Œ
    jwt.verify(token, config.jwt.secret, (err, user) => {
      if (err) {
        if (err.name === 'TokenExpiredError') {
          return res.status(401).json({ error: 'è®¿é—®ä»¤ç‰Œå·²è¿‡æœŸ' });
        } else if (err.name === 'JsonWebTokenError') {
          return res.status(403).json({ error: 'æ— æ•ˆçš„è®¿é—®ä»¤ç‰Œ' });
        }
        return res.status(500).json({ error: 'ä»¤ç‰ŒéªŒè¯å¤±è´¥' });
      }

      req.user = user;
      next();
    });
  };
}

// ç”Ÿæˆæ–°çš„è®¿é—®ä»¤ç‰Œ
export function generateAccessToken(user) {
  // ç­¾å‘æ–°çš„ä»¤ç‰Œ
  return jwt.sign(
    {
      username: user.username,
      role: user.role,
      userId: user.userId || uuidv4()
    },
    config.jwt.secret,
    { expiresIn: config.jwt.accessTokenExpiry }
  );
}

// æ£€æŸ¥ç”¨æˆ·æƒé™
export function checkPermission(requiredRole) {
  return (req, res, next) => {
    // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦æœ‰è¶³å¤Ÿçš„æƒé™
    if (!req.user || !req.user.role || req.user.role < requiredRole) {
      return res.status(403).json({ error: 'æ²¡æœ‰è¶³å¤Ÿçš„æƒé™æ‰§è¡Œæ­¤æ“ä½œ' });
    }

    // ç”¨æˆ·æœ‰è¶³å¤Ÿæƒé™ï¼Œç»§ç»­æ‰§è¡Œä¸‹ä¸€ä¸ªä¸­é—´ä»¶
    next();
  };
}

// ç”¨æˆ·æƒé™ç®¡ç†
export function setupUserRoutes(app, authenticateToken) {
  // è·å–æ‰€æœ‰ç”¨æˆ·
  app.get('/users', authenticateToken(), (req, res) => {
    // æ¨¡æ‹Ÿç”¨æˆ·æ•°æ®åº“
    const mockUsers = [
      { id: 1, username: 'admin', roles: ['ADMIN'] },
      { id: 2, username: 'manager', roles: ['ENERGY_MANAGER'] }
    ];
    const users = mockUsers.map((user) => ({
      id: user.id,
      username: user.username,
      roles: user.roles
    }));

    res.json(users);
  });

  // åˆ›å»ºæ–°ç”¨æˆ·
  app.post('/users', authenticateToken(), (req, res) => {
    const { username, password, role } = req.body;

    if (!username || !password || !role) {
      res.status(400).json({ error: 'ç¼ºå°‘å¿…è¦å‚æ•°' });
      return;
    }

    if (roles[role] === undefined) {
      res.status(400).json({ error: 'æ— æ•ˆçš„è§’è‰²' });
      return;
    }

    // æ£€æŸ¥ç”¨æˆ·åæ˜¯å¦å·²å­˜åœ¨ - æ¨¡æ‹Ÿæ£€æŸ¥
    const mockUsers = ['admin', 'manager'];
    const existingUser = mockUsers.includes(username);
    if (existingUser) {
      res.status(400).json({ error: 'ç”¨æˆ·åå·²å­˜åœ¨' });
      return;
    }

    // åˆ›å»ºæ–°ç”¨æˆ· - æ¨¡æ‹Ÿåˆ›å»º
    const newUser = {
      id: Date.now(),
      username,
      password: `hashed_${password}`, // æ¨¡æ‹Ÿå¯†ç å“ˆå¸Œ
      roles: [role]
    };

    // æ¨¡æ‹Ÿä¿å­˜åˆ°æ•°æ®åº“
    console.log('ç”¨æˆ·å·²åˆ›å»º:', newUser.username);

    // è¿”å›åˆ›å»ºçš„ç”¨æˆ·ï¼ˆä¸åŒ…å«å¯†ç ï¼‰
    const { password: _, ...userWithoutPassword } = newUser;
    res.status(201).json(userWithoutPassword);
  });
}

