    const hourlyData = {};
    electricityData.forEach((item) => {
      const hour = moment(item.timestamp).format('YYYY-MM-DD HH:00');
      // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
      // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
      // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
      // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
      if (!hourlyData[hour]) {
