    const hourlyDistribution = {};
    Object.entries(hourDistribution).forEach(([hour, values]) => {
      // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
      // TODO: è€ƒè™‘ä½¿ç”¨æ—©æœŸè¿”å›æˆ–ç­–ç•¥æ¨¡å¼æ¥å‡å°‘åµŒå¥—
