  // å¤„ç†å‘Šè­¦
  handleAlert(alert) {
    const { deviceId, level, message } = alert;

    // æ·»åŠ åˆ°å‘Šè­¦åˆ—è¡¨
    this.alerts.unshift({
      ...alert,
      id: `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
    });

    // ä¿æŒæœ€è¿‘100æ¡å‘Šè­¦
    if (this.alerts.length > 100) {
      this.alerts = this.alerts.slice(0, 100);
    }

    // åœ¨åœºæ™¯ä¸­æ˜¾ç¤ºå‘Šè­¦æ•ˆæœ
    this.showAlertInScene(deviceId, level);

    // è§¦å‘è‡ªå®šä¹‰äº‹ä»¶
