  // åœ¨åœºæ™¯ä¸­æ˜¾ç¤ºå‘Šè­¦æ•ˆæœ
  showAlertInScene(deviceId, level) {
    // å¯ä»¥æ·»åŠ é—ªçƒæ•ˆæœã€é¢œè‰²å˜åŒ–ç­‰
    const device = this.sceneManager.devices.get(deviceId);
    if (device) {
      // æ ¹æ®å‘Šè­¦çº§åˆ«è®¾ç½®é¢œè‰²
