      switch (level) {
        case 'critical':
          color = 0xff0000;
          break;
        case 'warning':
          color = 0xffa500;
          break;
        default:
          color = 0xffff00;
      }

      // ä¸´æ—¶æ”¹å˜è®¾å¤‡é¢œè‰²
      const originalColor = device.material.color.getHex();
      device.material.color.setHex(color);

