import bcrypt from 'bcrypt';
import { validationResult } from 'express-validator';
import BaseController from './BaseController.js';
import User from '../../../core/entities/User.js';
import config from '../../../shared/config/index.js';
import logger from '../../../shared/utils/logger.js';
import { defaultJWTManager } from '../../../core/services/jwtManager.js';

/**
 * è®¤è¯æ§åˆ¶å™¨
 * å¤„ç†ç”¨æˆ·è®¤è¯ç›¸å…³çš„ä¸šåŠ¡é€»è¾‘
 */
class AuthController extends BaseController {
  constructor() {
    super();
    this.saltRounds = 12;
    this.jwtManager = defaultJWTManager;
  }

  /**
   * ç”¨æˆ·ç™»å½•
   */
  login = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    const { username, password } = req.body;

    try {
      // æŸ¥æ‰¾ç”¨æˆ·
      const user = await User.findByUsername(username);
      if (!user) {
        this.logOperation(req, 'LOGIN_FAILED', { username, reason: 'USER_NOT_FOUND' });
        return res.unauthorized('ç”¨æˆ·åæˆ–å¯†ç é”™è¯¯');
      }

      // éªŒè¯å¯†ç 
      const isValidPassword = await bcrypt.compare(password, user.password);
      if (!isValidPassword) {
        this.logOperation(req, 'LOGIN_FAILED', { username, reason: 'INVALID_PASSWORD' });
        return res.unauthorized('ç”¨æˆ·åæˆ–å¯†ç é”™è¯¯');
      }

      // æ£€æŸ¥ç”¨æˆ·çŠ¶æ€
      if (user.status !== 'active') {
        this.logOperation(req, 'LOGIN_FAILED', { username, reason: 'USER_INACTIVE' });
        return res.forbidden('ç”¨æˆ·è´¦æˆ·å·²è¢«ç¦ç”¨');
      }

      // ç”ŸæˆJWTä»¤ç‰Œ
      const tokenPayload = {
        id: user.id,
        username: user.username,
        role: user.role,
        permissions: user.permissions || []
      };

      const accessToken = this.jwtManager.generateToken(tokenPayload);
      const refreshToken = this.jwtManager.generateRefreshToken(tokenPayload);

      // æ›´æ–°æœ€åç™»å½•æ—¶é—´
      await User.updateLastLogin(user.id);

      // æ¸…ç†æ•æ„Ÿæ•°æ®
      const userResponse = this.sanitizeUser(user);

      this.logOperation(req, 'LOGIN_SUCCESS', { userId: user.id, username });

      res.success(
        {
          user: userResponse,
          tokens: {
            accessToken,
            refreshToken,
            expiresIn: config.jwt.expiresIn
          }
        },
        'ç™»å½•æˆåŠŸ'
      );
    } catch (error) {
      logger.error('ç™»å½•å¤±è´¥', {
        error: error.message,
        username,
        ip: req.ip
      });
      res.internalError('ç™»å½•å¤±è´¥ï¼Œè¯·ç¨åé‡è¯•');
    }
  });

  /**
   * ç”¨æˆ·æ³¨å†Œ
   */
  register = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    const { username, password, email, role = 'viewer' } = req.body;

    try {
      // æ£€æŸ¥ç”¨æˆ·åæ˜¯å¦å·²å­˜åœ¨
      const existingUser = await User.findByUsername(username);
      if (existingUser) {
        return res.conflict('ç”¨æˆ·åå·²å­˜åœ¨');
      }

      // æ£€æŸ¥é‚®ç®±æ˜¯å¦å·²å­˜åœ¨
      if (email) {
        const existingEmail = await User.findByEmail(email);
        if (existingEmail) {
          return res.conflict('é‚®ç®±å·²è¢«ä½¿ç”¨');
        }
      }

      // åŠ å¯†å¯†ç 
      const hashedPassword = await bcrypt.hash(password, this.saltRounds);

      // åˆ›å»ºç”¨æˆ·
      const userData = {
        username,
        password: hashedPassword,
        email,
        role,
        status: 'active',
        createdAt: new Date().toISOString()
      };

      const newUser = await User.create(userData);
      const userResponse = this.sanitizeUser(newUser);

      this.logOperation(req, 'USER_REGISTER', { userId: newUser.id, username });

      res.success(userResponse, 'æ³¨å†ŒæˆåŠŸ', 201);
    } catch (error) {
      logger.error('æ³¨å†Œå¤±è´¥', {
        error: error.message,
        username,
        email,
        ip: req.ip
      });
      res.internalError('æ³¨å†Œå¤±è´¥ï¼Œè¯·ç¨åé‡è¯•');
    }
  });

  /**
   * è·å–å½“å‰ç”¨æˆ·ä¿¡æ¯
   */
  getCurrentUser = this.asyncHandler(async (req, res) => {
    try {
      const user = await User.findById(req.user.id);
      if (!user) {
        return res.notFound('ç”¨æˆ·ä¸å­˜åœ¨');
      }

      const userResponse = this.sanitizeUser(user);
      res.success(userResponse);
    } catch (error) {
      logger.error('è·å–ç”¨æˆ·ä¿¡æ¯å¤±è´¥', {
        error: error.message,
        userId: req.user?.id
      });
      res.internalError('è·å–ç”¨æˆ·ä¿¡æ¯å¤±è´¥');
    }
  });

  /**
   * æ›´æ–°ç”¨æˆ·ä¿¡æ¯
   */
  updateProfile = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    const { email, currentPassword, newPassword } = req.body;
    const userId = req.user.id;

    try {
      const user = await User.findById(userId);
      if (!user) {
        return res.notFound('ç”¨æˆ·ä¸å­˜åœ¨');
      }

      const updateData = {};

      // æ›´æ–°é‚®ç®±
      if (email && email !== user.email) {
        const existingEmail = await User.findByEmail(email);
        if (existingEmail && existingEmail.id !== userId) {
          return res.conflict('é‚®ç®±å·²è¢«ä½¿ç”¨');
        }
        updateData.email = email;
      }

      // æ›´æ–°å¯†ç 
      if (newPassword) {
        if (!currentPassword) {
          return res.badRequest('æ›´æ–°å¯†ç éœ€è¦æä¾›å½“å‰å¯†ç ');
        }

        const isValidPassword = await bcrypt.compare(currentPassword, user.password);
        if (!isValidPassword) {
          return res.unauthorized('å½“å‰å¯†ç é”™è¯¯');
        }

        updateData.password = await bcrypt.hash(newPassword, this.saltRounds);
      }

      if (Object.keys(updateData).length === 0) {
        return res.badRequest('æ²¡æœ‰éœ€è¦æ›´æ–°çš„æ•°æ®');
      }

      updateData.updatedAt = new Date().toISOString();
      const updatedUser = await User.update(userId, updateData);
      const userResponse = this.sanitizeUser(updatedUser);

      this.logOperation(req, 'PROFILE_UPDATE', { userId, fields: Object.keys(updateData) });

      res.success(userResponse, 'ç”¨æˆ·ä¿¡æ¯æ›´æ–°æˆåŠŸ');
    } catch (error) {
      logger.error('æ›´æ–°ç”¨æˆ·ä¿¡æ¯å¤±è´¥', {
        error: error.message,
        userId
      });
      res.internalError('æ›´æ–°ç”¨æˆ·ä¿¡æ¯å¤±è´¥');
    }
  });

  /**
   * ç”¨æˆ·ç™»å‡º
   */
  logout = this.asyncHandler(async (req, res) => {
    try {
      const token = req.headers.authorization?.replace('Bearer ', '');
      if (token) {
        // å°†ä»¤ç‰ŒåŠ å…¥é»‘åå•
        await this.jwtManager.blacklistToken(token);
      }

      this.logOperation(req, 'LOGOUT', { userId: req.user?.id });

      res.success(null, 'ç™»å‡ºæˆåŠŸ');
    } catch (error) {
      logger.error('ç™»å‡ºå¤±è´¥', {
        error: error.message,
        userId: req.user?.id
      });
      res.internalError('ç™»å‡ºå¤±è´¥');
    }
  });

  /**
   * åˆ·æ–°è®¿é—®ä»¤ç‰Œ
   */
  refreshToken = this.asyncHandler(async (req, res) => {
    const { refreshToken } = req.body;

    if (!refreshToken) {
      return res.badRequest('ç¼ºå°‘åˆ·æ–°ä»¤ç‰Œ');
    }

    try {
      // éªŒè¯åˆ·æ–°ä»¤ç‰Œ
      const decoded = this.jwtManager.verifyRefreshToken(refreshToken);

      // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦ä»ç„¶å­˜åœ¨ä¸”æ´»è·ƒ
      const user = await User.findById(decoded.id);
      if (!user || user.status !== 'active') {
        return res.unauthorized('ç”¨æˆ·ä¸å­˜åœ¨æˆ–å·²è¢«ç¦ç”¨');
      }

      // ç”Ÿæˆæ–°çš„è®¿é—®ä»¤ç‰Œ
      const tokenPayload = {
        id: user.id,
        username: user.username,
        role: user.role,
        permissions: user.permissions || []
      };

      const newAccessToken = this.jwtManager.generateToken(tokenPayload);
      const newRefreshToken = this.jwtManager.generateRefreshToken(tokenPayload);

      // å°†æ—§çš„åˆ·æ–°ä»¤ç‰ŒåŠ å…¥é»‘åå•
      await this.jwtManager.blacklistToken(refreshToken);

      res.success(
        {
          accessToken: newAccessToken,
          refreshToken: newRefreshToken,
          expiresIn: config.jwt.expiresIn
        },
        'ä»¤ç‰Œåˆ·æ–°æˆåŠŸ'
      );
    } catch (error) {
      logger.error('åˆ·æ–°ä»¤ç‰Œå¤±è´¥', {
        error: error.message,
        ip: req.ip
      });

      if (error.name === 'JsonWebTokenError' || error.name === 'TokenExpiredError') {
        return res.unauthorized('æ— æ•ˆçš„åˆ·æ–°ä»¤ç‰Œ');
      }

      res.internalError('ä»¤ç‰Œåˆ·æ–°å¤±è´¥');
    }
  });

  /**
   * æ¸…ç†ç”¨æˆ·æ•æ„Ÿæ•°æ®
   */
  sanitizeUser(user) {
    const { password: _password, ...sanitizedUser } = user;
    return sanitizedUser;
  }

  /**
   * éªŒè¯ç”¨æˆ·æƒé™
   */
  checkPermission = this.asyncHandler(async (req, res) => {
    const { permission } = req.params;
    const userPermissions = req.user.permissions || [];
    const hasPermission = userPermissions.includes(permission) || req.user.role === 'admin';

    res.success({ hasPermission }, 'æƒé™æ£€æŸ¥å®Œæˆ');
  });
}

