  /**
   * è·å–å•ä¸ªèƒ½è€—è®°å½•
   */
  getEnergyRecord = this.asyncHandler(async (req, res) => {
    const { id } = req.params;

    try {
      const energyRecord = await EnergyData.findById(id);
      if (!energyRecord) {
        return res.notFound('èƒ½è€—è®°å½•ä¸å­˜åœ¨');
      }

