    }
  });

  /**
   * åˆ›å»ºèƒ½è€—æ•°æ®è®°å½•
   */
  createEnergyData = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    try {
      const energyData = {
        ...req.body,
        created_by: req.user.id,
        created_at: new Date().toISOString()
      };

      // éªŒè¯è®¾å¤‡æ˜¯å¦å­˜åœ¨
      if (energyData.device_id) {
