      // å¤„ç†æ‰¹é‡æ•°æ®
      const processedRecords = records.map((record) => ({
        ...record,
        created_by: req.user.id,
        created_at: new Date().toISOString(),
        timestamp: record.timestamp || new Date().toISOString()
      }));

      const result = await EnergyData.batchCreate(processedRecords);

      this.logOperation(req, 'ENERGY_DATA_BATCH_CREATE', {
        recordCount: processedRecords.length,
        deviceIds
      });

      res.success(
        {
          created_count: result.insertedCount,
          records: result.records
        },
        'æ‰¹é‡åˆ›å»ºèƒ½è€—æ•°æ®æˆåŠŸ',
        201
      );
    } catch (error) {
      logger.error('æ‰¹é‡åˆ›å»ºèƒ½è€—æ•°æ®å¤±è´¥', {
        error: error.message,
        recordCount: req.body.records?.length,
        userId: req.user?.id
      });
      res.internalError('æ‰¹é‡åˆ›å»ºèƒ½è€—æ•°æ®å¤±è´¥');
    }
  });

