const jwt = require('jsonwebtoken');
const responseFormatter = require('./responseFormatter');

// JWTé…ç½®
const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key';
const JWT_EXPIRES_IN = process.env.JWT_EXPIRES_IN || '24h';
const JWT_REFRESH_SECRET = process.env.JWT_REFRESH_SECRET || 'your-refresh-secret-key';
const JWT_REFRESH_EXPIRES_IN = process.env.JWT_REFRESH_EXPIRES_IN || '7d';

// ä»¤ç‰Œé»‘åå•ï¼ˆåœ¨ç”Ÿäº§ç¯å¢ƒä¸­åº”ä½¿ç”¨Redisç­‰å¤–éƒ¨å­˜å‚¨ï¼‰
const tokenBlacklist = new Set();

/**
 * ç”Ÿæˆè®¿é—®ä»¤ç‰Œ
 * @param {Object} payload - ä»¤ç‰Œè½½è·
 * @returns {string} JWTä»¤ç‰Œ
 */
function generateAccessToken(payload) {
  return jwt.sign(payload, JWT_SECRET, {
    expiresIn: JWT_EXPIRES_IN,
    issuer: 'carbon-management-system',
    audience: 'carbon-management-client'
  });
}

/**
 * ç”Ÿæˆåˆ·æ–°ä»¤ç‰Œ
 * @param {Object} payload - ä»¤ç‰Œè½½è·
 * @returns {string} JWTåˆ·æ–°ä»¤ç‰Œ
 */
function generateRefreshToken(payload) {
  return jwt.sign(payload, JWT_REFRESH_SECRET, {
    expiresIn: JWT_REFRESH_EXPIRES_IN,
    issuer: 'carbon-management-system',
    audience: 'carbon-management-client'
  });
}

/**
 * éªŒè¯è®¿é—®ä»¤ç‰Œ
 * @param {string} token - JWTä»¤ç‰Œ
 * @returns {Object} è§£ç åçš„è½½è·
 */
function verifyAccessToken(token) {
  return jwt.verify(token, JWT_SECRET, {
    issuer: 'carbon-management-system',
    audience: 'carbon-management-client'
  });
}

/**
 * éªŒè¯åˆ·æ–°ä»¤ç‰Œ
 * @param {string} token - JWTåˆ·æ–°ä»¤ç‰Œ
 * @returns {Object} è§£ç åçš„è½½è·
 */
function verifyRefreshToken(token) {
  return jwt.verify(token, JWT_REFRESH_SECRET, {
    issuer: 'carbon-management-system',
    audience: 'carbon-management-client'
  });
}

/**
 * å°†ä»¤ç‰ŒåŠ å…¥é»‘åå•
 * @param {string} token - è¦åŠ å…¥é»‘åå•çš„ä»¤ç‰Œ
 */
function blacklistToken(token) {
  tokenBlacklist.add(token);

  // å®šæœŸæ¸…ç†è¿‡æœŸçš„ä»¤ç‰Œï¼ˆç®€å•å®ç°ï¼Œç”Ÿäº§ç¯å¢ƒåº”ä½¿ç”¨æ›´é«˜æ•ˆçš„æ–¹æ³•ï¼‰
  setTimeout(
    () => {
      tokenBlacklist.delete(token);
    },
    24 * 60 * 60 * 1000
  ); // 24å°æ—¶åæ¸…ç†
}

/**
 * æ£€æŸ¥ä»¤ç‰Œæ˜¯å¦åœ¨é»‘åå•ä¸­
 * @param {string} token - è¦æ£€æŸ¥çš„ä»¤ç‰Œ
 * @returns {boolean} æ˜¯å¦åœ¨é»‘åå•ä¸­
 */
function isTokenBlacklisted(token) {
  return tokenBlacklist.has(token);
}

/**
 * ä»è¯·æ±‚ä¸­æå–ä»¤ç‰Œ
 * @param {Object} req - Expressè¯·æ±‚å¯¹è±¡
 * @returns {string|null} æå–çš„ä»¤ç‰Œ
 */
function extractToken(req) {
  // ä»Authorizationå¤´ä¸­æå–
  const authHeader = req.headers.authorization;
  if (authHeader && authHeader.startsWith('Bearer ')) {
    return authHeader.substring(7);
  }

  // ä»æŸ¥è¯¢å‚æ•°ä¸­æå–
  if (req.query.token) {
    return req.query.token;
  }

  // ä»Cookieä¸­æå–
  if (req.cookies && req.cookies.accessToken) {
    return req.cookies.accessToken;
  }

  return null;
}

/**
 * è®¤è¯ä¸­é—´ä»¶
 * éªŒè¯ç”¨æˆ·èº«ä»½å¹¶å°†ç”¨æˆ·ä¿¡æ¯é™„åŠ åˆ°è¯·æ±‚å¯¹è±¡
 */
function authenticate(req, res, next) {
  try {
    const token = extractToken(req);

    if (!token) {
      return responseFormatter.unauthorized(res, 'ç¼ºå°‘è®¿é—®ä»¤ç‰Œ');
    }

    // æ£€æŸ¥ä»¤ç‰Œæ˜¯å¦åœ¨é»‘åå•ä¸­
    if (isTokenBlacklisted(token)) {
      return responseFormatter.unauthorized(res, 'ä»¤ç‰Œå·²å¤±æ•ˆ');
    }

    // éªŒè¯ä»¤ç‰Œ
    const decoded = verifyAccessToken(token);

    // æ£€æŸ¥ä»¤ç‰Œæ˜¯å¦åŒ…å«å¿…è¦çš„ç”¨æˆ·ä¿¡æ¯
    if (!decoded.userId || !decoded.username || !decoded.role) {
      return responseFormatter.unauthorized(res, 'ä»¤ç‰Œæ ¼å¼æ— æ•ˆ');
    }

    // å°†ç”¨æˆ·ä¿¡æ¯é™„åŠ åˆ°è¯·æ±‚å¯¹è±¡
    req.user = {
      userId: decoded.userId,
      username: decoded.username,
      email: decoded.email,
      role: decoded.role,
      permissions: decoded.permissions || [],
      tokenId: decoded.jti, // JWT ID
      issuedAt: decoded.iat,
      expiresAt: decoded.exp
    };

    // å°†åŸå§‹ä»¤ç‰Œä¹Ÿé™„åŠ åˆ°è¯·æ±‚å¯¹è±¡ï¼ˆç”¨äºæ³¨é”€ç­‰æ“ä½œï¼‰
    req.token = token;

    // è®°å½•è®¤è¯æ—¥å¿—
    console.log(
      `[${new Date().toISOString()}] Authentication: User ${decoded.username} (${decoded.role}) authenticated`
    );

    next();
  } catch (error) {
    console.error('è®¤è¯é”™è¯¯:', error);

    if (error.name === 'TokenExpiredError') {
      return responseFormatter.unauthorized(res, 'ä»¤ç‰Œå·²è¿‡æœŸ');
    } else if (error.name === 'JsonWebTokenError') {
      return responseFormatter.unauthorized(res, 'ä»¤ç‰Œæ— æ•ˆ');
    } else if (error.name === 'NotBeforeError') {
      return responseFormatter.unauthorized(res, 'ä»¤ç‰Œå°šæœªç”Ÿæ•ˆ');
    } 
    return responseFormatter.internalError(res, 'è®¤è¯å¤±è´¥', error);

  }
}

/**
 * å¯é€‰è®¤è¯ä¸­é—´ä»¶
 * å¦‚æœæä¾›äº†ä»¤ç‰Œåˆ™éªŒè¯ï¼Œå¦åˆ™ç»§ç»­æ‰§è¡Œ
 */
function optionalAuthenticate(req, res, next) {
  try {
    const token = extractToken(req);

    if (!token) {
      // æ²¡æœ‰ä»¤ç‰Œï¼Œç»§ç»­æ‰§è¡Œä½†ä¸è®¾ç½®ç”¨æˆ·ä¿¡æ¯
      return next();
    }

    // æ£€æŸ¥ä»¤ç‰Œæ˜¯å¦åœ¨é»‘åå•ä¸­
    if (isTokenBlacklisted(token)) {
      // ä»¤ç‰Œåœ¨é»‘åå•ä¸­ï¼Œç»§ç»­æ‰§è¡Œä½†ä¸è®¾ç½®ç”¨æˆ·ä¿¡æ¯
      return next();
    }

    // éªŒè¯ä»¤ç‰Œ
    const decoded = verifyAccessToken(token);

    // æ£€æŸ¥ä»¤ç‰Œæ˜¯å¦åŒ…å«å¿…è¦çš„ç”¨æˆ·ä¿¡æ¯
    if (decoded.userId && decoded.username && decoded.role) {
      // å°†ç”¨æˆ·ä¿¡æ¯é™„åŠ åˆ°è¯·æ±‚å¯¹è±¡
      req.user = {
        userId: decoded.userId,
        username: decoded.username,
        email: decoded.email,
        role: decoded.role,
        permissions: decoded.permissions || [],
        tokenId: decoded.jti,
        issuedAt: decoded.iat,
        expiresAt: decoded.exp
      };

      req.token = token;

      console.log(
        `[${new Date().toISOString()}] Optional Authentication: User ${decoded.username} (${decoded.role}) authenticated`
      );
    }

    next();
  } catch (error) {
    // å¯é€‰è®¤è¯å¤±è´¥æ—¶ä¸è¿”å›é”™è¯¯ï¼Œç»§ç»­æ‰§è¡Œ
    console.warn('å¯é€‰è®¤è¯å¤±è´¥:', error.message);
    next();
  }
}

/**
 * åˆ·æ–°ä»¤ç‰Œä¸­é—´ä»¶
 * éªŒè¯åˆ·æ–°ä»¤ç‰Œå¹¶ç”Ÿæˆæ–°çš„è®¿é—®ä»¤ç‰Œ
 */
function refreshToken(req, res, next) {
  try {
    const refreshToken = req.body.refreshToken || req.cookies.refreshToken;

    if (!refreshToken) {
      return responseFormatter.unauthorized(res, 'ç¼ºå°‘åˆ·æ–°ä»¤ç‰Œ');
    }

    // éªŒè¯åˆ·æ–°ä»¤ç‰Œ
    const decoded = verifyRefreshToken(refreshToken);

    // æ£€æŸ¥åˆ·æ–°ä»¤ç‰Œæ˜¯å¦åŒ…å«å¿…è¦çš„ç”¨æˆ·ä¿¡æ¯
    if (!decoded.userId || !decoded.username || !decoded.role) {
      return responseFormatter.unauthorized(res, 'åˆ·æ–°ä»¤ç‰Œæ ¼å¼æ— æ•ˆ');
    }

    // ç”Ÿæˆæ–°çš„è®¿é—®ä»¤ç‰Œ
    const newAccessToken = generateAccessToken({
      userId: decoded.userId,
      username: decoded.username,
      email: decoded.email,
      role: decoded.role,
      permissions: decoded.permissions
    });

    // å¯é€‰ï¼šç”Ÿæˆæ–°çš„åˆ·æ–°ä»¤ç‰Œï¼ˆæ»šåŠ¨åˆ·æ–°ï¼‰
    const newRefreshToken = generateRefreshToken({
      userId: decoded.userId,
      username: decoded.username,
      email: decoded.email,
      role: decoded.role
    });

    // å°†æ–°ä»¤ç‰Œé™„åŠ åˆ°å“åº”å¯¹è±¡
    res.locals.newTokens = {
      accessToken: newAccessToken,
      refreshToken: newRefreshToken,
      expiresIn: JWT_EXPIRES_IN
    };

    // å°†ç”¨æˆ·ä¿¡æ¯é™„åŠ åˆ°è¯·æ±‚å¯¹è±¡
    req.user = {
      userId: decoded.userId,
      username: decoded.username,
      email: decoded.email,
      role: decoded.role,
      permissions: decoded.permissions || []
    };

    console.log(
      `[${new Date().toISOString()}] Token Refresh: User ${decoded.username} tokens refreshed`
    );

    next();
  } catch (error) {
    console.error('ä»¤ç‰Œåˆ·æ–°é”™è¯¯:', error);

    if (error.name === 'TokenExpiredError') {
      return responseFormatter.unauthorized(res, 'åˆ·æ–°ä»¤ç‰Œå·²è¿‡æœŸ');
    } else if (error.name === 'JsonWebTokenError') {
      return responseFormatter.unauthorized(res, 'åˆ·æ–°ä»¤ç‰Œæ— æ•ˆ');
    } 
    return responseFormatter.internalError(res, 'ä»¤ç‰Œåˆ·æ–°å¤±è´¥', error);

  }
}

/**
 * æ³¨é”€ä¸­é—´ä»¶
 * å°†å½“å‰ä»¤ç‰ŒåŠ å…¥é»‘åå•
 */
function logout(req, res, next) {
  try {
    const token = extractToken(req);

    if (token) {
      // å°†ä»¤ç‰ŒåŠ å…¥é»‘åå•
      blacklistToken(token);
      console.log(
        `[${new Date().toISOString()}] Logout: Token blacklisted for user ${req.user?.username || 'unknown'}`
      );
    }

    // æ¸…é™¤ç”¨æˆ·ä¿¡æ¯
    req.user = null;
    req.token = null;

    next();
  } catch (error) {
    console.error('æ³¨é”€é”™è¯¯:', error);
    return responseFormatter.internalError(res, 'æ³¨é”€å¤±è´¥', error);
  }
}

/**
 * æ£€æŸ¥ä»¤ç‰Œæœ‰æ•ˆæœŸä¸­é—´ä»¶
 * å¦‚æœä»¤ç‰Œå³å°†è¿‡æœŸï¼Œåœ¨å“åº”å¤´ä¸­æ·»åŠ æç¤º
 */
function checkTokenExpiry(req, res, next) {
  if (req.user && req.user.expiresAt) {
    const now = Math.floor(Date.now() / 1000);
    const timeToExpiry = req.user.expiresAt - now;

    // å¦‚æœä»¤ç‰Œåœ¨30åˆ†é’Ÿå†…è¿‡æœŸï¼Œæ·»åŠ è­¦å‘Šå¤´
    if (timeToExpiry < 30 * 60) {
      res.set('X-Token-Warning', 'Token expires soon');
      res.set('X-Token-Expires-In', timeToExpiry.toString());
    }
  }

  next();
}

/**
 * é€Ÿç‡é™åˆ¶ä¸­é—´ä»¶ï¼ˆåŸºäºç”¨æˆ·ï¼‰
 * @param {number} maxRequests - æœ€å¤§è¯·æ±‚æ•°
 * @param {number} windowMs - æ—¶é—´çª—å£ï¼ˆæ¯«ç§’ï¼‰
 * @returns {Function} Expressä¸­é—´ä»¶å‡½æ•°
 */
function userRateLimit(maxRequests = 100, windowMs = 15 * 60 * 1000) {
  const userRequests = new Map();

  return (req, res, next) => {
    const userId = req.user?.userId || req.ip;
    const now = Date.now();

    if (!userRequests.has(userId)) {
      userRequests.set(userId, { count: 1, resetTime: now + windowMs });
      return next();
    }

    const userRequest = userRequests.get(userId);

    if (now > userRequest.resetTime) {
      // é‡ç½®è®¡æ•°å™¨
      userRequest.count = 1;
      userRequest.resetTime = now + windowMs;
      return next();
    }

    if (userRequest.count >= maxRequests) {
      return responseFormatter.tooManyRequests(res, 'è¯·æ±‚è¿‡äºé¢‘ç¹ï¼Œè¯·ç¨åå†è¯•');
    }

    userRequest.count++;
    next();
  };
}

