import cache from '../../../shared/config/cache.js';
import redisClient from '../../../database/redisClient.js';
const { setAsync, expireAsync, DEFAULT_TTL } = cache;

/**
 * ç¼“å­˜ä¸­é—´ä»¶ - ç”¨äºç¼“å­˜GETè¯·æ±‚çš„å“åº”ç»“æœ
 * @param {number} ttl - ç¼“å­˜è¿‡æœŸæ—¶é—´(ç§’)
 */
const cacheMiddleware =
  (ttl = DEFAULT_TTL) =>
    async (req, res, next) => {
    // åªç¼“å­˜GETè¯·æ±‚
      if (req.method !== 'GET') {
        return next();
      }

      // ç”Ÿæˆå”¯ä¸€ç¼“å­˜é”®
      const cacheKey = `api:${req.originalUrl}`;

      try {
      // å°è¯•ä»ç¼“å­˜è·å–æ•°æ®
        const cachedData = await redisClient.get(cacheKey);

        if (cachedData) {
        // è¿”å›ç¼“å­˜æ•°æ®
          const data = JSON.parse(cachedData);
          return res.json(data);
        }

        // é‡å†™res.jsonæ–¹æ³•
        const originalJson = res.json;
        res.json = function (body) {
        // ç¼“å­˜å“åº”æ•°æ®
          setAsync(cacheKey, JSON.stringify(body))
            .then(() => expireAsync(cacheKey, ttl))
            .catch((err) => console.error('ç¼“å­˜è®¾ç½®å¤±è´¥:', err));

          return originalJson.call(this, body);
        };

        next();
      } catch (err) {
        console.error('ç¼“å­˜ä¸­é—´ä»¶é”™è¯¯:', err);
        next();
      }
    };
export { cacheMiddleware, clearCacheMiddleware };

