  notFoundHandler,
  setupProcessHandlers
};

import path from 'path';
import fs from 'fs';

/**
 * æ€§èƒ½ç›‘æ§ä¸­é—´ä»¶
 * ç”¨äºè·Ÿè¸ªAPIå“åº”æ—¶é—´ã€ç¼“å­˜å‘½ä¸­ç‡ç­‰æ€§èƒ½æŒ‡æ ‡
 */
class PerformanceMonitor {
  constructor() {
    this.metrics = {
      requests: 0,
      totalResponseTime: 0,
      cacheHits: 0,
      cacheMisses: 0,
      endpoints: new Map()
    };

    this.logFile = path.join(process.cwd(), 'logs', 'performance.log');
    this.reportFile = path.join(process.cwd(), 'test-results', 'performance_monitor.csv');

    // ç¡®ä¿æ—¥å¿—ç›®å½•å­˜åœ¨
    this.ensureLogDirectory();

    // å®šæœŸç”Ÿæˆæ€§èƒ½æŠ¥å‘Š
    this.startPeriodicReporting();
  }

  ensureLogDirectory() {
    const logDir = path.dirname(this.logFile);
    const reportDir = path.dirname(this.reportFile);

    if (!fs.existsSync(logDir)) {
      fs.mkdirSync(logDir, { recursive: true });
    }

    if (!fs.existsSync(reportDir)) {
      fs.mkdirSync(reportDir, { recursive: true });
    }
  }

  /**
   * åˆ›å»ºæ€§èƒ½ç›‘æ§ä¸­é—´ä»¶
   */
  middleware() {
    return (req, res, next) => {
      try {
        const startTime = Date.now();
        // ä¿®å¤ï¼šå®‰å…¨åœ°è·å–è·¯å¾„ï¼Œé¿å…æœªå®šä¹‰é”™è¯¯
        let endpoint = req.path;
        try {
          endpoint = `${req.method} ${req.route?.path || req.path}`;
        } catch (e) {
          // å¦‚æœè·å–route.pathå¤±è´¥ï¼Œä½¿ç”¨req.path
          endpoint = `${req.method} ${req.path}`;
        }

        // è®°å½•è¯·æ±‚å¼€å§‹
        this.recordRequestStart(endpoint);

        // ç›‘å¬å“åº”ç»“æŸ
        res.on('finish', () => {
          try {
            const responseTime = Date.now() - startTime;
            const cacheHit = res.getHeader('X-Cache-Hit') === 'true';

            // ä½¿ç”¨process.nextTickå»¶è¿Ÿè®°å½•ï¼Œé¿å…é˜»å¡å“åº”
            process.nextTick(() => {
              this.recordRequestEnd(endpoint, responseTime, cacheHit, res.statusCode);
            });
          } catch (error) {
            console.error('æ€§èƒ½ç›‘æ§è®°å½•å“åº”ç»“æŸå¤±è´¥:', error);
          }
        });
      } catch (error) {
        console.error('æ€§èƒ½ç›‘æ§ä¸­é—´ä»¶é”™è¯¯:', error);
      }

      // æ— è®ºå¦‚ä½•éƒ½ç»§ç»­å¤„ç†è¯·æ±‚
      next();
    };
  }

  /**
   * è®°å½•è¯·æ±‚å¼€å§‹
   */
  recordRequestStart(endpoint) {
    this.metrics.requests++;

    if (!this.metrics.endpoints.has(endpoint)) {
      this.metrics.endpoints.set(endpoint, {
        requests: 0,
        totalTime: 0,
        cacheHits: 0,
        cacheMisses: 0,
        errors: 0
      });
    }

    this.metrics.endpoints.get(endpoint).requests++;
  }

  /**
   * è®°å½•è¯·æ±‚ç»“æŸ
   */
  recordRequestEnd(endpoint, responseTime, cacheHit, statusCode) {
    this.metrics.totalResponseTime += responseTime;

    const endpointMetrics = this.metrics.endpoints.get(endpoint);
    if (endpointMetrics) {
      endpointMetrics.totalTime += responseTime;

      if (cacheHit) {
        this.metrics.cacheHits++;
        endpointMetrics.cacheHits++;
      } else {
        this.metrics.cacheMisses++;
        endpointMetrics.cacheMisses++;
      }

      if (statusCode >= 400) {
        endpointMetrics.errors++;
      }
    }

    // è®°å½•åˆ°æ—¥å¿—æ–‡ä»¶
    this.logPerformanceData(endpoint, responseTime, cacheHit, statusCode);
  }

  /**
   * è®°å½•æ€§èƒ½æ•°æ®åˆ°æ—¥å¿—æ–‡ä»¶
   */
  logPerformanceData(endpoint, responseTime, cacheHit, statusCode) {
    try {
      const logEntry = {
        timestamp: new Date().toISOString(),
        endpoint,
        responseTime,
        cacheHit,
        statusCode
      };

      const logLine = `${JSON.stringify(logEntry)}\n`;

      // ä½¿ç”¨å¼‚æ­¥æ–‡ä»¶æ“ä½œï¼Œé¿å…é˜»å¡è¯·æ±‚
      // ä½¿ç”¨setImmediateè¿›ä¸€æ­¥å»¶è¿Ÿæ—¥å¿—å†™å…¥ï¼Œç¡®ä¿ä¸å½±å“è¯·æ±‚å¤„ç†
      setImmediate(() => {
        fs.appendFile(this.logFile, logLine, (err) => {
          if (err) {
            console.error('å†™å…¥æ€§èƒ½æ—¥å¿—å¤±è´¥:', err);
          }
        });
      });
    } catch (error) {
      console.error('è®°å½•æ€§èƒ½æ•°æ®å¤±è´¥:', error);
    }
  }

  /**
   * è·å–æ€§èƒ½ç»Ÿè®¡ä¿¡æ¯
   */
  getStats() {
    const avgResponseTime =
      this.metrics.requests > 0 ? this.metrics.totalResponseTime / this.metrics.requests : 0;

    const cacheHitRate =
      this.metrics.cacheHits + this.metrics.cacheMisses > 0
        ? (this.metrics.cacheHits / (this.metrics.cacheHits + this.metrics.cacheMisses)) * 100
        : 0;

    const endpointStats = [];
    for (const [endpoint, metrics] of this.metrics.endpoints) {
      const avgTime = metrics.requests > 0 ? metrics.totalTime / metrics.requests : 0;
      const hitRate =
        metrics.cacheHits + metrics.cacheMisses > 0
          ? (metrics.cacheHits / (metrics.cacheHits + metrics.cacheMisses)) * 100
          : 0;

      endpointStats.push({
        endpoint,
        requests: metrics.requests,
        avgResponseTime: Math.round(avgTime),
        cacheHitRate: Math.round(hitRate * 100) / 100,
        errors: metrics.errors
      });
    }

    // æŒ‰è¯·æ±‚æ•°æ’åº
    endpointStats.sort((a, b) => b.requests - a.requests);

    return {
      totalRequests: this.metrics.requests,
      avgResponseTime: Math.round(avgResponseTime),
      cacheHitRate: Math.round(cacheHitRate * 100) / 100,
      totalCacheHits: this.metrics.cacheHits,
      totalCacheMisses: this.metrics.cacheMisses,
      endpoints: endpointStats
    };
  }

  /**
   * ç”Ÿæˆæ€§èƒ½æŠ¥å‘Š
   */
  generateReport() {
    const stats = this.getStats();
    const timestamp = new Date().toISOString();

    // ç”ŸæˆCSVæ ¼å¼çš„æŠ¥å‘Š
    const csvHeader = 'timestamp,endpoint,requests,avg_response_time,cache_hit_rate,errors\n';
    let csvContent = csvHeader;

    for (const endpoint of stats.endpoints) {
