  /**
   * ç”ŸæˆMarkdownæ ¼å¼çš„æ€§èƒ½æŠ¥å‘Š
   */
  generateMarkdownReport(stats) {
    const timestamp = new Date().toLocaleString('zh-CN');

    let markdown = '# ç³»ç»Ÿæ€§èƒ½ç›‘æ§æŠ¥å‘Š\n\n';
    markdown += `**ç”Ÿæˆæ—¶é—´:** ${timestamp}\n\n`;

    markdown += '## æ€»ä½“æ€§èƒ½æŒ‡æ ‡\n\n';
    markdown += `- **æ€»è¯·æ±‚æ•°:** ${stats.totalRequests}\n`;
    markdown += `- **å¹³å‡å“åº”æ—¶é—´:** ${stats.avgResponseTime}ms\n`;
    markdown += `- **ç¼“å­˜å‘½ä¸­ç‡:** ${stats.cacheHitRate}%\n`;
    markdown += `- **ç¼“å­˜å‘½ä¸­æ¬¡æ•°:** ${stats.totalCacheHits}\n`;
    markdown += `- **ç¼“å­˜æœªå‘½ä¸­æ¬¡æ•°:** ${stats.totalCacheMisses}\n\n`;

    markdown += '## æ¥å£æ€§èƒ½è¯¦æƒ…\n\n';
    markdown += '| æ¥å£ | è¯·æ±‚æ•° | å¹³å‡å“åº”æ—¶é—´(ms) | ç¼“å­˜å‘½ä¸­ç‡(%) | é”™è¯¯æ•° |\n';
    markdown += '|------|--------|------------------|---------------|--------|\n';

    for (const endpoint of stats.endpoints) {
      markdown += `| ${endpoint.endpoint} | ${endpoint.requests} | ${endpoint.avgResponseTime} | ${endpoint.cacheHitRate} | ${endpoint.errors} |\n`;
    }

    markdown += '\n## æ€§èƒ½ä¼˜åŒ–å»ºè®®\n\n';

    if (stats.cacheHitRate < 50) {
      markdown += `- âš ï¸ ç¼“å­˜å‘½ä¸­ç‡è¾ƒä½(${stats.cacheHitRate}%)ï¼Œå»ºè®®æ£€æŸ¥ç¼“å­˜ç­–ç•¥\n`;
    } else if (stats.cacheHitRate > 80) {
      markdown += `- âœ… ç¼“å­˜å‘½ä¸­ç‡è‰¯å¥½(${stats.cacheHitRate}%)\n`;
    }

    if (stats.avgResponseTime > 1000) {
      markdown += `- âš ï¸ å¹³å‡å“åº”æ—¶é—´è¾ƒé•¿(${stats.avgResponseTime}ms)ï¼Œå»ºè®®ä¼˜åŒ–æŸ¥è¯¢æˆ–å¢åŠ ç¼“å­˜\n`;
    } else if (stats.avgResponseTime < 200) {
      markdown += `- âœ… å“åº”æ—¶é—´è¡¨ç°è‰¯å¥½(${stats.avgResponseTime}ms)\n`;
    }

    // æ‰¾å‡ºå“åº”æ—¶é—´æœ€é•¿çš„æ¥å£
    if (stats.endpoints && stats.endpoints.length > 0) {
      const slowestEndpoint = stats.endpoints.reduce((prev, current) =>
        prev.avgResponseTime > current.avgResponseTime ? prev : current
      );

      if (slowestEndpoint && slowestEndpoint.avgResponseTime > 500) {
        markdown += `- âš ï¸ æœ€æ…¢æ¥å£: ${slowestEndpoint.endpoint} (${slowestEndpoint.avgResponseTime}ms)ï¼Œå»ºè®®ä¼˜åŒ–\n`;
      }
    }

    return markdown;
  }

  /**
   * å¼€å§‹å®šæœŸæŠ¥å‘Š
   */
  startPeriodicReporting() {
    // å»¶è¿Ÿå¯åŠ¨æŠ¥å‘Šç”Ÿæˆï¼Œé¿å…åœ¨åº”ç”¨å¯åŠ¨æ—¶å°±å¼€å§‹ç”ŸæˆæŠ¥å‘Š
    setTimeout(() => {
